css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ---------- BROWSER Theme ---------- */

toolbox, 
toolbar, 
toolbarset, 
toolbox#navigator-toolbox toolbarbutton, 
splitter, 
.searchbar-engine-button {
  -moz-appearance: none !important;
  background: none #fff !important;
}

/* ---------- TOOLBOX under TABS ---------- */

.tab-drop-indicator-bar {
  position:fixed!important;
  top:0px !important;
  left:0px !important;
  width:100%!important;
  z-index: 1000 !important;
}

toolbox#navigator-toolbox {
  padding-top: 32px;
  border-bottom: solid 1px #ccc !important;
}

.tabbrowser-tabs {
  height: 30px !important;
  background: url("data:image/x-icon;base64,") !important;
}

.tabbrowser-strip {
  position: fixed !important;
  top: 0px !important;
}

.tabbrowser-tabs, .tabbrowser-strip {
  width: 100% !important;
}

/* --- dropmarkers --- */

toolbox#navigator-toolbox toolbarbutton dropmarker, 
.searchbar-dropmarker-image { 
  display: none !important;
}

/* ---------- TABS ---------- */

.tabbrowser-tab {
  color: #000 !important;
  -moz-appearance: none !important;
  margin-bottom: 0px !important;
  -moz-border-radius-topleft: 5px !important;
  -moz-border-radius-topright: 5px !important;
  -moz-border-radius-bottomright: 0px !important;
  -moz-border-radius-bottomleft: 0px !important;
  background: bottom url("data:image/jpeg;base64,")!important;
}

.tabbrowser-tab[selected="true"] { 
  background: #fff !important;
  color: #000 !important;
}

tab {
  margin-right: 5px !important;
  opacity: 0.6 !important;
  padding-left: 0px !important;
  margin-top:3px !important;
}

tab:hover {
  opacity: 0.75 !important;
}

tab[selected="true"] {
  opacity: 1 !important;
}

tab[busy] .tab-icon-image {
  list-style-image: url("data:image/png;base64,") !important;
}

.scrollbutton-up, .scrollbutton-down, .tabs-alltabs-button {
  margin-top: -3px !important;
  margin-bottom: 0px !important;
  width: 24px !important;
  border: solid 1px #ccc !important;
  border-top: none !important;
  background: bottom url("data:image/jpeg;base64,")!important;
  opacity: 0.7 !important;
}

.scrollbutton-up {
  border-left: none !important;
  -moz-border-radius-topright: 5px !important;
  margin-bottom: 1px !important;
}

.scrollbutton-down {
  border-right: none !important;
  -moz-border-radius-topleft: 5px !important;
  margin-bottom: 1px !important;
}


.tabs-bottom { display: none !important; }

.close-button {
  margin-bottom:1px!important;
}

#personal-bookmarks .bookmark-item {
outline-width: 10px!important;
}

#personal-bookmarks .bookmark-item menuitem:hover
{
border:none !important;
}
#personal-bookmarks .bookmark-item:hover {
/*-moz-appearance: none !important;*/
border: 1px lightgrey solid !important;
}

.bookmark-item .toolbarbutton-text {
 font-size:12px !important;
}

.bookmark-item[container="true"]:not([livemark="true"]):not([query="true"]){
list-style-image: url("data:image/png;base64,%3D%3D") !important;
-moz-image-region: rect(0px 16px 16px 0px) !important;
}

.bookmark-item[container="true"]:not([livemark="true"])[query="true"]{
list-style-image: url("data:image/png;base64,%3D") !important;
-moz-image-region: rect(0px 16px 16px 0px) !important;
}


.tab-close-button {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-appearance: none !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}
.tab-close-button:hover, .close-button:hover {
  -moz-appearance: none !important;
  -moz-image-region: rect(16px, 16px, 32px, 0) !important;
}
.tab-close-button:active, .close-button:active {
  -moz-appearance: none !important;
  -moz-image-region: rect(32px, 16px, 48px, 0) !important;
}

.tabs-newtab-button {
  -moz-appearance:none!important;
}

.close-button {
  background:#fff!important;
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
  -moz-appearance: none!important;
  color: #000 !important;
  padding-top:8px !important;
  margin-bottom: 0px !important;
}

.alltabs-button {
  -moz-appearance: none!important;
  background:#fff!important;
}

.close-button:hover {
/*-moz-appearance:none !important;*/
}

/*
.tabs-alltabs-button { 
  border-right: none !important; 
  margin-bottom: 1px !important;
}

.tabs-alltabs-button .toolbarbutton-menu-dropmarker .dropmarker-icon {
  margin-left: 1px !important;
  list-style-image: url("data:image/png;base64,") !important;
}
*/

.tabs-alltabs-popup { margin-top: 0px !important; }


html > body > img:only-child {
 position: absolute;
 top: 0;
 left: 0;
 right: 0;
 bottom: 0;
 margin: auto;
 padding: 3px;
}

/* ---------- POPUPS MENUS ---------- */


#toolbar-context-menu, menupopup, popup {
  background-color: #fff !important;
  -moz-appearance: none !important;
  border: 1px solid !important;
  -moz-border-top-colors: #ccc !important;
  -moz-border-right-colors: #ccc !important;
  -moz-border-bottom-colors: #ccc !important;
  -moz-border-left-colors: #ccc !important;
  color: #333 !important;
}



menu, menuitem , menu[id='tinymenu']:hover
{
  border: none !important;
  height:25px !important;
/*  -moz-appearance: none !important;*/
  color: #333 !important;
  background-color: #fff !important;
}

menuitem:hover, menu:hover , menu[open="true"] {
  color: #222 !important;
  background-color: #BBC3C4 !important;
}


menuitem[disabled="true"]
{
  -moz-appearance: none !important;
  color: #888!important;
}


menuitem[disabled="true"]:hover
{
  -moz-appearance: none !important;
  color: #888!important;
  background-color: #fff !important;
}



menuseparator
{
  border: none !important;
  border-bottom: 1px dotted lightgrey !important;
  padding: 0px !important;
  -moz-appearance: none !important;
  margin: 0px 0px 0px 0px !important;
  background: #fff !important;

}

statusbar, findbar {
-moz-appearance: none !important;
border-top: 1px solid lightgrey !important;
}

findbar
{ 
background: #fff !important;
-moz-appearance : none !important;
border: none !important;
color: #000 !important;
}

statusbarpanel
{ 
height: 25px !important;
background: #fff !important;
-moz-appearance : none !important;
border: none !important;
color: #000 !important;
}

#identity-box {
/*  -moz-appearance: none !important;
  background: #fff !important;
  color:#f00!important;
*/
  -moz-border-right-colors: lightgrey lightgrey -moz-Field !important; 
}

#identity-box > hbox{
  -moz-appearance: none !important;
  border-right: none !important;
  -moz-border-radius-topleft: 0 !important;
  -moz-border-radius-bottomleft: 0 !important;
  -moz-background-clip: padding !important;
}

#searchbar .searchbar-engine-button {
  -moz-appearance: none !important;
  -moz-border-right-colors: lightgrey lightgrey -moz-Field !important; 
}

#urlbar * {
  -moz-appearance: none !important;
}
#urlbar , #searchbar .searchbar-textbox 
{	
  -moz-appearance: none !important;
  border: 1px -moz-Field solid !important;
  -moz-border-top-colors: lightgrey lightgrey -moz-Field !important;
  -moz-border-right-colors: lightgrey lightgrey -moz-Field !important; 
  -moz-border-left-colors: lightgrey lightgrey -moz-Field !important;
  -moz-border-bottom-colors: lightgrey lightgrey -moz-Field !important;
  -moz-border-radius: 0pt !important;
  padding: 0px 0px 0px 0px !important;
}
