css:
@-moz-document domain('airdcpp-web.github.io') {
:root {
--b1: #080808;
--b2: #101010;
--b3: #181818;
--b4: #202020;
--b5: #242424;
--b6: #282828;
--b7: #303030;
--b8: #383838;
--b9: #404040;
--t5: #686868;
--t4: #989898;
--t3: #AFAFAF;
--t2: #C8C8C8;
--t1: #FFFFFF;
--c1: #5C8EF0;
--c2: #334059;
--c3: #2B3446;
--w1: #EC3D3D;
--w2: #C9980B;
}

body * { outline: none !important; }
body { background: var(--b1); color: var(--t4); }
#background-wrapper { background: none !important; }
h1, h2, h3, h4, h5, h6, .ui.header { color: var(--t2); }
.ui.header .sub.header { color: var(--t3); }
.highlighter-rouge .highlight,
code { background: var(--b5); border-color: var(--b5); color: var(--t2); }

/* accordion */
.ui.styled.accordion { background: var(--b4); box-shadow: none; }
.ui.styled.accordion .log-section { border-bottom: 1px solid var(--b8); }
.ui.styled.accordion .log-section:last-child { border: none; }
.settings-layout .system.logging .log-section.active { background: var(--b2); }
.ui.styled.accordion .active.title { color: var(--t2); }
.ui.styled.accordion .title:hover,
.ui.checkbox input:focus ~ label { color: var(--t1); }

/* badge */
.ui.menu .item > .label,
.ui.ui.ui.grey.label { background: var(--b7); color: var(--t2); }

/* blog */
.ui.items > .item > .content > a.header { color: var(--c1); }
.ui.items > .item > .content > a.header:hover { color: var(--t1); }
.ui.items > .item .meta { color: var(--t4); }

/* button */
.ui.blue.button,
.ui.basic.green.button,
.ui.basic.red.button,
.ui.inverted.red.basic.button, .ui.inverted.red.basic.buttons .button, .ui.inverted.red.buttons,
.ui.inverted.green.basic.button, .ui.inverted.green.basic.buttons .button, .ui.inverted.green.buttons,
.ui.button { background: var(--b8); border-color: var(--b8); color: var(--t3); box-shadow: none; }
.ui.active.button { background: var(--b9); border-color: var(--b9); color: var(--t2); }
.ui.inverted.green.basic.button:focus,
.ui.inverted.green.basic.button:hover,
.ui.inverted.red.basic.button:focus,
.ui.inverted.red.basic.button:hover,
.ui.blue.button:focus,
.ui.blue.button:hover,
.ui.basic.green.button:focus,
.ui.basic.green.button:hover,
.ui.basic.red.button:focus,
.ui.basic.red.button:hover,
.ui.button:focus,
.ui.button:hover,
.ui.active.button:focus,
.ui.active.button:hover { background: var(--c2); border-color: var(--c2); color: var(--t1); box-shadow: none; }
.ui.inverted.green.basic.button:active,
.ui.inverted.red.basic.button:active,
.ui.blue.button:active,
.ui.basic.green.button:active,
.ui.basic.red.button:active,
.ui.button:active,
.ui.active.button:active { background: var(--c3); border-color: var(--c3); color: var(--t1); }
.ui.button.disabled { background: var(--b7); border-color: var(--b7); color: var(--t4); }
.ui.cancel.button:hover i.remove.icon { color: var(--t1); }
.ui.two.buttons > .button { width: 48% !important; margin-right: 2em; }

/* card */
.ui.cards > .card { background: var(--b3); box-shadow: none; }
.ui.cards > .card > .content > .header { color: var(--t2); }
.ui.list > .item .header { color: var(--t3); }
.ui.list > .item .description { color: var(--t4); }

/* chat */
.chat.session .chat.item.bot .author .caption,
.chat.session .status.item .text { color: var(--b8) !important; }

/* directory-table */
.ui.table { background: var(--b4); border-color: var(--b8); color: var(--t3); }
.ui.table > thead > tr > th { background: var(--b6); color: var(--t2); }

/* docs */
#sidebar h3 { margin-top: 0.5em; }

/* download */
.download-dialog .disk-info { color: var(--t5); }

/* file browser */
.browserbar .path-token.current { color: var(--t2) !important; }
.ui.breadcrumb .divider { color: var(--t5); }
.virtual-table .fixedDataTableLayout_main { background: var(--b3); border-color: var(--b8); }
.public_fixedDataTableRow_main { border-color: var(--b8); }
.public_fixedDataTable_header .public_fixedDataTableCell_main { background: var(--b6); border-color: var(--b8); }
.virtual-table .public_fixedDataTable_header a { color: var(--t2); }
.public_fixedDataTableCell_main { background: var(--b4); border-color: var(--b8); }
.public_fixedDataTableCell_main a { color: var(--t3); }
.table-footer { background: var(--b1); }
#sidebar-container .table-footer { background: var(--b2); }
.public_fixedDataTableRow_highlighted .public_fixedDataTableCell_main { background: var(--b3); }
.public_fixedDataTableCell_columnResizerKnob { background: var(--c2); }

/* form */
.ui.form input[type="password"]::placeholder,
.ui.form input[type="text"]::placeholder { color: var(--t5); }
.ui.input > input,
.ui.form select,
.ui.form textarea,
.ui.form input:not([type]),
.ui.form input[type="password"],
.ui.form input[type="text"] { background: var(--b6); border-color: var(--b6); color: var(--t2); }
.ui.input > input:active,
.ui.input > input:focus,
.ui.input > input:hover,
.ui.form select:active,
.ui.form select:focus,
.ui.form select:hover,
.ui.form textarea:active,
.ui.form textarea:focus,
.ui.form textarea:hover,
.ui.form input:not([type]):active,
.ui.form input:not([type]):focus,
.ui.form input:not([type]):hover,
.ui.form input[type="password"]:active,
.ui.form input[type="password"]:focus,
.ui.form input[type="password"]:hover,
.ui.form input[type="text"]:active,
.ui.form input[type="text"]:focus,
.ui.form input[type="text"]:hover { background: var(--b7); border-color: var(--b7); color: var(--t1); }
.ui.checkbox label:hover,
.ui.checkbox label { color: var(--t4); }
.ui.label { background: var(--b9); color: var(--t3); }
.ui.form .field > label { color: var(--t2); }
.ui.input.fluid .react-autosuggest__container input:focus,
.ui.action.input:not([class*="left action"]) > input:focus { border-color: var(--b7) !important; }
/* test: messages form */
#sidebar-container .ui.input.fluid react-autosuggest__container input:focus { border-color: var(--c2) !important; border-right-color: var(--c2) !important; }

/* icon */
i.blue.icon.icon.icon.icon,
i.grey.icon.icon.icon.icon,
.ui.icon.input > i.icon::after,
.ui.icon.input > i.icon::before { color: var(--t3); }

/* link */
a { color: var(--c1); }
a:hover { color: var(--t1); }

/* menu */
.ui.menu,
#side-menu,
.ui.inverted.menu { background: var(--b3); }
.ui.inverted.menu .item > a:not(.ui),
.ui.inverted.icon.menu .item { color: var(--t3); }
.ui.inverted.icon.menu .item:hover { color: var(--t1); }
.ui.inverted.icon.menu .item i.blue.icon.icon.icon.icon { color: var(--t3); }
.ui.inverted.icon.menu .item:hover i.blue.icon::before { color: var(--t1); }
.ui.inverted.menu .item,
.ui.inverted.menu .active.item { outline: none; }
.ui.inverted.menu .active.item { color: var(--t2) !important; }
#side-menu .statistics-icons .content .header { color: var(--t5); }
#side-menu .statistics-icons i.blue.icon.icon.icon.icon { color: var(--t5); }

/* dropdown menu */
.ui.inverted.menu .active.item,
.ui.dropdown .menu,
.ui.pointing.dropdown > .menu:not(.hidden)::after,
.ui.menu .dropdown.item .menu { background: var(--b4); }
.ui.dropdown .menu > .item { color: var(--t3); }
.ui.dropdown .menu > .header,
.ui.dropdown .menu .active.item { color: var(--t2); }
.ui.menu .ui.dropdown .menu > .active.item { color: var(--t2) !important; }
.ui.dropdown .menu > .item:hover { color: var(--t1); }
.ui.menu .ui.dropdown .menu > .item { color: var(--t3) !important; }
.ui.menu .ui.dropdown .menu > .item:hover { color: var(--t1) !important; }

/* message */
.ui.message { background: var(--b3); color: var(--t4); }

/* modal */
.ui.modal { background: var(--b3); }
.ui.modal > .header { background: var(--b4); }
.ui.modal > .content { background: var(--b3); }
.ui.modal > .actions { background: var(--b2); }
.ui.accordion .title:not(.ui) { color: var(--t4); }
.ui.basic.modal,
.ui.basic.modal > .header { color: var(--t4); }

/* notification */
.notification-error { background: var(--b6) !important; border-color: var(--b6) !important; box-shadow: none !important; color: var(--t3) !important; }
.notification-info { background: var(--b6) !important; border-color: var(--b6) !important; box-shadow: none !important; color: var(--t3) !important; }
.notification-dismiss { background: var(--w1) !important; }
.notification-info h4 { color: var(--w2) !important; }

/* popup menu */
.ui.popup { background: var(--b4); border-color: var(--b4); }
.ui.text.menu .item { color: var(--t3); }
.ui.menu a.item:hover { color: var(--t1); }

/* progress bar */
.ui.progress { background: var(--b2); }
.ui.progress > .label { color: var(--t4); }

/* segment */
.ui.segment { background: var(--b3); }
.ui.stacked.segment::after,
.ui.stacked.segment::before,
.ui.stacked.segments::after,
.ui.stacked.segments::before { display: none; }

/* settings */
.ui.vertical.menu .menu .item,
.options .ui.tiny.header,
.ui.menu .item { color: var(--t3); }
.ui.blue.header,
.ui.secondary.menu .active.item,
.ui.secondary.pointing.menu .active.item { color: var(--t2); }
.ui.secondary.pointing.menu .active.item:hover,
.ui.secondary.menu .active.item:hover,
.ui.secondary.menu a.item:hover,
.ui.secondary.pointing.menu a.item:hover { color: var(--t1); }

/* sidebar */
#sidebar { background: var(--b3);}
.session-container.vertical,
#sidebar-container { background: var(--b2);}
.simple-layout { background: none; }

/* striped table */
.ui.styled.accordion .title,
.ui.table > tbody > tr > td { border-color: var(--b8); }

/* tabular menu */
.ui.tabular.menu .item { color: var(--t3); }
.ui.tabular.menu .item:hover { color: var(--t1); }
.ui.vertical.tabular.menu .active.item { background: var(--b3); border-color: var(--b8); color: var(--t2); }
.ui.vertical.tabular.menu { border-color: var(--b8); }
}
