css:
@-moz-document domain("reddit.com")
{
     

/* Image Credits/Copyrights: Ninja - WallpapersMade */    

  body
  {
    background: #272b30 url(https://qph.fs.quoracdn.net/main-qimg-9f30d254236939978c11b6e662d94d27.webp) center top fixed !important;
    background-size: cover !important;
    color: #45494d;
  }

  .front-page > div.content
  {
    background: transparent !important;
  }

  a.title
  {
    font-size: 14px !important;
    color: #fff !important;
  }

  .tagline, .search-result-meta
  {
    color: #ddd !important;
    font-size: x-small;
  }

  .tagline a, .search-result-meta a
  {
    color: #AAD1F8 !important;
  }

  .entry .buttons li a
  {
    color: #bbb !important;
  }

  .side
  {
    float: right;
    background-color: rgba(0,0,0,0.5) !important;
    ;
  }

  .link.promotedlink.promoted, .link.promotedlink.external
  {
    background-color: #000 !important;
  }

  #header-bottom-left
  {
    font-size: larger;
    background: transparent !important;
  }

  #header-img
  {
   /* background: url(https://s18.postimg.cc/cmyt43xo9/reddit7.png) -4px 20px fixed !important; 
    background-repeat: no-repeat !important;
    background-size: 135px !important;
    height: 30px;
    width: 125px !important; */;
  }

  #header
  {
    border-bottom: 1px solid #555 !important;
  }

  .md-container > div.md > p
  {
    color: #eee !important;
  }

  blockquote > p
  {
    color: #ccc !important;
  }

  .promoted.odd, .promotedlink
  {
    display: none !important;
  }

  html.js.cssanimations.csstransforms > body.listing-page.hot-page.front-page > div.footer-parent > div.footer.rounded
  {
    background: rgba(255,255,255,0.7) !important;
  }

  .listing-page > div.content
  {
    background: rgba(0,0,0,0.4) !important;
  }

  .wiki-page > div.content
  {
    background: rgba(0,0,0,0.7) !important;
  }

  #header-bottom-left > ul.tabmenu > li > a
  {
    background: #ccc !important;
  }

  .content
  {
    overflow: hidden;
    width: 99% !important;
  }

  body > div > div > div#ad_unit
  {
    display: none !important;
  }

  div.side
  {
    display: none !important;
  }

  #siteTable.sitetable.linklisting > div
  {
    background: rgba(0,0,0,0.4) !important;
    border: 0px!important;
  }

  .listing-page.hot-page > div.content
  {
    background: none !important;
  }

  #header
  {
    border-bottom: 1px solid #5f99cf;
    position: relative;
    background: rgba(255,255,255,0.7) !important;
  }

  .listing-page.hot-page > div.footer-parent
  {
    background: rgba(255,255,255,0.7) !important;
  }

  .bottommenu
  {
    color: #111 !important;
    margin-top: 5px !important;
  }

  .bottommenu a
  {
    color: #111 !important;
  }

  .trending-subreddits-content > ul > li > a
  {
    color: #ddd !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .trending-subreddits > div.trending-subreddits-content > strong
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .menuarea > div.spacer > span.dropdown-title.lightdrop
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .menuarea > div.spacer > div.dropdown.lightdrop > span.selected
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .listing-page > div.content > div.locationbar
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  #siteTable a
  {
    color: #f7d800 !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  #siteTable.sitetable.linklisting > div
  {
    background: none !important;
  }

  .listing-page.hot-page > div.footer-parent
  {
    background: rgba(255,255,255,0.7) !important;
  }

  .footer-parent
  {
    background: rgba(255,255,255,0.8) !important;
  }

  #siteTable.sitetable.linklisting > div > div.entry.unvoted > ul.flat-list.buttons
  {
    background: rgba(0,0,0,0.5) !important;
    padding: 2px !important;
    max-width: 200px !important;
  }

  #siteTable.sitetable.linklisting > div > div.entry.unvoted > ul.flat-list.buttons > li.first
  {
    background: none !important;
  }

  .listing-page.hot-page > div#header > div#header-bottom-left > ul.tabmenu > li > a
  {
    color: #fff !important;
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
  }

  .expando-button.video.collapsed
  {
    margin-top: 11px !important;
  }

  #header > div#header-bottom-left > ul.tabmenu > li > a
  {
    background: rgba(0,0,0,0.5) !important;
    color: #fff !important;
  }

  #header
  {
    width: 99% !important;
  }

  #sr-header-area
  {
    width: 99% !important;
  }

  .trending-subreddits-content
  {
    width: 90% !important;
  }

  p.debuginfo
  {
    width: 95% !important;
  }

  .entry.unvoted
  {
    width: 99% !important;
  }

  p.tagline
  {
    widrh: 97% !important;
  }

  #siteTable.sitetable.linklisting > div
  {
    width: 99% !important;
  }
}
