css:
@-moz-document domain("twitter.com") {
/*------Avatar Images------*/
.ProfileAvatar, 
.ProfileAvatar-image, 
.ProfileAvatar-placeholderImage {
    border-radius: 12px !important
}
.Avatar,
.avatar,
.avatar--circular,
.ProfileCard-avatarImage, 
.ProfileCard-avatarLink,
.ProfileCardMini-avatarImage,
.nav .session .dropdown-toggle, 
.DashboardProfileCard-avatarImage,
.Gallery.with-tweet.without-tweet .Gallery-media {
	border-radius: 5px !important;
}
.ProfileAvatar {
        box-shadow: 0 1px 1px rgba(136,153,166,0.15);
}
/*------Profile (Home Page)------*/

.ProfileCardStats-statLabel {
    font-weight: normal;
    font-size: 11px;
    text-transform: uppercase
}
.ProfileCardStats-statValue {
    font-weight: 500;
    font-size: 20px
}
.ProfileCardStats-statLabel {
    font-weight: 500;
    font-size: 11px;
    text-transform: uppercase;
}

.ProfileCardStats {
    padding-bottom: 10px;
    padding-left: 10px;
    padding-right: 10px;
}
.DashboardProfileCard .ProfileCardStats {
    padding-top: 10px;
}
/* Profile Page */
.ProfileNav-item.is-active .ProfileNav-stat, .ProfileNav-stat:hover, .ProfileNav-stat:focus {
    border-bottom-width: 4px;
}
.ProfileNav-label {
    font-weight: normal;
    text-transform: uppercase;
    font-size: 11px
}
.ProfileHeading-content {
    border-radius: 5px 5px 0 0;
}
.ProfileHeading-toggleItem {
    font-weight: normal
}
/*------Body & Other Modules------*/

body {
    background-color: #f5f8fa;
}

.Trends {
    border: 1px solid #ecf0f3;
    border-radius: 5px;
}
h3 {
    color: #66757f;
    font-size: 20px;
    line-height: 22px;
    font-weight: normal!important;
}
.Trends .context-trend-item .trend-name {
    font-weight: normal;
}
.Trends .trend-location {
    font-size: 18px;
    font-weight: normal;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border: 1px solid #cfe8f3;
    border-radius: 5px 5px 0 0;
}
.wtf-module .UserSmallListItem {
    border-bottom: 0;
    padding-bottom: 0;
}
.module {
    border-radius: 5px;
}

.ProfileSidebar .flex-module-header h3 {
    color: #66757f;
}
/* Tweet Status View */
.tweet .stats a {
    padding-top: 6px;
    width: auto;
}
.stats .stat-count strong {
    font-size: 18px;
    font-weight: 500;
    line-height: 20px;
    display: block;
    color: #1b95e0;
}
.stats a strong {
    color: #1b95e0;
    display: block;
    font-size: 18px;
    font-weight: 500;
    line-height: 20px;
}
.stats .stat-count a {
    font-size: 10px;
    font-weight: normal;
    padding-bottom: 4px;
    text-transform: uppercase;
    float: left;
    margin-right: 10px;
    overflow: hidden;
    min-width: 72px;
}

.ReplyingToContextBelowAuthor {
    font-size: 12px;
}
b {
    font-weight: normal;
}
.ThreadedConversation, .ThreadedConversation-moreReplies:after {
    border-bottom: 1px solid #e6ecf0;
}
.ThreadedConversation {
    margin: 10px 0;
}
.permalink.has-replies .inline-reply-tweetbox {
    border-bottom: 1px solid #e6ecf0;
    border-radius: 0;
}
/*------Buttons------*/
.EdgeButton, .EdgeButton:visited {
    border-radius: 4px;
    background-image: linear-gradient(rgba(0,0,0,0),rgba(0,0,0,0.05));
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.15);
    border: 1px solid #0000004a;
}
.EdgeButton--secondary, .EdgeButton--secondary:hover, .EdgeButton--secondary:focus, .EdgeButton--secondary:active {
    color: #14171a;
}
.EdgeButton--small {
    font-size: 13px;
}
}
