css:
@-moz-document domain("roblox.com") {
:root {
    --zvar: 1001
}

.chat-main {
    box-shadow: none;
}
.dark-theme{
    background-color: #0a1932;
    border-color: transparent;
    animation: fadein 1.5s;
}

.dark-theme .content {
    background-color: #1b335b !important;
}

.dark-theme .rbx-header{
    background-color: #1b335b !important;
}

.dark-theme .container-footer{
    background-color: #0a1932 !important;
}

.dark-theme .content{
    background-color: #0a1932 !important;
}

.dark-theme .section-content{
    background-color: #1b335b !important;
}

.dark-theme .stack .stack-list .stack-row {
    background-color: #1b335b !important;
}

.dark-theme .stack .stack-list .stack-row {
    background-color: #1b335b !important;
}

.dark-theme .rbx-tabs-horizontal .nav-tabs .rbx-tab-heading {
    background-color: #1b335b !important;
}

.dark-theme .chat-main .chat-header {
    background-color: #1b335b !important;
    
}

.dark-theme .chat-main .chat-body {
    background-color: #0a1932 !important;
    border-width: medium !important;
    border-color: #1b335b !important;
}

.dark-theme .dialog-container .dialog-input-container .dialog-input {
    background-color: #0a1932 !important;
}

.dark-theme .input-field {
    background-color: #0a1932 !important;
    border-color: #0000
}

.dark-theme .chat-windows-header .chat-header-label span {
    float: right;
}
.dark-theme .notification, .dark-theme .notification-blue, .dark-theme .notification-red {
    background-color: #1b335b;
    text-align: right;
    color: #fff;
}

.dark-theme .dialog-container .dialog-header{
    background-color: #1b335b !important;
}
.dark-theme .dialog-container .dialog-body {
    background-color: #0a1932 !important;
}
.dark-theme .dialogs .dialog-container .dialog-message-container .indicated-message-bubble {
    background-color: #000;
    color: #fff;
}
.dark-theme .dialog-container {
    background-color: #1b335b;
    color: #000;
}
.rbx-left-col li{
	overflow: visible;
}

.dark-theme .rbx-divider {
    background-color: transparent;
}
.btr-profile-favorites .game-card-container {
    border: none;
}
.btr-profile .btr-pager-holder {
    margin-top: 1.5em;
}
.gotham-font div {
    font-size: 16px;
    font-weight: 400;
    line-height: 1;
}

#navigation{
    background-color: transparent;
    box-shadow: none;
    border: none;
}

}

@-moz-document domain("roblox.com") {
/* #232527 --background color */
@keyframes fadein{
    from {opacity: 0;}
    to {opacity: 1;}
}

.chat-container.collapsed .chat-main, .chat-main, .dialog-container, .dialog-container.collapsed{
	transition: height 0.5s;
}

.game-card-container{
    transition: height 0.5s, top 0.5s;
	height: 180px !important;
    overflow: hidden;
    position: relative;
    top: 25px;
}

.game-card-container:hover{
    transition-delay: .75s !important;
    transition: height 0.5s, size 0.25s, top 0.5s;
    height: 120% !important;
    top: 0px;
}

.rbx-header .rbx-navbar li .nav-menu-title:hover{
	border-color: white !important;
    transition: border-color .25s;
}


#mCSB_1_container ul li {
    transition: width 0.5s, opacity 0.5s, background-color 0.5s, border 0.5s, border-radius 0.5s;
    width: 30px;
    opacity: .25;
    overflow: hidden;
    margin-bottom: 15px !important;
    border: none;
    
}

#mCSB_1_container ul li:hover {
    animation-name: transparency;
    animation-duration: 0.25s !important;
    animation-timing-function: ease-in-out;
    opacity: 1;
  }
#mCSB_1_container ul li:hover {
    transition-delay: 0.5s !important;
    animation-name: expand;
    animation-duration: 0.75s;
    animation-timing-function: ease-in-out;
    width: 130px !important;
  }
}
