css:
@-moz-document url-prefix("https://www.easports.com/es/fifa/ultimate-team/web-app")
{
.ut-navigation-button-control {
    z-index: 999 !important;
    position: absolute !important;
    right: 20px !important;
    background-color: greenyellow !important;
    bottom: -658px !important;
}

.buyButton {
    z-index: 20 !important;
    background-color: #3fe3ef !important;
    bottom: 1px !important;
    position: absolute !important;
}


.ui-dialog-type-message {
    position: absolute !important;
    right: 0px !important;
    bottom: -17px !important;
    z-index: 999 !important;
}

.dialog-msg {
    text-align: center;
    padding: 0.5rem;
}

.Dialog header {    
    font-weight: 600;
    height: 2.5em;
    line-height: 2.5em;
}

.Dialog .ut-button-group button:nth-child(1) {
    color: #3fe3ef;
    bottom: -42px !important;
}

.Dialog .ut-button-group button:nth-child(2) {
    color: #3fe3ef;
    bottom: 62px !important;
}
}
