css:
@-moz-document domain("clever.com") {
/*portal*/
body, .ResourceLink--link, .BrowardLogin--page {background-color: #2C2F33; color: #FFFFFF}
h1, h2, h3, h4, h5, h6 {color: #7289DA}
p {color: #FFFFFF}
a.ResourceLink--link {color: #99AAB5}
.Sidebar--fixedContentContainer {background-color: #23272A}
.Header--HeaderLink--active::after {border-bottom: 0.5rem solid #2C2F33}

/*windows*/
.Modal, .Modal--window .Modal--header, .Modal--window--content,a.Button--secondary, button.Button--secondary {background-color: #2C2F33; color: #FFFFFF}
.Modal--header h1, h2, h3, h4, h5, h6 {color: #7289DA} 
.Modal--window {border: #99AAB5}
.TextInput {border: 0.0625rem solid #0000004d; background-color: #23272A; color: #99AAB5 }
.TextInput label {color: #99AAB5 }

/*announcements*/
.MountedCard {background-color: #23272A}
.MountedCard--up--rightAlign::before {border-bottom-color: #23272A}

/*dropdown*/
.Header--DropDownMenu--DropDownMenuLink > button, .Header--DropDownMenu--DropDownMenuLink a, .Header--DropDownMenu--DropDownMenuLink > button:hover, .Header--DropDownMenu--DropDownMenuLink a:hover {background-color: #23272A; color: #436cf2;}

/*search*/
.Search--Input--isActive.Search--Input, .Search--Input--isActive .Search--Input--input.Search--Input--withPlaceholder {background-color: #2C2F33; color: #FFFFFF}
.Search--Input--input {color: #FFFFFF}
.Search--noResults {background-color: #2C2F33}
.Search--ResultGroup--heading {background-color: #23272A; color: #436cf2}
.Search--Result--contentWrapper, .Search--ResultGroup, a.Search--Result {background-color: #2C2F33; color: #FFFFFF; border: none }

/*login page*/
.BrowardLogin--header{background-color:#436cf2; border: 0px}
.AuthMethod--container{box-shadow: none}

/*loading page*/
.FullPageLoader {
    background-color: #2C2F33;

}

.FullPageLoader--LoadingText{
filter: brightness(500%);
}

}
