css:
@-moz-document domain("allday2.com") {
div.row.all-content:last-child > div.span2.left-column:first-child > div.image-top,
div.row.all-content:last-child > div.span2.left-column:first-child > h4:nth-child(4),
div.row.all-content:last-child > div.span2.right-column:last-child > div.image-top,
div.row.all-content:last-child > div.span2.right-column:last-child > h4:nth-child(6),
#dle-content > div:nth-child(4) > div,
#dle-content > div.post__holder,
div.post_content > div:nth-child(12) > font > font,
#footer-text,
.rating-short,
#calendar-layer,
.da-list,
 div.span2.right-column:last-child > a:last-child,
div.post_content:nth-child(2) > center:first-child > a > b > h3,
div.post_content:nth-child(2) > center:nth-child(15) > a > b > h3{
	display:none!important;
}
article .post_meta,
#footer-text{
	border:none!important;
}
.post__holder{
	box-shadow:none!important;
}
body{
	background:#f5f5f5!important;
}
.filterable-portfolio .image-wrap{
	background:none!important;
}
#portfolio-grid{
	padding-right:7px!important;
}
.portfolio_item{
	padding: 20px 0!important;
	margin: 8px 5px!important;
	background: #fff!important;
    box-shadow: 0 0 0 1px rgba(0,0,0,.1),0 2px 3px rgba(0,0,0,.2)!important;
    transition: box-shadow 83ms;
	border-radius: 2px !important;
}
.post__holder{
	margin: 3px 5px!important;
	background: #fff!important;
    box-shadow: 0 0 0 1px rgba(0,0,0,.1),0 2px 3px rgba(0,0,0,.2)!important;
    transition: box-shadow 83ms;
	border-radius: 2px !important;
}
.thumbnail__portfolio,
.filterable-portfolio .image-wrap{
	height:350px!important;
}
.image-wrap img{
	width:100%!important;
	height:auto!important;
}
.caption.caption__portfolio > p{
	margin: 0 20px!important;
}
.navigation > i:first-child > a,
.navigation > i:nth-child(14) > a{
	background:#ddd!important;
	color: #222!important;
}
.navigation > i:first-child > a:hover,
.navigation > i:nth-child(14) > a:hover,
.navigation > a:hover,
.navigation > span,
.caption.caption__portfolio > p > a:hover{
	background:#ff0000!important;
	color: #fff!important;
}
.navigation > a,
.navigation > i > span,
.navigation > span.nav_ext{
	background:none!important;
	color: #222!important;
}
.caption.caption__portfolio > p > a{
	border-radius:5px!important;
}
#content .filterable-portfolio .caption h3{
	text-transform:inherit;
	font-weight:bold!important;
}
.excerpt{
	text-transform:none!important;
	line-height:18px!important;
	font-size:12px!important;
}
.content-center{
	padding:0 10px!important;
}
.caption.caption__portfolio > h3{
	margin: 0px 20px 18px!important;
}
.ui-dialog-buttonset button, .bbcodes, .btn{
	text-transform:inherit;
	font-size:14px!important;
	float:right!important;
}
.form-wrapper{
	box-shadow:none!important;
}
a{
	color:#222!important;
}
div.post_content > div > a:link{
	color:#FF2000!important;
}
}
