/* ==UserStyle==
@name           AVF Zenforo 2
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document domain("www.avforums.com") {
    /* Insert code here... */
    
    .p-body, .p-header {
        background-color: #222222 !important;
        background-image: none !important;
        font-family:  "Liberation Sans", Arial, Geneva, Tahoma, Verdana, Helvetica, Arial, sans-serif !important;
	    font-size: 0.9em !important;
    }
    
    .p-nav a, .p-nav a:visited, button.button--cta, a.button.button--cta, 
    #top > div.p-body > div > ul:nth-child(3) a, 
    #top > div.p-body > div > ul:nth-child(3) a:link,
    #top > div.p-sectionLinks > div > div > ul > li a,
    .p-breadcrumbs>li a 
       
    {
        color: #999999 !important;
        font-size: 1.02em !important;
        
    }
    
   #top > div.p-body > div > div.p-body-header > div.p-title > h1,
   #top > div.p-body > div > div.p-body-header > div.p-description > ul > li > a,
   form > div.menu-footer > span > a > span,
    div.p-body > div > div.p-body-header > div.p-title > div > a > span
    
    {
        color: #eeeeee !important;
        
    }
    
    
    #top > div.p-body > div > div.p-body-header > div.p-title {
        margin-right:0px
    }
    
    
    .p-body-inner, .p-header-inner,
    #top > div.p-navSticky.p-navSticky--primary > nav > div,
    #top > div.p-sectionLinks > div{
        max-width: 1000px !important;
    }
 
    .p-body-sidebar {
        display: none !important;
    }
    
    .bbWrapper, div > div.message-cell.message-cell--main {
        font-family:  "Liberation Sans", Arial, Geneva, Tahoma, Verdana, Helvetica, Arial, sans-serif !important;
        font-size: 1.08em !important;
        color: #555555 !important;
        background-color:#EBEDF2 !important;
    }
    
    .message-name {
        font-family:  'Segoe UI','Helvetica Neue',Helvetica,Roboto,Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans',sans-serif;
    }
    
    .label.label--avforange {
    background-color: #3398ff7a !important;
    border-color: #3a4fb3c4;    
    }
    
   div.structItem-cell.structItem-cell--main > div.structItem-title > a.labelLink > span:hover,
   a > span.label.label--avforange:hover {
    border-color: #b11111;
    }
   
    .block-container, .filterBar  {
    background-color:#EBEDF2 !important;
        }
    
    .block-filterBar {
            background-color:#EBEDF2 !important;
        background:none !important;
    }
    
    a {
        color: #555555 !important;
        font-size: 0.95em !important;
    }
    a:visited {
        color:#9d2362 !important;
    }
    
    .p-title-pageAction {
        background-color:#222222 !important;
        padding: 5px 8px !important;
    }
    
    .avatar {
        border-radius: 20% !important
    }
    
    a.username,
    div > div.message-cell.message-cell--main > div > div > div > article > div.bbWrapper > blockquote > div.bbCodeBlock-title a
    {
    font-family:  "Liberation Sans", Arial, Geneva, Tahoma, Verdana, Helvetica, Arial, sans-serif !important;
    font-size: 1.1em !important;
    font-weight: bold !important;
    text-decoration: none !important;
    color: #4864a5 !important;
    }
    
    a.fauxBlockLink-blockLink {
    font-family:  "Liberation Sans", Arial, Geneva, Tahoma, Verdana, Helvetica, Arial, sans-serif !important;
    font-size: 1.0em !important;
    font-style:italic !important;
    text-decoration: underline !important;
    color: #9d2362 !important
    }
    
    
    div.bbWrapper > blockquote {
    border-left:3px solid #9d2362 !important;
}
    
    #top > div.p-body > div > ul.notices.notices--block.js-notices {
        display:none !important
    }

    .structItem {
    display: table;
    table-layout: fixed;
    border-collapse: collapse;
    border-top: 1px solid #bbbbbb !important;
    list-style: none;
    margin: 0;
    padding: 0;
    width: 100%;
    }

    div > div.js-alertsMenuBody > div > ol > li.menu-row.menu-row--separated.menu-row--clickable.menu-row--highlighted,
    div > div.message-cell.message-cell--main > div > div > div > article > div.bbWrapper > blockquote > div.bbCodeBlock-title, 
    div > div.message-cell.message-cell--main > div > div > div > article > div.bbWrapper > blockquote > div.bbCodeBlock-content,
    .bbCodeBlock-content{
        background-color: #233b9d1c !important
    
    }
    
    div > div.message-cell.message-cell--main > div > div > div.message-userContent.lbContainer.js-lbContainer > article > div.bbWrapper > blockquote > div.bbCodeBlock-content > div.bbCodeBlock-expandContent {
        font-style:italic !important;
        background-color: #e3e7f1
    }
    
    .badge.badge--highlighted, .badgeContainer.badgeContainer--highlighted:after {
        font-size: 0.9em !important;
    }
 
    #top > div.p-body > div > div.p-body-header > div > h1 > a {
        color: #0df1f1 !important;
    }
    
}
