css:
@-moz-document url-prefix("https://qna.habr.com/") {
body, .hljs, .user-summary__name, .answer__text, .comment__text, .page__text, .question__text, .question__title, .field__input, .section-header__title, .question__title-link, .tabs__link, .panel-heading__title, .questions-filters__title , .filters-menu__link, .selectBox-label, .field__label, .tag__title, .page__header-title, .selectBox-options>li>a, .page-header__title, .card__head-title, .menu__item-link, .popup, .checkbox__label, .popup__header, input[type="text"], .btn__text, .ui-menu .ui-menu-item a, .empty-block__title, .menu_dropdown .menu__item-link {
    color: #dee1e5;
}

.logo {
    filter: none!important;
}

#TMpanel {
    display: none!important;
}

body, .layout, .page__header, .page__tabs, .tabs, .page, .content-list_comments, .field__input, .icons-bar, .form_comments, .page__filters, .filters-menu__link, code, pre, .panel-heading, .question__complexity .svg-icon_level>.svg-icon, .layout__body, .selectBox-option, .menu_dropdown, .popup, .selectBox-options>li>a, .form__buttons, .ui-menu, .input[type="text"] {
    background-color: #424855!important;
}

.hljs, .progress, .ui-state-focus, .menu__item-link:hover {
    background-color: #4c5260!important;
}

.question__title-link:visited {
    color: #666699;
}

.tmservices-panel {
    display: none!important;
}

.question-head, .icons-bar, .field__input, .icons-bar__item, .content-list__item, .tabs, .filters-menu, .avatar-uploader, .selectBox-dropdown-menu, .selectBox-options>li>a, .page__tabs_wrap, .content-list_sidebar-block.content-list_sidebar-block, .icons-bar__item:last-child, .menu__item, .icon_form, input[type="text"], .popup__button, .btn_complexity-filter, .ui-menu .ui-menu-item, .section-header {
    border-color: #525a6b!important;
}

@media only screen and (min-width: 1024px) {
.layout__body:before {
    background: #424855!important;
 }   
}

.btn_complexity[disabled] {
    border: 1px solid;
}

.btn, .filters-menu, .icon_form, .svg-icon, .page__filters_wrap, .btn_complexity[disabled], .btn_complexity-filter .svg-icon_filter-level>.svg-icon, .empty-block__button:hover, .btn[disabled] {
    background-color: transparent;
}

.icon_form, .icons-bar__item svg:not(.radio_flat.icon_radio_dot) {
    fill: #dee1e5!important;
}

.answer__text pre code.hljs {
    margin-left: -15px;
    padding: 15px 15px;
}

.hljs-keyword, .hljs-attr, .hljs-name {
    color: #c792ea!important;
}

.hljs-regexp {
    color: #82aaff;
}

.hljs-subst, .hljs-string {
    color: #c3e88d;
}

.hljs-literal, .hljs-number {
    color: #f78c6c;
}

.hljs-built_in, .hljs-title {
    color: #ffcb6b!important;
}

.hljs-tag {
    color: #89ddff;
}
}
