css:
@-moz-document domain("game.starwars-noj.fr") {

/*General*/

.Area_Header_Channel, .Area_Header_Query {
    background-image: url("https://zupimages.net/up/19/39/gio1.png");
}

.GamePane_Areas {
	background-color: #323232;
}


/*Chat*/

.Chat, .UserList, .CommandInput, .CommandInput_InputField, .SettingsPane, .SettingsPane_Header, .SettingsPane_Toolbar, .Message_Text, .Message_Login, .Message_Timestamp, .popover, .popover-title {
    color: silver;
    background-color: #323232;
}

.popover span:hover, .popover li:hover {
    color: silver;
}

.CommandInput_SendButton {
	color: #2B2B2B;
}

.CommandInput_Character {
    color: gray;
    background-color: #202020;
    transition: none !important;
}

.CommandInput_Character:hover {
    color: silver;
    background-color: gray;
}

.MessageNotice {
    background:radial-gradient(ellipse at bottom, gray 0%, rgba(0,0,0,0) 60%) ;
}

.MessageNotice a {
    color: silver;
}

.MessageNotice a:hover {
    color: white;
}

.Areas_List_Item_Counter {
    background-color: silver;
}

.Areas_List_Item_Counter.actions {
    background-color: #323232;
}

.UserList {
	overflow: visible;
    width: 13%;
}


/*Chat-bordures*/

.Area_Header, .Area_Content, .popover-title, .UserList_User {
	border-bottom: 1px #454545 solid;
}

.Areas {
    border-right: 1px silver solid;
}

.UserList_User:hover {
	background-color:#454545;
}

.UserList {
	border-left: 1px #454545 solid;
}

.Message_Login {
	border-right: 1px #454545 solid
}

.popover li {
    border-top: 1px #454545 solid;
}

.arrow, .arrow::after {
	visibility: hidden;
}


/*Préférences*/

hr {
    border: 0;
    height: 1px;
    background: #737373;
}

.Select-control, .Select-value, .Select-menu-outer, .form-control, .panel, .alert-info {
    color: silver;
    background-color: #454545;
}

.Select-item, .Select-item-icon, .Select-item-icon:hover {
    color: silver;
    background-color: #323232;
    border: none;
}

.panel.panel-warning {
    color: silver;
    background-color: #454545;
    border: none;
}

.panel.panel-warning .panel-heading {
    color: silver;
    background-color: #737373;
    border: none;
}

.ColorSelect_Option {
    color: #737373;
}

.btn-default {
    color: silver;
	background-color: #454545;
    border: #454545;
}

.btn-default:hover {
    color: white;
	background-color: #737373;
    border: #737373;
}

.btn-danger {
    color: silver;
    background-color: #7F0000;
}

.btn-primary {
    color: white;
    background-color: #337AB7;
}

.SettingsPane_Nav a[role='button']:hover {
    color: white;
    background-color: #737373;
}

.SettingsPane_Nav a[role='button']:visited {
    color: white;
    background-color: #737373;
}

/*Préférences-bordures*/

.alert-info, .Select-control, .form-control, .Select-menu-outer, .btn-danger {
    border: none;
}

.SettingsPane_Header {
	border-bottom: 1px #454545 solid;
}

.SettingsPane_Nav {
	border-right: 1px #454545 solid;
}

.SettingsPane_Toolbar {
	border-top: 1px #454545 solid;
}
}
