@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oncourse.iu.edu") {

#headerMax #siteNav {margin-left:0 !important; font-size:12px !important;}

#portalOuterContainer {position:relative !important;}
#headerMax #siteNavWrapper {height:0 !important;}
#headerMax #mastLogo, #headerMax #mastBanner {display:none !important;}
#headerMax #mastLogin {position:absolute !important; bottom:0; right:0; background:#600 !important; -moz-border-radius-topleft:10px !important; padding:.5em 1.5em 1em 1em !important;}
#headerMax #grandsearchboxcontainer {position:absolute !important; bottom:0; overflow:hidden;}
#headerMax #searchboxcontainer {padding-bottom:1em;}
#headerMax #searchboxcontainer, #headerMax #searchbox {width:630px !important;}
#headerMax #searchbox {margin-left:0 !important;}
#headerMax #searchbox>:first-child {display:none;}

.course #toolMenuWrap, .workspace #toolMenuWrap {float:left !important; position:static !important; color:#FFF !important; background-color:transparent !important; border-right:#000 solid 1px !important; background-image:url(data:image/png;base64,) !important;}

.course #toolMenu li a, .workspace #toolMenu li a {color:#600 !important;}
.course #toolMenu li a:hover, .workspace #toolMenu li a:hover {background:#300 !important; color:#fff !important;}
.course .selectedTool, .workspace .selectedTool {background:#000 !important; color:#fff !important; padding-top:.5em !important; padding-bottom:.5em !important;}


.specialLink .nil img, .breadCrumb h3 img {visibility:hidden !important;}
.breadCrumb h3 {background-position:5em bottom !important;}

/* open folder - 16x16 */
.specialLink .nil a[title*="Close"], .specialLink h3 .nil {background-image:url(data:image/png;base64,) !important; background-repeat:no-repeat !important;}
/* closed folder - 16x16 */
.specialLink .nil a[title*="Open"], form>.breadCrumb h3 {background-image:url(data:image/png;base64,) !important; background-repeat:no-repeat !important;}

/* Unsupported icons - attachment form */
#attachForm h4 a[href$="docx"],
#attachForm h4 a[href$="7z"],
#attachForm h4 a[href$="pptx"]
{padding-left:23px; background-color:#FFF; position:relative; left:-25px;}

/* Unsupported icons - attachments to assignments */
.attachList a[href$="docx"],
.attachList a[href$="7z"],
.attachList a[href$="pptx"]
{padding-left:32px; position:relative; left:-32px; top:2px; background:#FFF; margin-right:-32px;}

/* "Unsupported" filetype icons */
a[href$="docx"]:first-child img {visibility:hidden !important;}
.attachList a[href$="docx"],
#attachForm h4 a[href$="docx"],
a[href$="docx"]:first-child
{background-image:url(data:image/png;base64,) !important; background-repeat:no-repeat !important;}

a[href$="7z"]:first-child img {visibility:hidden !important;}
.attachList a[href$="7z"],
#attachForm h4 a[href$="7z"],
a[href$="7z"]:first-child
{background-image:url(data:image/png;base64,) !important; background-repeat:no-repeat !important;}

a[href$="pptx"]:first-child img {visibility:hidden !important;}
.attachList a[href$="pptx"],
#attachForm h4 a[href$="pptx"],
a[href$="pptx"]:first-child
{background-image:url(data:image/gif;base64,) !important; background-repeat:no-repeat !important;}

/* end unsupported icons */


.lines tr td {border-bottom:#999 dashed 1px !important; border-collapse:collapse !important; padding-top:.5em !important; padding-bottom:.5em !important;}
}
