css:
@-moz-document url-prefix("https://operatorstaff.clodo.ru/helpdesk/") {
	html {
	    height: 100%;
	}
	body {
	    font: normal 110% "Trebuchet MS", Helvetica, sans-serif;
	    margin: 0;
	    height: 100%;
	    background: #2b2929;
	    color: #c1c0c0;
	}
	a {
	    color: #56b0dd;
	}
	a:hover {
	    color: #dd5e5e;
	}
	/* Header */
	table.header {
	    background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#666666));
	    background: -moz-linear-gradient(top, #000000, #666666);
	}
	table.header td.slog {
	    padding: 15px;
	    text-shadow: 1px 1px 1px #000;
	}
	table.header h1 {
	    margin: 0;
	    padding: 0;
	    color: #ddd;
	}
	table.header p {
	    margin: 0;
	    color: #efefef;
	}
	table.header td.user {
	    color: #efefef;
	    padding: 0 15px 0 0;
	    font-size: 90%;
	}
	table.header td.user a {
	    color: orange;
	}
	table.header td.user a:hover {
	    color: red;
	}
	/* End Header */
	div.message_detail {
	    margin: 0;
	}
	div.message_detail b {
	    color: #c1c0c0;
	    text-shadow: none;
	    font-weight: bold;
	}
	div.message_detail span {
	    font-size: 12px;
	}
	div.message_detail span.msgcepochka {
	    padding: 1px 4px 0 4px;
	    -moz-border-radius: 4px;
	    -webkit-border-radius: 4px;
	    font-size: 14px;
	    background: orange;
	}
	div.message_detail span a {
	    color: #fff;
	    text-decoration: none;
	    font-weight: normal;
	}
	div.message_detail p {
	    margin: 0 0 4px 0;
	}
	div.message_detail td.leftmsg {
	    border-left: solid 1px #fff;
	    border-right: solid 1px #ccc;
	    padding: 0 15px;
	}
	div.message_detail td.rightmsg {
	    border-left: solid 1px #fff;
	    padding: 0 0 0 10px;
	    width: 1%;
	}
	div.message_detail td.senticon {
	    border-right: solid 1px #ccc;
	    padding-right: 8px;
	    width: 100px;
	}
	div.message_detail span.closemsg {
	    padding: 4px 10px;
	    -moz-border-radius: 8px;
	    -webkit-border-radius: 8px;
	    font-size: 16px;
	    text-shadow: 1px 1px 1px #fff;
	    background: -webkit-gradient(linear, left top, left bottom, from(#99ff99), to(#339933));
	    background: -moz-linear-gradient(top, #99ff99, #339933);
	}
	div.message_detail span.chatmsg {
	    padding: 4px 10px;
	    -moz-border-radius: 8px;
	    -webkit-border-radius: 8px;
	    font-size: 16px;
	    text-shadow: 1px 1px 1px #fff;
	    background: -webkit-gradient(linear, left top, left bottom, from(#ffcc66), to(#cc9900));
	    background: -moz-linear-gradient(top, #ffcc66, #339933);
	}
	div.message_detail span.chatmsg a, div.message_detail span.closemsg a {
	    color: #003300;
	}
	div.usermsg {
	    font-size: 80%;
	    padding: 15px;
	    background: -webkit-gradient(linear, left top, left bottom, from(#27313c), to(#090909));
	    background: -moz-linear-gradient(top, #27313c, #090909);
	    border-top: solid 1px #dfdfdf;
	}
	div.operatormsg {
	    font-size: 80%;
	    padding: 15px;
	    background: -webkit-gradient(linear, left top, left bottom, from(#2d2c2c), to(#3e3d3d));
	    background: -moz-linear-gradient(top, #2d2c2c, #3e3d3d);
	    border-top: solid 1px #dfdfdf;
	}
	p.onemsgdetail {
	    text-align: right;
	    margin: 0;
	    color: #888;
	    font-style: italic;
	}
	p.file {
	    font-size: 12px;
	    border-top: solid 1px #eee;
	    margin: 10px 0 0 0;
	    padding-top: 10px;
	}
	div.msglisting {
	    overflow: auto;
	}
	/*	table.header td.menu table.main_menu td.current {
		color: #fff;
		background: -webkit-gradient(linear, left top, left bottom, from(#336633), to(#669933));
        background: -moz-linear-gradient(top, #3d749a, #305f7f);
		-moz-border-radius: 4px;  
		-webkit-border-radius: 4px;
		box-shadow: 0px 1px 1px #a0c3d9;
		-moz-box-shadow: 0px 1px 1px #a0c3d9;
		-webkit-box-shadow: 0px 1px 1px #a0c3d9;
	}
	table.header td.menu table.main_menu td.current a {
		color: #fff;
		height: 50px;
		overflow: hidden;
	}
*/
	table.ticketmail {
	    background: #373535;
	    height: 100%;
	    border-top: solid 1px #fff;
	}
	table.ticketmail td.ticket_leftpanel {
	    background: #2b2929;
	    border-left: solid 1px #ccc;
	    border-top: solid 1px #ccc;
	    border-right: solid 1px #fff;
	    border-bottom: solid 1px #fff;
	    padding: 0;
	    height: 100%;
	}
	table.ticketmail td.ticket_leftpanel h4 {
	    padding-bottom: 5px;
	    margin: 0;
	    padding: 5px 10px;
	    font-size: 16px;
	    background: -webkit-gradient(linear, left top, left bottom, from(#2b2a2a), to(#151515));
	    background: -moz-linear-gradient(top, #2b2a2a, #151515);
	}
	table.ticketmail td.ticket_leftpanel h4.later {
	    background: -webkit-gradient(linear, left top, left bottom, from(#effcff), to(#caf4fe));
	    background: -moz-linear-gradient(top, #effcff, #caf4fe);
	}
	table.ticketmail td.ticket_leftpanel h4.gemor {
	    background: -webkit-gradient(linear, left top, left bottom, from(#fff4f4), to(#fecaca));
	    background: -moz-linear-gradient(top, #fff4f4, #fecaca);
	}
	table.ticketmail td.ticket_leftpanel h4.vip {
	    background: -webkit-gradient(linear, left top, left bottom, from(#eaffea), to(#a3f2a3));
	    background: -moz-linear-gradient(top, #eaffea, #a3f2a3);
	}
	div.msgotdel {
	    border: solid green;
	    border-width: 2px 0;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox {
	    margin: 0;
	    width: 200px;
	    border-top: solid 1px #bbb;
	    font-size: 14px;
	}
	table.ticketmail td.ticket_leftpanel table.vip {}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr {}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr td {
	    padding: 5px 10px;
	    border-bottom: solid 1px #eee;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr.currentbox {
	    background: #7b7b7b;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr.currentbox a {
	    text-decoration: none;
	    color: #c1c0c0;
	    font-weight: normal;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr td.msgcount {
	    padding: 5px 10px 5px 0;
	    width: 1%;
	    text-align: right;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr td.msgcount span {
	    background: #eee;
	    color: #aaa;
	    padding: 1px 8px;
	    -moz-border-radius: 5px;
	    -webkit-border-radius: 5px;
	    font-size: 12px;
	}

	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr div.msgcount_new {
	    padding: 5px 0 5px 0;
	    text-align: right;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr div.msgcount_new span {
	    background: #eee;
	    color: #aaa;
	    padding: 1px 8px;
	    -moz-border-radius: 5px;
	    -webkit-border-radius: 5px;
	    font-size: 12px;
	}

	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr td.inbox span {
	    background: green;
	    color: #fff;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr td.sent span {
	    background: orange;
	    color: #fff;
	}
	table.ticketmail td.ticket_leftpanel table.ticket_msgbox tr td.all span {
	    background: #3366cc;
	    color: #fff;
	}
	table.ticketmail td.ticket_list {
	    width: 100%;
	    height: 100%;
	    background: #121212;
	    border-left: solid 1px #ccc;
	    border-top: solid 1px #ccc;
	    border-right: solid 1px #fff;
	    border-bottom: solid 1px #fff;
	}
	table.ticketmail div.ticketscroll {
	    height: 100%;
	    z-index: 10;
	    position: relative;
	    overflow-y: scroll;
	    overflow-x: hidden;
	}
	table.ticketmail td.ticket_message {
	    width: 100%;
	    height: 90%;
	    background: #121212;
	    border-left: solid 1px #ccc;
	    border-top: solid 1px #ccc;
	    border-right: solid 1px #fff;
	    border-bottom: solid 1px #fff;
	    overflow-y: scroll;
	    overflow-x: hidden;
	}
	table.ticketmail td.ticket_leftpanel select {
	    width: 200px;
	}
	DIV.hdthead {
	    margin: 0;
	    height: 30px;
	    background: -webkit-gradient(linear, left top, left bottom, from(#2b2a2a), to(#151515));
	    background: -moz-linear-gradient(top, #2b2a2a, #151515);
	    border-top: solid 1px #000;
	    border-bottom: solid 1px #aaa;
	    overflow-y: none;
	    font-size: 20px;
	    padding: 10px 15px;
	}
	div.message_detail {
	    background: -webkit-gradient(linear, left top, left bottom, from(#2b2a2a), to(#151515));
	    background: -moz-linear-gradient(top, #2b2a2a, #151515);
	    border-bottom: solid 1px #ccc;
	    padding: 10px;
	    font-size: 14px;
	    margin: 1px 0 20px 0;
	}
	span.history {
	    background: #999933;
	    padding: 1px 4px 0 4px;
	    -moz-border-radius: 4px;
	    -webkit-border-radius: 4px;
	    color: #fff;
	    font-weight: bold;
	}
	span.vip {
	    background: #cc3333;
	    padding: 1px 4px 0 4px;
	    -moz-border-radius: 4px;
	    -webkit-border-radius: 4px;
	    color: #fff;
	    font-weight: bold;
	}
	span.abused {
	    background: #00c6ff;
	    padding: 1px 4px 0 4px;
	    -moz-border-radius: 4px;
	    -webkit-border-radius: 4px;
	    color: #000000;
	    font-weight: bold;
	}
	span.hight, span.middle, span.low, span.dolg, span.new, span.panic {
	    padding: 1px 4px 0 4px;
	    -moz-border-radius: 4px;
	    -webkit-border-radius: 4px;
	    color: #fff;
	}
	span.panic {
	    background: #ff0000;
	}
	span.hight {
	    background: #cc3333;
	}
	span.middle {
	    background: #3366cc;
	}
	span.low {
	    background: green;
	}
	span.dolg {
	    background: #ffd1d1;
	    padding: 2px 5px;
	    color: #000;
	}
	span.new {
	    background: #00cc00;
	}
	#ajax_loader {
	    text-align: center;
	}
	#ajax_loader img {
	    margin: 25px;
	}

	/* button basics */
	a.minibutton {
	    font-family: "PFBeauSansPro", "Trebuchet MS";
	    display: inline-block;
	    height: 30px;
	    padding: 0 0 0 3px;
	    font-size: 17px;
	    font-weight: normal;
	    color: #aaa2a2;
	    text-shadow: none;
	    background: #7d7d7d;
	    white-space: nowrap;
	    border: none;
	    overflow: visible;
	    overflow-y: hidden;
	    cursor: pointer;
	    text-decoration: none;
	}

	a.minibutton > span {
	    display: block;
	    height: 23px;
	    padding: 7px 15px 0px 13px;
	    line-height: 17px;
	    background: #4f4d4d;
	}

	a.minibutton:hover, a.minibutton:focus {
	    color: #dd5e5e;
	    text-decoration: none;
	    text-shadow: none;
	    background: #756e6e;
	    filter: glow(color=#ffffff, strength=1);
	}
	a.minibutton:hover > span, a.minibutton:focus > span {
	    background-position: 100% -30px;
	}

	a.minibutton.mousedown {
	    background-position: 0 -60px;
	}
	a.minibutton.mousedown > span {
	    background-position: 100% -60px;
	}

	tr.unread {
	    font-weight: bold;
	    color: white;
	}

	td.maintd {
	    background: #fff;
	    box-shadow: 0px 0px 10px #aaa;
	    -moz-box-shadow: 0px 0px 10px #aaa;
	    -webkit-box-shadow: 0px 0px 10px #aaa;
	}
	.innertube {
	    margin: 20px 30px;
	}

	div#search_man {
	    margin: 25px;
	    padding: 10px;
	    border: 1px solid;
	    background: #121212 !important;
	}

	.flexigrid {
	    font-size: 12px;
	    position: relative;
	    border: 0px solid #eee;
	    overflow: hidden;
	    color: #c1c0c0;
	    background: #2b2929;
	}

	.flexigrid.hideBody {
	    height: 26px !important;
	    border-bottom: 1px solid #ccc;
	}

	.ie6fullwidthbug {
	    border-right: 0px solid #ccc;
	    padding-right: 2px;
	}

	.flexigrid div.nDiv table {
	    margin: 2px;
	}

	.flexigrid div.hDivBox {
	    float: left;
	    padding-right: 0;
	}

	.flexigrid div.bDiv table {}

	.flexigrid div.bDiv table.autoht {
	    border-bottom: 0px;
	    margin-bottom: 0px;
	}

	.flexigrid div.nDiv td {
	    padding: 2px 3px;
	    border: 1px solid #eee;
	    cursor: default;
	}

	.flexigrid div.nDiv td.ndcol1 {
	    border-right: 1px solid #ccc;
	}

	.flexigrid div.nDiv td.ndcol2 {
	    border-left: 1px solid #fff;
	    padding-right: 10px;
	}

	.flexigrid div.nDiv tr:hover td.ndcol1, .flexigrid div.nDiv tr.ndcolover td.ndcol1 {
	    border-right: 1px solid #d2e3ec;
	}

	.flexigrid div.nDiv tr:hover td.ndcol2, .flexigrid div.nDiv tr.ndcolover td.ndcol2 {
	    border-left: 1px solid #eef8ff;
	}

	.flexigrid div.mDiv div {
	    padding: 6px;
	    white-space: nowrap;
	}

	.flexigrid div.mDiv div.ptogtitle {
	    position: absolute;
	    top: 4px;
	    right: 3px;
	    padding: 0px;
	    height: 16px;
	    width: 16px;
	    overflow: hidden;
	    border: 1px solid #ccc;
	    cursor: pointer;
	}

	.flexigrid div.mDiv div.ptogtitle:hover {
	    background-position: left -2px;
	    border-color: #bbb;
	}

	.flexigrid div.tDiv2 {
	    float: left;
	    clear: both;
	    padding: 1px;
	}

	.flexigrid div.sDiv2 {
	    float: left;
	    clear: both;
	    padding: 5px;
	    padding-left: 5px;
	    width: 1024px;
	}

	.flexigrid div.sDiv2 input, .flexigrid div.sDiv2 select {
	    vertical-align: middle;
	}

	.flexigrid div.btnseparator {
	    float: left;
	    height: 22px;
	    border-left: 1px solid #ccc;
	    border-right: 1px solid #fff;
	    margin: 1px;
	}

	.flexigrid div.fbutton {
	    float: left;
	    display: block;
	    cursor: pointer;
	    padding: 1px;
	}

	.flexigrid div.fbutton div {
	    float: left;
	    padding: 1px 3px;
	}

	.flexigrid div.fbutton span {
	    float: left;
	    display: block;
	    padding: 3px;
	}

	.flexigrid div.fbutton:hover, .flexigrid div.fbutton.fbOver {
	    padding: 0px;
	    border: 1px solid #ccc;
	}

	.flexigrid div.fbutton:hover div, .flexigrid div.fbutton.fbOver div {
	    padding: 0px 2px;
	    border-left: 1px solid #fff;
	    border-top: 1px solid #fff;
	    border-right: 1px solid #eee;
	    border-bottom: 1px solid #eee;
	}


	/* end toolbar*/
	.flexigrid div.hDiv {
	    background: #3c3b3b;
	    position: relative;
	    border: 1px solid #ccc;
	    border-bottom: 0px;
	    overflow: hidden;
	}

	.flexigrid div.hDiv table {
	    border-right: 1px solid #fff;
	}

	.flexigrid div.cDrag {
	    float: left;
	    position: absolute;
	    z-index: 2;
	    overflow: visible;
	}

	.flexigrid div.cDrag div {
	    float: left;
	    background: none;
	    display: block;
	    position: absolute;
	    height: 24px;
	    width: 5px;
	    cursor: col-resize;
	}

	.flexigrid div.iDiv {
	    border: 1px solid #316ac5;
	    position: absolute;
	    overflow: visible;
	    background: none;
	}

	.flexigrid div.iDiv input, .flexigrid div.iDiv select, .flexigrid div.iDiv textarea {
	    font-family: Arial, Helvetica, sans-serif;
	    font-size: 11px;
	}

	.flexigrid div.iDiv input.tb {
	    border: 0px;
	    padding: 0px;
	    width: 100%;
	    height: 100%;
	    background: none;
	}

	.flexigrid div.bDiv {
	    border: 1px solid #ccc;
	    border-top: 0px;
	    background: #2b2a2a;
	    overflow: auto;
	    position: relative;
	}

	.flexigrid div.bDiv table {
	    border-bottom: 1px solid #ccc;
	}

	.flexigrid div.hGrip:hover, .flexigrid div.hGrip.hgOver {
	    border-right: 1px solid #999;
	    margin-right: 0px;
	}

	.flexigrid div.vGrip {
	    height: 5px;
	    overflow: hidden;
	    position: relative;
	    background: #000;
	    border: 1px solid #ccc;
	    border-top: 0px;
	    text-align: center;
	    cursor: row-resize;
	}

	.flexigrid div.vGrip span {
	    display: block;
	    margin: 1px auto;
	    width: 20px;
	    height: 1px;
	    overflow: hidden;
	    border-top: 1px solid #aaa;
	    border-bottom: 1px solid #aaa;
	    background: none;
	}

	.flexigrid div.hDiv th, .flexigrid div.bDiv td/* common cell properties*/
	{
	    text-align: left;
	    border-right: 1px solid #ddd;
	    border-left: 1px solid #fff;
	    overflow: hidden;
	    vertical-align: top !important;
	}

	.flexigrid div.hDiv th div, .flexigrid div.bDiv td div, div.colCopy div/* common inner cell properties*/
	{
	    padding: 5px;
	    border-left: 0px solid #fff;
	}

	.flexigrid div.hDiv th, div.colCopy {
	    font-weight: normal;
	    height: 24px;
	    cursor: default;
	    white-space: nowrap;
	    overflow: hidden;
	}

	.flexigrid div.hDiv th.sorted {
	    background: #732323;
	    border-bottom: 0px solid #ccc;
	}

	.flexigrid div.hDiv th.thOver {}

	.flexigrid div.hDiv th.thOver div, .flexigrid div.hDiv th.sorted.thOver div {
	    border-bottom: 1px solid orange;
	    padding-bottom: 4px;
	}

	.flexigrid div.hDiv th.sorted div {
	    border-bottom: 0px solid #ccc;
	    padding-bottom: 5px;
	}

	.flexigrid div.hDiv th.thMove {
	    background: #fff;
	    color: #fff;
	}

	.flexigrid div.hDiv th.sorted.thMove div {
	    border-bottom: 1px solid #fff;
	    padding-bottom: 4px
	}

	.flexigrid div.hDiv th.thMove div {
	    background: #fff !important;
	}

	.flexigrid div.hDiv th div.sdesc {
	    background: none;
	}

	.flexigrid div.hDiv th div.sasc {
	    background: none;
	}

	.flexigrid div.bDiv td {
	    border-bottom: 1px solid #fff;
	    vertical-align: top;
	    white-space: nowrap;
	}

	.flexigrid div.hDiv th div {}

	.flexigrid div.bDiv td div {
	    border-top: 0px solid #fff;
	    padding-bottom: 4px;
	}


	.flexigrid tr td.sorted {
	    background: #260d0d;
	    border-right: 1px solid #ddd;
	    border-bottom: 1px solid #f3f3f3;
	}

	.flexigrid tr td.sorted div {}


	.flexigrid tr.erow td {
	    background: #575454;
	    border-bottom: 1px solid #f7f7f7;
	}

	.flexigrid tr.erow td.sorted {
	    background: #553030;
	    border-bottom: 1px solid #e3e3e3;
	}

	.flexigrid tr.erow td.sorted div {}

	.flexigrid div.bDiv tr:hover td,
	.flexigrid div.bDiv tr:hover td.sorted,
	.flexigrid div.bDiv tr.trOver td.sorted,
	.flexigrid div.bDiv tr.trOver td {
	    background: #274f64;
	    border-left: 1px solid #eef8ff;
	    border-bottom: 1px dotted #4389ae;
	}

	.flexigrid div.bDiv tr.trSelected:hover td,
	.flexigrid div.bDiv tr.trSelected:hover td.sorted,
	.flexigrid div.bDiv tr.trOver.trSelected td.sorted,
	.flexigrid div.bDiv tr.trOver.trSelected td,
	.flexigrid tr.trSelected td.sorted,
	.flexigrid tr.trSelected td {
	    background: #274f64;
	    border-right: 1px solid #d2e3ec;
	    border-left: 1px solid #eef8ff;
	    border-bottom: 1px solid #457893;
	}

	/* novstripe adjustments */
	.flexigrid.novstripe .bDiv table {
	    border-bottom: 1px solid #ccc;
	    border-right: 1px solid #ccc;
	}

	.flexigrid.novstripe div.bDiv td {
	    border-right-color: #fff;
	}

	.flexigrid.novstripe div.bDiv tr.erow td.sorted {
	    border-right-color: #e3e3e3;
	}

	.flexigrid.novstripe div.bDiv tr td.sorted {
	    border-right-color: #f3f3f3;
	}

	.flexigrid.novstripe div.bDiv tr.erow td {
	    border-right-color: #f7f7f7;
	    border-left-color: #f7f7f7;
	}

	.flexigrid.novstripe div.bDiv tr.trSelected:hover td,
	.flexigrid.novstripe div.bDiv tr.trSelected:hover td.sorted,
	.flexigrid.novstripe div.bDiv tr.trOver.trSelected td.sorted,
	.flexigrid.novstripe div.bDiv tr.trOver.trSelected td,
	.flexigrid.novstripe tr.trSelected td.sorted,
	.flexigrid.novstripe tr.trSelected td {
	    border-right: 1px solid #0066FF;
	    border-left: 1px solid #0066FF;
	}

	.flexigrid.novstripe div.bDiv tr.trOver td, .flexigrid.novstripe div.bDiv tr:hover td {
	    border-left-color: #d9ebf5;
	    border-right-color: #d9ebf5;
	}

	/* end novstripe */
	.flexigrid div.pDiv {
	    background: #3c3b3b;
	    border: 1px solid #ccc;
	    border-top: 0px;
	    overflow: hidden;
	    white-space: nowrap;
	    position: relative;
	}

	.flexigrid div.pDiv div.pDiv2 {
	    margin: 3px;
	    margin-left: -2px;
	    float: left;
	    /*width: 1024px;*/
	}

	div.pGroup {
	    float: left;
	    background: none;
	    height: 24px;
	    margin: 0px 5px;
	}

	.flexigrid div.pDiv .pPageStat, .flexigrid div.pDiv .pcontrol {
	    position: relative;

	    top: 5px;
	    overflow: visible;
	}

	.flexigrid div.pDiv input {
	    vertical-align: text-top;
	    position: relative;
	    top: -5px;
	    color: #eeecec;
	    background: #3c3b3b;
	}

	.flexigrid div.pDiv div.pButton {
	    float: left;
	    width: 22px;
	    height: 22px;
	    border: 0px;
	    cursor: pointer;
	    overflow: hidden;
	}

	.flexigrid div.pDiv div.pButton:hover, .flexigrid div.pDiv div.pButton.pBtnOver {
	    width: 20px;
	    height: 20px;
	    border: 1px solid #ccc;
	    cursor: pointer;
	}

	.flexigrid div.pDiv div.pButton span {
	    width: 20px;
	    height: 20px;
	    display: block;
	    float: left;
	}

	.flexigrid div.pDiv div.pButton:hover span, .flexigrid div.pDiv div.pButton.pBtnOver span {
	    width: 19px;
	    height: 19px;
	    border-top: 1px solid #fff;
	    border-left: 1px solid #fff;
	}


	/* ie adjustments */
	.flexigrid.ie div.hDiv th div, .flexigrid.ie div.bDiv td div, div.colCopy.ie div/* common inner cell properties*/
	{
	    overflow: hidden;
	}
}
