/* i really want this to be global */
@-moz-document url(https://www.cambridgescp.com/sites/www.cambridgescp.com/files/legacy_root_files/clc/webbooks/book1/index.html?p=1) {
	/*=====================================================================
	 *
	 * Filename: book.css
	 * Author: Sean Dunwoody
	 * Date Created: 22/10/2014
	 * Media: Screen
	 *
	 * Description
	 * -----------
	 * This file contains styles specific to this book (unit
	  1)
	 *
	 *=====================================================================*/


    h1 { color: #185e8f; }
    .highlight { color: #185e8f; }
	.stage-title h2 { color: #185e8f; }
    .stage-summary-container .page-container { background: #185e8f; }

    .stage-summary-container .page-container.page-178{background-color: #185e8f}
    .stage-summary-container .page-container.page-190{background-color: #185e8f}


	.page-178 #pages-container {background: #000000;}

    .pages-container { background-image: url('https://i.imgur.com/0N9alAZ.png'); }

	.global-nav-container { background: #185e8f; }
	.global-nav a { background: #2382c5; border-top-color: #206ea5; }
	.nav-link-expanded > a,
	.global-nav .nav-link-selected { background: #103c5a; }
	.global-nav-sub { border-left-color: #426b88; }

	.fig-highlight figcaption { color: #185e8f; }

	.fig-list li figcaption:before { color: #185e8f; }

    .page-footer { border-top-color: #185e8f; }
	

	.sidebar-pompeii {
    	background: url('https://www.cambridgescp.com/sites/www.cambridgescp.com/files/legacy_root_files/clc/webbooks/book1/img/stage_3/page36back.jpg');
    	opacity: 0.4;
    	width: 205px;
    	height: 615px;
    	position: absolute;
	}
	.bg-pompeii {
    	position: absolute;
    	width: 525px;
    	height: 615px;
    	margin-top: -8px;
    	background: url('https://www.cambridgescp.com/sites/www.cambridgescp.com/files/legacy_root_files/clc/webbooks/book1/img/stage_3/page37back.jpg');
    	left: 0px;
    	opacity: 0.3;

	}
}
