css:
@-moz-document domain(explosm.net)
{
    table[width="942"] {
        width:768px !important;
    }
    td[style="padding: 0px 10px;"] {
       padding: 0px 0px 0px 10px !important;
    }
    #sidebar {
       background-image: url(http://www.explosm.net/images/prdy/background.gif) !important;
       background-position: top right !important;
       width: 8px !important;
       height: 100% !important;
       padding: 0px !important;
       margin: 0px !important;
       min-height: 600px;
    }
    div#sidebar>div , div#sidebar>font , div#bodycontent>font>table {
        display:none !important;
        /*visibility:hidden !important;*/
    } 
}
@-moz-document url-prefix(http://www.explosm.net/comics/),
               url-prefix(http://www.explosm.net/movies/)
{
    #footer {
       width: 728px !important;
       position: absolute !important;
       bottom: 5px;
    }
}
