css:
@-moz-document domain("clips.twitch.tv") {
/* Share Buttons */
.tw-root--theme-dark .tw-align-items-center.tw-flex.tw-justify-content-end.tw-mg-y-1.tw-relative.tw-z-above {
    display: none !important;
}

/* Remove container padding */
.tw-root--theme-dark .clips-root__main {
    padding: 0 !important;
}

/* Container width */
.tw-root--theme-dark .clips-watch {
    max-width: 100vw !important;
    padding-bottom: 0 !important;
}

/* Hide popular/related clips */
.tw-root--theme-dark .tw-pd-t-1 {
    display: none;
}

/* Extend chat height */
.tw-root--theme-dark .clips-sidebar {
    height: calc(100vh - 50px);
}

/* Vertically centre video */
.tw-root--theme-dark .tw-c-background-base.tw-elevation-3.tw-flex.tw-flex-column.tw-lg-flex-row > .tw-flex-grow-1 {
    height: calc(100vh - 50px) !important;
    background: #000 !important;
    display: flex;
}
}
