/* ==UserStyle==
@name           Darker kurier.at
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A darker theme for kurier.at
@author         MikeZ
==/UserStyle== */

@-moz-document domain("kurier.at") {
 body {
        background-color: #111;
        color: #eee;
    }
    
    .longList-showMore,
    .card,
    .slider-nav,
    .navBar-sub li + li,
    .navBar-secondaryMenu li,
    .navBar,
    .linkBar,
    .media {
        background-color: #333;
    }
    
    .slider-nav-prev,
    .slider-nav-next,
    .slider,
    .longList,
    .noTouch .navBar-item:hover > a,
    .noTouch .navBar-item:hover .navBar-moreButton,
    .navBar-sub {
        background-color: #444;
    }
    
    .input input[type="text"],
    .select {
        background-color: #666;
    }
    
    .accordeon-content-inner, .accordeon-toggle-wrapper, .article, .calendar-main-cell, .card, .comment, .comment-answer, .comments-buttonWrap, .comments-inner, .comments-listAll, .comments-title, .headerSearch, .immoJobBox, .immoJobBox-search-results, .linkBar, .linkCard-link, .liveBlog-highlight, .login, .longList, .longList-showMore, .media, .navBar .portals a, .navBar-overlay .navBar-overlay-inner > li, .navBar-sub, .navigation-inner, .outbrain, .paginator, .search-authorHeader, .search-authorHeader-footer, .search-form button, .search-results, .search-topicHeader, .searchHeader, .singleTeaser, .siteFooter, .siteHeader-inner, .slider, .slider-nav, .tabBar, .tabbedList, .textBox, .videoPlaylist, .videoPlaylist-item, .weather .search, .weather-detail-desc, .weather-detail-main, .weather-detail-secondary, .weather-graph, .weather-list, .weather-listSpecial, .weather-locations, .weather-nav, .weather-warning, .weatherWidget .search, .weatherWidget-inner {
        background-color: #333;
    }

    .bs-c-sponsored .card, .bs-c-sponsored .is-singleSlider, .bs-c-sponsored .linkBar, .bs-c-sponsored .linkCard-link, .bs-c-sponsored .longList-showMore, .bs-c-sponsored .media, .bs-c-sponsored .multi-slider, .bs-c-sponsored .slider-nav, .bs-c-sponsored.media {

        background: #111;
    }
    
    .comments-feedback.isError {
        background-color: #333;
        color: #666;
        box-shadow: inset 0 1px 0 0 #666,inset -1px 0 0 0 #666,inset 0 -1px 0 0 #666,inset 1px 0 0 0 #666;
    }
    
    h1, h2 {
        color: #eee;
    }
    
    .kurierat .headerContainer-top
    {
        background-color: #800111;
    }
    
    .media.paid .teaser-tagLine::before,
    .hero.paid .teaser-title h2::before,
    .topTopics.numbered .teaser-title h2::before,
    .cleverpush-bell {
        background-color: #800111 !important;
    }

.pammesberger.plusContent {
    max-height: 1200px !important;
}

.pammesberger.plusContent:before {
    content: none;
}

#cfs-paywall-container {
    display: none;
}
}
