css:
@-moz-document domain("roblox.com") {
.popover, .dropdown-menu, .accoutrement-item a, .profile-view-selector, .btn-primary-md, .avatar-card-container, .btn-secondary-md, .btn-control-md, .item-card-container, .menu-vertical, .menu-secondary, .hat-slot, .hat-slot img, .menu-secondary-option, .menu-option, .group-details-container-desktop-and-tablet .section-content-off, .header-thumb, #games-switcher, #groups-switcher, #navbar-search-input, .carousel-control, .message-news,
.store-card, .stack-row, .related-asset-container, .related-asset-container img, .nav-tabs, .rbx-login-partial-legacy, .login-button, .asset-thumb-container {
    border-radius: 14px;
}
.scroller, .pager a, .game-card-container, .slide-item-image, .group-image thumbnail-2d img, .input-dropdown-btn, .modal-content, .text-header, .enable-three-dee, .section-content, .form-control, .roblox-popover-container,
.try-it-on {
    border-radius: 14px!important;
}
/*Left*/
.profile-avatar-left, .slide-item-container-left {
    border-radius: 14px 0px 0px 14px!important;
}
/*Right*/
.profile-avatar-right, .profile-avatar-mask, .slide-item-container-right {
    border-radius: 0px 14px 14px 0px!important;
}
/*Down*/
.avatar-card-btns, .alert {
    border-radius: 0px 0px 14px 14px!important;
}
/*Up*/
.game-card-thumb-container img, .item-card-thumb, .chat-windows-header, .chat-main, .dialog-container {
    border-radius: 14px 14px 0px 0px!important;
}
.profile-accoutrements-page, .item-serial-number, thumbnail-2d img, .social-link-icon, .universe-image {
    border-radius: 7px!important;
}
.game-item-container img {
    border-radius: 10px!important;
}
}
