css:
@-moz-document regexp("https://news\\.ycombinator\\.com/.*") {
body {
    background-color: #1c1c1c;
}
a {
    color: #999 !important;
}
.topsel a:link, .topsel a:visited {
    color: #ffffff !important;
}
.hnname a, .pagetop b {
    color: #fff !important;
}
table[id="hnmain"] {
    background-color: #232323;
}
td[bgcolor="#ff6600"] {
    -webkit-box-shadow: 0 .2rem .6rem 0 rgba(0, 0, 0, .25);
    box-shadow: 0 .2rem .6rem 0 rgba(0, 0, 0, .25);
    background-color: #3c3c3c;
    border-color: #222;
    padding: 10px;
}
#hnmain > tbody > tr > td[bgcolor] > table tbody tr td:nth-child(2) .pagetop {
    color: #fafafa;
    padding-left: 10px;
    font-size: 14px;
}
.pagetop, .pagetop a, .pagetop a:visited {
    color: #888;
}
.votearrow {
    background-image: none;
    margin: 0;
    width: 100%;
}
.votearrow:after {
    content:"â–²";
    display: block;
    color: #c7c7c7;
    width: 12px;
    height: 12px;
    font-size: 10px;
}
.itemlist {
    width: 100%;
}
.title, .votelinks, .athing {
    border-color: #222;
    background-color: #2c2c2c;
    padding-top: 5px;
}
.subtext, td[colspan="2"] {
    border-color: #222;
    background-color: #2c2c2c;
    padding-top: 5px;
    padding-bottom: 5px;
}
.subtext a, .subtext span, .subtext {
    color: #777 !important;
}
input:hover, textarea:hover, select:hover {
    border: 1px solid #555;
    box-shadow: 0 0 1px 0 rgba(189, 195, 199, .2);
}
input, textarea, select {
    background-color: #2c2c2c;
    border: 1px solid #444;
    color: #cdd9e2;
    padding: 5px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}
.c00, .c00 a:link {
    color: #777;
}
#hnmain img:first-child {
    filter: grayscale(40%);
}
#hnmain > tbody > tr:first-of-type table img {
    box-sizing: border-box;
    border: 1px black solid !important;
}
}

@-moz-document regexp("https://news\\.ycombinator\\.com/user\\?id=.*") {
.title, .votelinks, .athing {
    background-color: transparent;
}
}

@-moz-document regexp("https://news\\.ycombinator\\.com/item\\?id=.*") {
.subtext, td[colspan="2"] {
    background-color: transparent;
}
.title, .votelinks, .athing {
    background-color: transparent;
}
.default {
    background-color: #2c2c2c;
    width: 100%;
    padding: 5px;
}
.comment * {
    color: #777;
}
.comment a {
    color: #9a9a9a !important;
}
.comment a:visited {
    color: #616161 !important;
}
}

@-moz-document regexp("https://news\\.ycombinator\\.com/newcomments") {
.default {
    padding: 10px;
}
}

@-moz-document regexp("https://news\\.ycombinator\\.com/login.*") {
body {
	color: #9a9a9a;
    margin: 0 auto;
    width: 400px;
}

a {
	color: #9a9a9a;
}

a:visited {
	color: #616161;
}
form {
	background-color: #232323;
    padding: 30px;
    width: 250px;
}
}
