css:
@-moz-document domain("booking.com") {
body {
    color: black
}

.xpi__content__container {
    background: black;
}

.xpi__searchbox {
    padding: 30px;
}

#top {
    background: black
}
.xpi__content__wrapper
{
    border-bottom: 3px solid lime;
}

.xpi__content__wrappergray .xpi__searchbox .sb-searchbox__title, .xpi__content__wrappergray .xpi__searchbox .sb-searchbox__subtitle-text { 
    color: #ffffff
}

/*BACKGROUND OFFRE SAISONNIERE*/
.promo-section > div:last-child {
    background: none;
        border: none;
}

/*BACKGROUND HEADER*/
.header-wrapper {
    background-color: black
}

/*TEXTE CHF*/
#user_form .user_center_nav li a.popover-trigger:hover, #user_form .user_center_nav li a.popover_trigger {
    color: lime;
}

/*TEXTE CHF HOVER*/
#user_form .user_center_nav li a.popover-trigger:hover, #user_form .user_center_nav li a.popover_trigger:hover {
    color: white;
    background:black;
}

/*BOUTON CHF ET DRAPEAU SUISSE BORDER*/
#user_form .user_center_nav li.user_center_option
{
    border: 0px;
}

/*BOUTON AJOUT ETABLISSENT BORDURE*/
#user_form .sign_in_wrapper.sign_in_wrapper-add-property {
    border: 1px solid lime
}

/*BOUTON AJOUT ETABLISSENT TEXTE*/
#user_form .sign_in_wrapper.sign_in_wrapper-add-property {
    color: lime
}

/*BACKGROUND BOUTONS SINSCRIRE SE CONNECTER*/
#user_form .sign_in_wrapper {
    background-color:lime
}

/*TEXTE BOUTONS HOVER SINSCRIRE SE CONNECTER*/
#user_form .sign_in_wrapper:hover {
    color: white
}

/*BACKGROUND NAV*/
#cross-product-bar.cross-product-bar--icons:not(.cross-product-bar--highlight) .xpb__link {
    background-color: black
}

/*BACKGROUND SIGNETS HOVER*/
#cross-product-bar.cross-product-bar--icons:not(.cross-product-bar--highlight) .xpb__link:hover {
    background-color: white
}

/*BACKGROUND SIGNETS*/
#cross-product-bar {
    background-color: black
}

/*TEXTE SIGNETS*/
#cross-product-bar.cross-product-bar--icons .xpb__link {
    color: lime
}

/*TEXTE SIGNETS HOVER*/
#cross-product-bar.cross-product-bar--icons .xpb__link:hover {
    color:black
}

/*ICONES SIGNETS*/
#cross-product-bar .xpb__link__icon {
    fill: lime
}

.index .xpi__content__wrappergray .xp__travel-purpose 
{
    background: black;
    color: white;
}

.index .bui-checkbox__label::before, .bui-checkbox__label::after
{
   border-color: lime; 
}

.bui-banner__content 
{
    color: white;
}

.bui-button--secondary {
    border-color: lime;
}

.bui-button--secondary:hover, .bui-button--secondary:visited, .bui-button--secondary:focus, .bui-button--secondary:active, .bui-button--secondary:link
{
    color: lime;
    background: black;
}
.xp__fieldset {
    background-color: lime
}

.xp__button .sb-searchbox__button {
    background-color: black
}

.xp__button .sb-searchbox__button:hover {
    background-color: lime
}

.xpi__searchbox { 
background-image: url(https://i.pinimg.com/originals/d4/8c/19/d48c19802bdc2528c4c1fc08f8ab27e3.png)}

.nobg {
    background: #00FF00;
    background-image: url(https://i.pinimg.com/originals/d4/8c/19/d48c19802bdc2528c4c1fc08f8ab27e3.png)
}
}
