css:
@-moz-document domain("sbb.ch") {
html {
    color: #eee;
    
}



body {
   background: #111;
}

.mod_header {
    background: #333;
    border-bottom: 0px;
    
    box-shadow: 0px 0px 30px -10px #000;
}

.mod_megadropdown_level1_listitem_title {
    color: #999;
}

input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url] {
    background: #111;
}

.mod_textfield input.mod_textfield_control {
    border-color: #2b2b2b;
}

.mod_teaserelement_url:focus, .mod_teaserelement_url {
    background: #2b2b2b;
    box-shadow: inset 0px 0px 20px -10px #000;
    
    transition: all 0.3s ease;
}

.mod_teaserelement_url:focus, .mod_teaserelement_url:hover {
    background: #222;
    
    box-shadow: inset 0px 0px 30px -5px #000;
}


.mod_homepage_main_teaser_item_image_clip_bottom svg, .mod_homepage_main_teaser_item_image_clip_top svg {
   fill: #111;
}

.mod_homepage_main_teaser_item_image_clip_bottom:after {
    background: #111;
}

.mod_homepage_main_teaser_item_image_clip_top:before {
    background: #111;
}

.mod_lh_tag_container {
    color: #aaa;
}

.mod_megadropdown_level2_list .sub-nav {
    color: #eee;
    background: #333;
}


.mod_footer {
    background: #444;
}

.mod_footer_bottomcontainer {
    background: #222;
}

.mod_clock_container .mod_svgsprite_icon {
    fill: #eee;
}

.face circle {
    fill: #121212;
}

.mod_teaserelement_wrapper {
    color: #eee;
}

.mod_teaserelement_cta {
    color: #eee;
}

.mod_header_logo_content g :first-child{
    fill: #eee;
}
}
