css:
@-moz-document domain("www.diepresse.com") {
/* 
 * USERSTYLESHEET FOR www.diepresse.com / relaunch Sep., 25th, 2019
 * Description:
 * CSS to allow for easy replacement of site-wide fonts and font-styles
 * remove icons of authors
 * resize various elements (read descr. below)
 * change color scheme to old diepresse-Style
 */

html {
  font-family: Calibri, Arial, Helvetica, sans-serif;
  font-size: 100%;
  line-height: 1.5;
  color: #000e1a;
}

body {
  font-family: inherit;
}

p.lead {
  font-family: inherit;  
}

p:not([class]) {
  margin-bottom: 0px;
}

/* Headers */
.header {
  font-family: inherit;
  background-color: #005993;
  border-color: #fff;
  border-width: 1px;
}
.header--first__wrapper {
  border-bottom: 1px #aaa solid;
}
.header .navigation--search {
  border-left: 1px solid #aaa;
  border-right: 1px solid #aaa;
}
.header .navigation--search .search__wrapper {
 border-right: 1px solid #aaa;
}

.header .navigation--search .nav-btn--top > a,
.header .navigation--search .nav-btn--top > a:link,
.header .navigation--search .nav-btn--top > a:hover,
.header .navigation--search .nav-btn--top > a:active
{
  color: #ccc;
}

.header .navigation--search .nav-btn--top > a:hover {
  color: #fff;  
}



h1, h2, h3, h4, h5, h6 {
  color: #005993;
}

h1.article__title {
  font-size: 24pt;
}

h2:not([class]), h3:not([class]) {
  font-family: inherit;
  font-size: 120%;
}

/* Article, Anchor / Link-related */
div.article__body p {
  font-size: 14pt;
}

.article__title, h1:not([class]) {
  margin-bottom: 0px;
}

.article__header {
  display: block;
  margin-left: 3em;
}

.topics__link {
  font-family: inherit;
  font-size: 11pt;
  background-color: #cde;
}

/* Hide author-icons */
.author__image,
.author__image--small
{
  display: none;
}

/* IMAGE STYLES */


/* tame huge title image */
img.card__image {
  width: 90%;
  margin: auto;
}

/*
 * various paragraph styles; "cardXXX"
 * replaceing phony "rem"-units tp typographically save "pt"/"em"
 */

.card {
  font-family: inherit;
  color: #000;
}

.card__kicker span.badge {
  font-family: Arial;
}

.card__title {
  font-family: inherit;
  font-size: 1.6em;
  line-height: 1.2;
}

.card--default-xl .card__title {
  font-size: 22pt;
}

.card__lead {
  margin-bottom: 0;
  padding-right: 40px;
  font-size: 13pt;
}

.card__label {
  display: flex;
  background-color: #005993;
}

.card--default-xs .card__title {
  font-size: 13pt;
}


/* GRID STYLE - widen the grid */
.article__body > * {
  grid-column: center-start 1/center-end 20;
  padding: 10px;
}

/* comments - set font/size straight */
.comments {
  font-family: inherit;
  font-size: 12pt;
}

/* misc. stuff */
#abocontainer-m {
  display: none;
}
.block {
  margin-bottom: 1px;
}

/* FLYOUT stuff */
.flyout {
  background-color: #005993;
  font-family: inherit;
}

.flyout a, .flyout a:hover, .flyout a:link, .flyout a:visited {
  color: #ccc;
}

.flyout a:hover, .flyout a:visited { color: #fff; }

/* BUTTONS etc. */
.btn, a.btn {
  font-family: inherit;
  border-radius: 1px;
  padding: 6px 20px 10px 20px;
  font-size: 12pt;
}

.btn--login, a.btn--login {
  border: 1px solid #ccc !important;
  padding: 6px 20px 10px 20px;
  font-size: 12pt;
  background-color: #004973;
  color: #ccc;
}

/* SIDEBAR stuff */
.mostread__header {
  background-color: #005993;
  color: #fff;
  font-family: Calibri, Arial, Helvetica, sans-serif;
  font-size: 14pt;
  padding: 8px;
}



}
