css:
@-moz-document url-prefix(http://bit-tech.net),
               url-prefix(http://www.bit-tech.net)
{
    #skyscraper-advert, .header-advert, .advert-article-box-index, .advert-spacer {
        display:none !important;
    } 
    #maincontainer {
       background: #FFFFFF !important;
       overflow: hidden;
    }
    #navi {
       border-top: 5px solid #000000;
       padding: 0px !important;
       width: 160px !important;
       background: #FFFFFF;
       height: 100% !important;
       float: left !important;
    }
    #content-pane {
       background-position: 8px 0px !important;
       background-repeat: repeat-y !important;
       border: 5px solid #000000;
       /* width: 760px !important; */
       position: relative !important;
       left: 152px !important;
    }
    #article, #articlenews {
       position: relative !important;
       float: left !important;
       left: -153px !important;  
    }
    #maincol {
       position: relative !important;
       float: left !important;
       left: -153px !important;  
       width: 370px !important;    
    }
    #newscol{
       float: left !important;
    }
    #footer {
       background-position: 8px 0px !important;
       background-repeat: repeat-y !important;
       width: 100% !important;
       clear: both;
    }
    #footer>div {
        margin: 56px 0px 0px 0px !important;
        padding: 14px 14px 0px 55px !important; 
        height: 32px !important;
        position: relative;
        left: -157px;
        background: #000000;
    }
    ul#navi>li>a {
        margin-top:5px !important;
        margin-left:5px !important;
        width: 142px !important;
        font-size: 16px !important;
        float: left !important;
        height: 20px !important;
    }
}

@-moz-document url-prefix(http://forums.bit-tech.net)
{
    #header-ad,div[style~='160px;'] {
        display:none !important;
    } 
    #navi {
        top: 126px !important;
    }
    div[style~='264px;'] {
        height:151px !important;
    }
    div[style~='176px;'] {
        margin-right:0px !important;
    }
}
