css:
@-moz-document domain("www.tuinadvies.be"), domain("www.tuinadvies.nl"), domain("www.matelma.com") {
/*version:20201223.1*/
header[style*="margin-top: -28px;"] {
  margin-top: -105px !important;
  transition: margin-top 0.5s;
}

header[style*="margin-top: -28px;"]:hover,
header[style*=";position:fixed;"], header[style*="margin-top: 0px;"] {
  margin-top: -30px !important;
  transition: margin-top 0.2s;
}

body.new_body #bg_content {
  padding-top: 105px !important;
}

header[style*="margin-top: -28px;"] #header-left-bottom {
  top: 6px;
}
header[style*="margin-top: -28px;"]:hover #header-left-bottom {
  top: 0px;
}

header[style*="margin-top: -28px;"] #header-right-top {
  top: 53px !important;
  position: relative;
}
header[style*="margin-top: -28px;"]:hover #header-right-top {
  position: unset;
}

header[style*="margin-top: -28px;"] #header-right-bottom {
  display: none;
}
header[style*="margin-top: -28px;"]:hover #header-right-bottom {
  display: block;
}
}
