css:
@-moz-document domain("go.twitch.tv"), domain("twitch.tv") {
    :root {
      --purple: #6441A4 !important;
      --new-purple: #9147ff !important;
      --color-background-button-primary-default: #6441A4 !important;
      --white-transparent: rgba(255, 255, 255, 0.7) !important;
  }
  .top-nav__menu {
  	background-color: var(--purple) !important;
  }
  .top-nav__menu p.tw-font-size-4 {
    font-size: 1.6rem !important;
	color: #efeff1;
  }
  .top-nav__menu button, .top-nav__menu button:hover {
  	color: #fff !important;
  }
  .top-nav__menu .tw-avatar__presence {
    background-color: var(--purple);
  }
  .side-nav-card__live-status {
    display: flex !important;
    justify-content: flex-end !important;
    align-items: center !important;
  }
  .top-nav__menu > .tw-justify-content-start > *, .top-nav__menu > .tw-justify-content-start button {
    color: var(--white-transparent) !important;
  }
  .navigation-link.active, .navigation-link:hover, .top-nav__external-link:hover {
    color: #fff !important;
  }
  .tw-channel-status-text-indicator p {
    line-height: 1.9rem;
  }
  .channel-header {
    padding: 0 !important;
  }
  .channel-header > div:nth-child(2) {
    padding: 5px 0 !important;
  }
  .channel-header > div:nth-child(2) > div > div:nth-child(1) > div:nth-child(2) {
    position: relative !important;
  }
  .channel-header > div:nth-child(2) > div > div:nth-child(1) > div:nth-child(2) > div {
    position: absolute !important;
    bottom: 2px;
  }
  .channel-header > div:nth-child(2) > div > div:nth-child(1) > div:nth-child(2) > div button {
    top: -4px;
  }
  button {
    outline: 0;
  }
  .tw-core-button--primary:focus, .tw-core-button--primary:hover {
     background-color: var(--new-purple) !important;
  }
  .hosting-notification > div {
    padding: 8px !important;
  }
  .hosting-notification > div > div:nth-child(1) {
    flex-wrap: wrap !important;
  }
  .chat-input__textarea textarea {
    overflow-y: hidden;
  }
  .chat-input__buttons-container > div:nth-child(2) > div:nth-child(3) {
    display: flex;
  }
}
