css:
@-moz-document domain("twitch.tv") {
.tw-align-center.tw-border-radius-medium.tw-c-text-overlay.tw-channel-status-text-indicator.tw-font-size-6.tw-inline-block.tw-pd-x-05 {
    width: 83px;
    height: 22px;

}

.tw-strong.tw-upcase {
    padding-top: 2px;
    
}

.simplebar-content.root-scrollable__content {
    background-color: #18181b;
    
}

.simplebar-content.side-nav__scrollable_content {
    background-color: #18181b;
    
}

.tw-border-t.tw-pd-1 {
     background-color: #18181b;
    border-top-color: #18181b;
    color: #18181b;
    
}

.tw-border-t {
    border-top: 0px solid #18181b!important;
    border-top: var(--border-width-default) solid #18181b!important;
    border-right: 0px solid #18181b!important;
    border-bottom: 0px solid #18181b!important;
    border-left: 0px solid #18181b!important;
    
}


.tw-flex-grow-2.tw-pd-1 {
    color: #fff
                                            
}

.rooms-header.tw-align-items-center.tw-border-b.tw-flex.tw-flex-shrink-0.tw-full-width.tw-justify-content-center.tw-pd-l-1.tw-pd-r-1 {
    background-color: #18181b;
    
}

.slow {
    display: none !important;
    
}

.channel-leaderboard.tw-z-default {
    display: none !important;
    
}

.simplebar-scroll-content {
    background-color: #18181b;

}

.chat-input.tw-block.tw-pd-b-1.tw-pd-x-1 {
    background-color: #18181b;

}

::placeholder { 
  font-size: 0px;
}

.channel-root__right-column.tw-border-l.tw-flex.tw-flex-column.tw-flex-grow-0.tw-flex-nowrap.tw-flex-shrink-0.tw-full-height {
    border-left-color: #18181b !important; 
    
}

.rooms-header.tw-align-items-center.tw-border-b.tw-flex.tw-flex-shrink-0.tw-full-width.tw-justify-content-center.tw-pd-l-1.tw-pd-r-1 {
    
	border-bottom-color: #18181b !important; 
    
}
}
