css:
@-moz-document url-prefix("https://github.com") {
    
	/* Support for wide monitors */
	.container-lg,
	.container {
		width: auto;
		max-width: 1360px;
		padding-right: 16px!important;
		padding-left: 16px!important;
	}

	/* Narrow top bar */
	header.Header { display: none; }
	.pagehead {
		padding-top: 15px !important;
	}
	
	/* Rounded corner labels w/ white text at all times */
	.label-link,
	.IssueLabel {
		border-radius: 50px;
		font-size: 12px;
		height: 21px;
		line-height: 21px;
		padding: 0px 10px 0 10px;
		color: #fff !important;
		box-shadow: none;
	}
	
	/* Larger rounded corner label previews */
	.label-link {
		font-size: 16px;
		height: 31px;
		line-height: 31px;
		padding: 0px 15px 0 15px;
	}
	
	/* Downshift of labels in views */
	.labels {
		position: relative;
		top: 1px;
	}

	/* Margin separating titles and labels */
	a.h4 {
		margin-right: 4px;
	}
	

	/* Fix for timeline top border being clipped */
	.timeline-comment {
		margin-top: 1px;
	}

	.project-column,
	.js-project-column-cards {
		overflow: visible !important;
	}

	::-webkit-scrollbar {
       display: none;
    }

    #element::-webkit-scrollbar {
       display: none;
    }

    .project-columns-container {
        overflow: hidden !important;
    }

    body, html {
        height: auto !important;
        overflow: hidden;
    }

    body.full-width .container, body.full-width .container-lg, 
    body.split-diff .container, body.split-diff .container-lg {

        padding: 0 !important;

    }

}
