css:
@-moz-document url-prefix("https://www.twitch.tv") {
main {
    background: #111 !important;
}

.tw-root--theme-dark .navigation-link {
    color: #dfdfdf !important;
}
.tw-root--theme-dark .navigation-link:hover {
    color: #a970ff !important;
}
.tw-root--theme-dark .top-nav__external-link {
    color:#dfdfdf;
}
.tw-root--theme-dark .top-nav__external-link:hover {
    color:#a970ff;
}
.tw-c-background-base {
    background-color: #171717 !important;
}
.tw-root--theme-dark .chat-room {
    background: #171717 !important;
}
.tw-border-l {
    border-left: none !important;
}
.tw-root--theme-dark .side-nav__overlay-wrapper {
    background: #171717 !important;
}
.tw-root--theme-dark .channel-header {
    background: #171717 !important;
}
.channel-root__player-container {
    background: #111 !important;
}
.tw-root--theme-dark .channel-root__right-column {
    background: #171717 !important;
}
.tw-textarea {
    background: #242424 !important;
    border: none !important;
}
.tw-input {
    background: #242424 !important;
    border: none !important;
}
.tw-textarea:focus  {
    background: #2b2b2b !important;
}
.tw-input:focus  {
    background: #2b2b2b !important;
    border: none !important;
}
.tw-core-button--primary {
    background: #6936b7 !important;
}
.tw-core-button--primary.tw-core-button--disabled {
    background: #7a3bd9 !important;
    color: rgba(0,0,0,.3);
}
.tw-tabs {
    justify-content: flex-start !important;
}
}
