    /* Import from fonts.google.com */
    /* latin-ext */
    @font-face {
        font-family: 'Caveat Brush';
        font-style: normal;
        font-weight: 400;
        font-display: swap;
        src: local('Caveat Brush'), local('CaveatBrush-Regular'), url(https://fonts.gstatic.com/s/caveatbrush/v5/EYq0maZfwr9S9-ETZc3fKXt8UrOS43o.woff2) format('woff2');
        unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
    } /* latin */
    @font-face {
        font-family: 'Caveat Brush';
        font-style: normal;
        font-weight: 400;
        font-display: swap;
        src: local('Caveat Brush'), local('CaveatBrush-Regular'), url(https://fonts.gstatic.com/s/caveatbrush/v5/EYq0maZfwr9S9-ETZc3fKXt8XLOS.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
    } /* malayalam */
    @font-face {
        font-family: 'Manjari';
        font-style: normal;
        font-weight: 400;
        font-display: swap;
        src: local('Manjari Regular'), local('Manjari-Regular'), url(https://fonts.gstatic.com/s/manjari/v2/k3kQo8UPMOBO2w1UfcHoLnnA.woff2) format('woff2');
        unicode-range: U+0307, U+0323, U+0964-0965, U+0D02-0D7F, U+200C-200D, U+20B9, U+25CC;
    } /* latin */
    @font-face {
        font-family: 'Manjari';
        font-style: normal;
        font-weight: 400;
        font-display: swap;
        src: local('Manjari Regular'), local('Manjari-Regular'), url(https://fonts.gstatic.com/s/manjari/v2/k3kQo8UPMOBO2w1UfdnoLg.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
    }

    body {
        font-family: 'Manjari', "Helvetica Neue", sans-serif !important;
    }

    #cnt {
        /*background-image: linear-gradient(to left, #dee0d9, #dfe1db, #e0e2dd, #e2e3df, #e3e4e1);*/
        background: url(https://images.pexels.com/photos/1043506/pexels-photo-1043506.jpeg?auto=compress&cs=tinysrgb&dpr=3&h=300&w=600) no-repeat left top fixed;
        background-size: cover;
    }
    /*contenedor principal*/
    .big .mw {
        max-width: unset !important;
        /*background-image: linear-gradient(to left, #dee0d9, #dfe1db, #e0e2dd, #e2e3df, #e3e4e1);*/
    }

    /*1ra barra top*/
    #hdtbSum {
        background-image: linear-gradient(to left, #dee0d9, #dfe1db, #e0e2dd, #e2e3df, #e3e4e1) !important;
    }

    /* 2da barra top*/
    div#topabar {
        background-image: linear-gradient(to left, #9C27B0, #eb8f36, #dbb467, #fffffb, #e3e4e1);
    }

    .sfbg {
        /*background-image: linear-gradient(to left, #dee0d9, #dfe1db, #e0e2dd, #e2e3df, #e3e4e1);*/
    }

    /*contenedor de resultados*/
    #cnt #center_col, #cnt #foot {
        width: 85vw !important;
        margin-left: 80px !important;
    }

    /*columna de contenidos*/
    .srg {
        column-count: 2;
    }

    /*el contenedor de cada tarjeta*/
    .LC20lb {
        width: 100%;
    }

    .g {
        max-width: 520px !important;
        margin-bottom: 6px !important;
    }

    /*cada tarjeta*/
    .g > div {
        border-radius: 10px;
        padding: 5px 0;
        background-color: rgba(255, 255, 254, 0.87);
        font-family: 'Manjari', "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    }

    /*url dentro de la carjeta*/
    .TbwUpd {
        padding-left: 20px;
    }

    /*cabecera de la tarjeta*/
    .rc {
        padding-right: 12px;
    }
    .r {
        min-height: 120px !important;
        padding-left: 10px
    }

    /*el contenedor de la direccion destino */
    .TbwUpd {
        line-height: 0.9 !important;
    }

    /*la direccion misma del destino del resultado*/

    .iUh30 {
        line-height: normal !important;
    }
    cite {
        color: #35818d !important;
    }

    /*enlace en el titulo de la cabecera*/
    #res a {
        text-decoration: underline;
    }
    .mblink:visited, a:visited {
        color: #85867b;
    }
    a:link {
        color: #2c1007;
    }

    /*titulo dentro de la cabecera*/
    .r {
        min-height: 80px !important;
        width: 98%;
        margin-left: 1%!important;
        border-radius: 6px;
        overflow: visible;
        white-space: pre-wrap !important;
        font-family: 'Caveat Brush', cursive;
        font-size: 25px !important;
        display: inline-block;
        vertical-align: middle;
        -webkit-transform: translateZ(0);
        transform: translateZ(0);
        box-shadow: 0 0 1px rgba(0, 0, 0, 0);
        -webkit-backface-visibility: hidden;
        backface-visibility: hidden;
        -moz-osx-font-smoothing: grayscale;
        position: relative;
        background-color: #e8dcc9!important;

    }

    .r:hover {
        background-color: #e0d4b6;
    }
    .r:before {
        pointer-events: none;
        position: absolute;
        content: '';
        height: 0;
        width: 0;
        bottom: 0;
        left: 0;
        background: white;
        /* IE9 */
        background: linear-gradient(45deg, white 45%, #aaaaaa 50%, #cccccc 56%, white 80%);
        box-shadow: 1px -1px 1px rgba(0, 0, 0, 0.4);
        -webkit-transition-duration: 0.3s;
        transition-duration: 0.3s;
        -webkit-transition-property: width, height;
        transition-property: width, height;
    }

    .r:hover:before {
        width: 25px;
        height: 25px;
    }

    /*el enlace de 'traducir esta pagina', lo oculto*/
    .r a.fl {
        display: none;
    }

    /*footer de la tarjeta*/
    .s {
        color: #1c1414;
    }
    .rc .s {
        padding: 10px;
    }

    /*'otras personas tmb buscaron ''*/
    .d8lLoc {
        margin-left: 30px;
    }
    .d8lLoc a {
        color: #E91E63;
        margin-left: 5px;
    }

    /*otras busquedae en pie de pagina*/
    div#brs a {
        color: #9C27B0;
    }

    /*tarjetas de youtube*/
    .cv2VAd {
        background-color: rgba(185, 145, 119, 0.74) !important;

    }

    /*footer de esas tarjetas*/
    .RgAZAc {
        color: #efeeeb !important;
    }

    /*fecha dentro de esas tarjetas*/
    .zECGdd {
        color: #FFEB3B !important;
    }


    /*links de noticias*/
    .qmv19b{
        background-color: rgba(231, 231, 231, 0.9)!important;
    }

    /*los anuncios pagados*/
    .ads-ad {
        background-color: rgba(226, 226, 224, 0.88)!important;
        color: #33373a !important;
        padding: 10px;
    }

}
