css:
@-moz-document domain("twitch.tv")
{
body {background-color: #faf9fa!important;}
* {  font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif!important;}
.front-page-carousel {display:none;}
.qa-theatre-mode-button{
    position: absolute;
	bottom: 150%;
    width: 200%;
	height: 2500%;
	cursor:copy!important;}
.channel-info-bar {
background: rgb(15,14,17);
background: linear-gradient(0deg, rgba(15,14,17,0) 5%, rgba(25,23,28,1) 500%);
    border:none!important;
}
.player-slider__buffer{background: black;}
.player-slider__left {
    background: #6441a4;}
.pl-controls-top, .pl-controls-bottom, .player-play-overlay
{     background: none;
}
.pl-pinned-item, .player-streaminfo, .player-streamstatus
{    display: none;
}
.pl-clips-button {
filter: invert(100%);
  position: absolute;
  right: 28px;}
h5 {padding-bottom: 10px; font-weight: bold;color:#772ce8; padding-top: 6px;}
.tw-button-icon__icon  {color: #6441a4!important;}
.tw-elevation-1 {
background: #4d307c!important;
}
.tw-input--large{
background-color: white; opacity:0.4;
}
.tw-mg-0{color:white;}
.rooms-header, .chat-room, .tw-border-t.tw-pd-1, .side-nav__overlay-wrapper, .tw-root--theme-dark .chat-room {background: #efeef1; 
}
.tw-c-text-base, .tw-c-text-alt, .tw-link--inherit {color:black!important;}
.chat-input__textarea textarea {
background: white; border-color:#9580b0;
}
.tw-mg-1, .channel-header {border-bottom: var(--border-width-default) solid var(--color-border-base)!important;
}
.side-nav__overlay-wrapper, .tw-border-t {border-right: var(--border-width-default) solid var(--color-border-base)!important;
}
.tw-mg-1 {border-color:#772ce8!important;}
span.chat-author__display-name {
    color: red !important;
    letter-spacing: 1.2px !important;
    font-size: 12px;     
}
.tw-full-width.tw-justify-content-start p {color: #c2b8db; font-weight: bold; font-size: 9px!important; opacity:0.7;}
.tw-hide.tw-pd-x-2.tw-xl-flex {padding-left: 0rem!important;}
.top-nav__external-link.tw-interactive:nth-child(n+5), .tw-align-self-center.tw-pd-x-1:last-child  {display:none!important;}
 .tw-hide.tw-lg-flex, .prime-offers
  {
    display: none !important;
  }
.whispers-threads-box__container {
	opacity: 0 !important;
	transition: opacity 0.2s ease-in-out;
}
.whispers-threads-box__container:hover {
    opacity: 1 !important;
  }
.channel-leaderboard {
    	opacity: 0;
    	height: 0rem;
    	transition: opacity 0.2s ease-in-out;}

.channel-leaderboard:hover {
    	opacity: 1;}
.tw-tower--300>* {
    width: 25%!important;
}
}
