css:
@-moz-document domain("twitch.tv") {
.tw-root--theme-dark body {
     background-color: #0f0e11 !important;
}


.tw-root--theme-dark{
    --color-background-button-primary-hover:#6441a4; 
}


.tw-root--theme-dark .top-nav__menu {
    background-color: #4b367c!important;
}

.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: #19171c!important;
}


.tw-root--theme-dark .channel-header {
    background-color: #0f0e11!important;
}

.tw-root--theme-dark .channel-info-bar {
    background-color: #0f0e11!important;
}


/*CHAT*/

/*cheer leaderboard*/
.tw-root--theme-dark .channel-leaderboard .tw-c-background-base {
    background-color: #19171c!important;
}

.tw-root--theme-dark .chat-input {
    background-color: #0f0e11;    
}

.chat-list {
    font-family: Helvetica, sans-serif;
}


/*chat background*/
.tw-root--theme-dark .chat-list {
    background-color: #0f0e11;
}

.tw-root--theme-dark .rooms-header {
    background-color: #0f0e11;
}

/*input field*/
.chat-input__textarea {
    font-family: Helvetica, sans-serif;
}

/*chat message*/
.tw-root--theme-dark .text-fragment {
    color: #dad8de;
}



/*subscribe button*/
.tw-root--theme-dark .tw-core-button--primary {
    background-color: #6441a4;
}


/*czfix*/
.tw-channel-status-text-indicator p {
   white-space: nowrap;
}

.tw-root--theme-dark .channel-root {
     background-color: #0f0e11;
}

/*channel*/
.tw-root--theme-dark .side-nav-card__title p {
    color: #cac8d0!important;
}

/*category*/
.tw-root--theme-dark .side-nav-card__metadata p {
    color: #898395!important;
}

/*viewers*/
.tw-root--theme-dark .side-nav-card__live-status span {
    color: #898395!important;
}

/**/
.tw-c-background-accent {
    background-color: #4b367c!important;
}



.tw-root--theme-dark .tw-textarea:focus, .tw-root--theme-dark .tw-input:focus {
    background-color: #0f0e11;
} 


.theme--dark .pl-menu__item--block button:focus,.theme--dark .pl-menu__item--block button:hover {
    background-color: #6441a4;
}
 

.tw-svg__asset--verified {
    fill: hsl(264, 53%, 63%);
}

.tw-root--hover .tw-interactable--inverted.tw-interactable--hover-enabled:hover {
   background-color: #6441a4;
}

/*chat input background for vods*/
.tw-root--theme-dark .video-chat__input {
    background-color: #0f0e11;
}

.video-chat__input {
    font-family: Helvetica, sans-serif;
}

/*chat background for vods*/
.tw-root--theme-dark .video-chat__message-list-wrapper {
    background-color: #0f0e11;    
}

.video-chat__message-list-wrapper {
    font-family: Helvetica, sans-serif;
}

}
