css:
@-moz-document domain("twitch.tv") {
body {
    font-family: Helvetica, sans-serif !important;
}
body,
.tw-root--theme-light .side-nav__overlay-wrapper, 
.side-nav__overlay-wrapper,
.tw-root--theme-light .directory-header-new__banner-cover,
.tw-root--theme-light .channel-header, .channel-header,
.tw-root--theme-light .chat-room,
.tw-root--theme-light .channel-root__right-column {
    background: #ebe8f2 !important;
    color: #000 !important;
}
.tw-c-background-base{
    background-color: #4b367c !important;
} 
.emote-picker{
    background-color:#fafafb
}
.chat-user-card{
    background:#000;
}
.t-bits-card__footer{
    background:#4b367c !important;
}
.t-bits-card__contents{
    background:#fff !important;
}
.tw-pd-l-1{
    background:#fff
}
.cheermote-prefix-selector{
    background:#fff !important;
}
.sub-modal__container{
    background-color: #fff !important;
} 
.channel-leaderboard-header__users {
    background: #efeef1 !important;
}
.tw-interactable {
    background: #e2e1e3 !important;
}
.tw-interactable--selected {
    background: #cbc6d0 !important;
}
.top-nav__menu{
    background: #4b367c !important;
}
.top-nav{
    background: #4b367c !important;
}
.tw-button-icon__icon{
    color:#e2e1e3 !important;
}
.tw-c-text-base{
    background: #efeef1 !important;
}
.tw-c-text-alt{
    color: #c7c5cc !important;
}
.tw-c-text-alt-2{
    color: #9f9ba6 !important;
}
.tw-tab-item{
    background-color:#ebe8f2
}
.tw-input--large {
    background: #efeef1 !important;
}
.tw-combo-input__button-icon--large{
    background: #efeef1 !important;
}
.subscribe-button__dropdown{
    background-color:#fff
}
.navigation-link{
    color: #cbc1e1 !important;
}
.side-nav__scrollable_content{
    background: #17141f !important;
}
.side-nav-card__link:hover{
    background: #3d3553 !important;
}
.side-nav-header{
    color: #a4a1aa !important;
}
.tw-border-b {
    border-bottom: 1px solid #d0d0d0 !important;
}
.tw-border-l {
    border-left: 1px solid #252525 !important;
}
.tw-elevation-1 {
    box-shadow: none !important;
    border-bottom: 1px solid #252525 !important;
}
.tw-core-button--primary{
    color: #fff;
    background-color:#4b367c
}
.tw-core-button--secondary{
    color:#e2e1e3 !important;
}
.tw-svg{
    color:#313791
}
.tw-icon__svg{
    color:#9495a6
}
.tw-link--button{
    color:#8285ae
}
.tw-root--hover .tw-core-button--destructive:hover:not(.tw-core-button--disabled){
    background-color:#4b367c
}
.tw-svg__asset--lock{
    color:#fff;
}
.tw-svg__asset--heart{
    color:#dedfe6;
}
.tw-svg__asset--unheart{
    color:#dedfe6;
}
.tw-svg__asset--followhollow{
    color:#dedfe6;
}
.tw-svg__asset--notificationbell{
    color:#dedfe6;
}
.tw-svg__asset--notificationbelloff{
    color:#dedfe6;
}
.tw-svg__asset--notificationbellfilled{
    color:#dedfe6;
}
h1 {
    font-size: 4.5rem !important;
}
.tw-semibold, .tw-strong {
    font-weight: 300!important;
}
h2[data-a-target="stream-title"] {
    font-weight: 300 !important;
}
.tw-textarea {
    background-color: #fff !important;
}
}
