css:
@-moz-document domain("hlpugs.tf") {

@font-face {
	font-family: tfclasses;
	src: url(../fonts/tfclasses.ttf);
}

:root {
	/*HLPugs.tf color palette*/
	--primary-color: #960500;
	--primary-translucent-color: rgba(150, 5, 0, 0.75);
	--light-primary-color: #B52F1B;
	--dark-primary-color: #680300;
	--darker-primary-color: #771401;
	--background-color: #121212;
	--background-translucent-color: rgba(21, 21, 21, 0.9);
	--light-background-color: #121212;
	--dark-background-color: #050505;
	--container-color: #ffffff;
	--text-color: #ffffff;
	--chat-highlight-color: #4e1111;
	--chat-highlight-accent-color: #b71c1c;
	--patron-color: #85bb65;
	--team-red-color: #f44336;
	--team-blu-color: #2196f3;
	--button-color: #5a6676;
	--success-color: #6ac85d;
	--success-accent-color: #417939;
	--danger-color: #c62828;
	--danger-accent-color: #811818;
	--warning-color: #d6a411;
	--warning-accent-color: #ffa000;
}

/*custom scrollbar styling*/

.ss-content {
	overflow-x: hidden;
}

.ss-scroll {
    position: relative;
    background-color: rgba(255, 255, 255, 0.1);
    width: 9px;
    border-radius: 4px;
    top: 0;
    z-index: 2;
    cursor: pointer;
    opacity: 0;
    transition: opacity 0.25s linear;
}

@media (max-aspect-ratio: 187/100) {
	.header.classHeader > h1 {
		display: none;
	}
	
	.header {
		height: 4vh;
	}
}

@media (max-aspect-ratio: 159/100) {
	.listholder > div > a > img {
		display: none;
	}
	
	.listholder > div > a > span > b > i {
		display: none;
	}
}

@media (max-aspect-ratio: 113/100) {
	.classCounter {
		display: none;
	}
	#siteName {
		display: none;
	}
	#siteRegion {
		display: none;
	}
	#sponsorLink {
		display: none;
	}
}

.preReadyActive {
	background-color: var(--light-primary-color);
	transform: rotateY(360deg);
	transition: 0.5s ease transform;
}

.preReadyDisabled {
	color: rgba(255, 255, 255, 0.3);
	transition: 0.25s ease color;
}

.toolbarItem.preReadyDisabled:hover {
	color: rgba(255, 255, 255, 0.3);
	transition: 0.25s ease color;
}

.header > h1 {
	cursor: pointer;
}

#sponsorPlayerLink {
	flex-shrink: 0;
}

.chatLink {
	color: #e1f5fe;
}

.statsContainer {
	background-color: var(--darker-primary-color);
	border: .304vh solid var(--darker-primary-color);
	font-size: 2vh;
	height: 24vh;
	line-height: 30px;
	margin: auto;
	position: absolute;
	opacity: 0.9;
	text-align: center;
	vertical-align: middle;
	width: 9.8vw;
}

#displayPugContainer {
	width: 100%;
}

.serverDivider {
	width: 10.25vh !important;
}

.bluteamserver {
	width: 100%;
	text-align: right;
}

.tfclass {
	font-weight: 100;
	font-family: 'tfclasses' !important;
	font-size: 3vh !important;
}

.headerClass {
	margin-left: .5vh;
}

.noPugs {
	height: 3vh;
}

.pugInfoOverview {
	border: .304vh solid var(--darker-primary-color);
	border-bottom: 0px;
	border-top: 0px;
	display: none;
	height: 9vh;
}

.map {
	height: 6vh !important;
	object-fit: cover;
	width: 12vw;
}

.statsBody {
	background-color: rgb(33, 33, 33);
	height: 85%;
	width: 100%;
}
 
.playerCircle {
	justify-content: space-between!important;
	/*background-color: inherit !important;*/
}

.playerCircle span {
	position: relative;
	margin-left: 0.5vw;
	margin-right: 1vw;
	bottom: 1.1vh;
}

.playerCircle img {
	margin-right: 0.25vw;
}

#roles > thead > tr > th > button {
	box-shadow: 0.2vh 0.2vh 1vh rgba(0, 0, 0, 0.2) !important;
	transition: border-bottom 0s ease;
	transition: background 0.2s ease;
}

#roles .cancelButton {
	width: 2vw;
	font-size: 0.75vw;
	height: 3vh;
}

#roles td {
	justify-content: space-evenly;
}

#roles .playerArrResult button {
	position: relative;
	width: 5vw;
	bottom: 1vh;
	margin-left: 1vw;
}

#roleSearchContainer {
	border-top: 1.5vh solid var(--primary-color);
}

#roles {
	min-width: 70%;
}

#roleName {
	margin: 2vw;
}
 
#adminbar {
	background: var(--darker-primary-color);
	box-shadow: .75vh .5vh 1vh rgba(0, 0, 0, 0.3);
	display: flex;
	flex-direction: column;
	height: 100%;
	width: 13vw;
}

#adminContent {
	overflow: auto;
	overflow-x: hidden;
	padding: 1vw;
	padding-bottom: 0vh;
	position: relative;
	width: 100%;
}

#adminMain {
	display: flex;
	height: 93.5vh;
}

#adminSearch>tr>td {
	justify-content: center;
	/*text-align: center;*/
}

#adminSearch>tr>td:first-child {
	justify-content: space-between;
	width: 40%;
}

#adminSearch>tr>td:nth-child(2) {
	width: 20%;
}

#adminSearch>tr>td:nth-child(3) {
	width: 10%;
}

#adminSearch>tr>td:nth-child(4) {
	width: 10%;
}

#adminSearch>tr>td:nth-child(5) {
	width: 10%;
}

#adminSearch>tr>td:nth-child(6) {
	width: 10%;
}

#announcementsTable > #adminSearch>tr>td:first-child {
	width: 100%;
}

.adminPages:not(#adminServers) {
	display: none;
}

.adminTab > .fa-ban {
	font-size: 1.7vh !important;
}

#aliasHolder {
	align-items: center;
	display: flex;
	flex-direction: column;
}

.announcement {
	background-color: var(--primary-color);
    /* border-radius: 3px; */
    cursor: pointer;
    display: block;
    font-size: 1.8vh;
    height: 3.3vh;
    margin: 0 0.25vw 0.5vh 0.25vw;
    padding: 0.5vh 0.5vw 0;
    position: relative;
    /*z-index: -1;*/
    border-bottom: .508vh solid var(--dark-primary-color);
}

.announcement a {
	text-decoration: none;
}

.announcementText {
	margin: 0;
	opacity: 0;
}

#warningsTable > tbody td:nth-child(1) {
	width: 18%;
}

tr:not(.expired) .editMessage:hover {
	cursor: pointer;
	background-color: var(--primary-color);
}

.warning {
	background-color: var(--warning-color);
	display: block;
	font-size: 2vh;
	margin: 0 0.25vw 0.5vh 0.25vw;
	padding: 0.5vh 0.5vw 0;
	position: relative;
	border-bottom: .508vh solid var(--warning-accent-color);
}


#newAnnouncementMessage {
}

#arrow {
	transition: transform .25s ease;
}

#bansTable, #announcementsTable {
	table-layout: fixed;
	width: 100%;
}

#bansTable thead tr {
	height: 7vh;
}

#bansTable>tbody>tr>td:first-child {
	/*width: 100%;*/
	width: 30%;
}

#bansTable>tbody>tr>td:nth-child(2) {
	/*width: 100%;*/
	width: 50%;
}

#bansTable>tbody>tr>td:nth-child(3) {
	width: 100%;
	word-break: break-all;
	word-wrap: break-word;
}

#bansTable>tbody>tr>td:nth-child(4) {
	/*width: 100%;*/
	width: 50%;
}

#bansTable>tbody>tr>td:nth-child(5) {
	width: 100%;
}

#bansTable>tbody>tr>td:nth-child(6) {
	width: 100%;
}

#bottomBar {
	background-color: #191919;
	border-bottom: solid .508vh var(--dark-background-color);
	bottom: 0;
	height: 3.75vh;
	position: absolute;
	width: 100vw;
}

#captainList {
	display: flex;
	flex-grow: 1;
	flex-wrap: wrap;
	justify-content: space-between;
	min-height: 4.94vh;
}

#captainList .player {
	width: calc(50% - 1.16vh);
}

#captainList a {
	width: 100%;
}

#captains {
	margin: 0 0.1vw 0 0.25vw;
	min-height: 5vh;
	overflow: hidden;
	width: 19.8vw;
}

#centeralias {
	border: none;
	border-bottom: solid .25vh #ccc;
	font-size: 1.5vh;
	height: 3vh;
	outline: none;
	padding: 1vh;
	width: 15vw;
}

#chat {
	flex-shrink: 1;
	height: 100%;
}

#chatholder {
	height: 100%;
	list-style: none;
	margin: 0;
	overflow-x: hidden;
	/*overflow-y: scroll;*/
	padding: 0;
	/*padding-right: 1.72vw;*/
	width: 100%;
}

#chatholder .chatitem:nth-child(even) {
	background-color: var(--light-background-color);
}

#chatlog {
	background-color: var(--background-translucent-color);
	/*border-left: 0.3vh solid var(--darker-primary-color);*/
	/*border-right: 0.3vh solid var(--darker-primary-color);*/
	box-sizing: border-box;
	display: flex;
	flex-direction: column;
	height: 85vh;
	margin-bottom: 3vh;
	/*overflow: hidden;*/
	vertical-align: bottom;
	width: auto;
}

.disabledClassList>.player {
	opacity: .2;
}

#chatmessage {
	background-color: var(--light-background-color);
	border: 0;
	border-bottom: 0.1vh solid var(--primary-color);
	border-radius: 0;
	color: var(--text-color);
	font-family: Lato, sans-serif;
	font-size: 1.5vh;
	height: 3vh;
	margin: 0.5vh;
	outline: none;
	overflow: hidden;
	padding: 0;
	resize: none;
	text-align: left;
	text-shadow: 0px 0px;
	transition: 0.25s border-bottom;
	width: calc(100% - 1.016vh);
}

#chatmessage:focus {
	border-bottom: 0.1vh solid var(--text-color);
	transition: 0.25s border-bottom;
}

#chatmessage::placeholder {
	font-family: Lato, sans-serif;
	/*commented out due to Firefox bug*/
	/*opacity: 1;*/
	transition: 0.25s opacity;
}

#chatmessage:focus::placeholder {
	opacity: 0;
	transition: 0.25s opacity;
}

#profileStatFlex {
	display: flex;
	justify-content: space-evenly;
	margin-bottom: .5vh;
}

#chatNotiHold {
	align-items: center;
	display: flex;
}

#classes {
	display: flex;
	/* flex-wrap: wrap; */
	justify-content: center;
	margin: 0.75vh 0.25vw;
}

#clock {
	bottom: 0;
	font-size: 2vh;
	margin: 0.5vh;
	position: absolute;
	right: 15vw;
}

.connectedNumberLabel {
	font-size: 1.75vh;
	padding-left: .5vh;
}

#connectedNumber {
	font-size: 1.75vh;
	padding-left: .5vh;
}

#connectInfo {
	/*margin-top: 20vh;*/
	align-items: center;
	background: rgba(0, 0, 0, .75);
	bottom: 0;
	display: flex;
	height: 100vh;
	position: absolute;
	text-align: center;
	top: 0;
	width: 85vw;
}

#connectInfo .bluRoster td, #connectInfo .bluRoster th:last-child {
	background-color: var(--team-blu-color);
	border: none;
	padding: 0 1vw;
}

.bluRoster, .redRoster {
	margin: 0 auto;
	text-align: center;
}

#connectInfo .redRoster td, #connectInfo .redRoster th:last-child {
	background-color: var(--team-red-color);
	border: none;
	padding: 0 1vw;
}

#loading {
	align-items: center;
	background: var(--background-color);
	bottom: 0;
	display: flex;
	flex-direction: column;
	height: 100vh;
	justify-content: center;
	position: absolute;
	text-align: center;
	top: 0;
	width: 100vw;
}

#loading>h1 {
	font-size: 5vh;
	margin: 0 auto;
}

#loadingBar {
	background-color: var(--primary-color);
	height: 100%;
	transition: width 60s linear, background-color 30s linear;
	width: 0;
}

#loadingHeader {
	display: none;
}

#loadingHolder {
	background-color: var(--container-color);
	height: 2vh;
	margin: 0 auto;
	margin-bottom: .5vh;
	width: 12.5vw;
}

#lockChat {
	align-items: center;
	cursor: pointer;
	display: flex;
	font-size: 1.75vh;
	height: 3vh;
	justify-content: center;
	transition: color .2s ease;
	width: 3vh;
}

#lockChat:hover {
	color: #B3E5FC;
}

#logoContainer {
	display: flex;
	margin-left: 1vh;
}

#logo {
	height: 6vh;
	margin-top: 1vh;
}

#logoText {
	display: flex;
	flex-direction: column;
	margin-left: 1vh;
	margin-top: 0.5vh;
}

#siteBrand {
	display: flex;
	font-weight: bold;
	margin-top: 1vh;
}

#siteName {
	font-size: 2.5vh;
}

#siteRegion {
	font-size: 1.2vh;
	margin-left: 2vh;
	margin-top: 1.3vh;
}

#sponsorLink {
	font-size: 1.2vh;
	margin-left: 0.1vh;
}

#main {
	display: flex;
	height: 89.5vh;
	overflow: auto;
	position: absolute;
	width: 85vw;
	overflow-y: hidden;
}

#main-area {
	flex-shrink: 1;
	height: calc(100% - 1vh);
	overflow-x: hidden;
	width: 100%;
	padding-top: 1vh;
}

#management tbody {
	border: none;
}

#management tbody tr th {
	flex-direction: row !important;
	justify-content: space-between !important;
	padding-left: .5vh;
	padding-right: .5vh;
}

#management tbody tr th button {
	margin-left: 1vh;
	width: 9vh !important;
}

#mapRow {
	height: auto;
}

#mapRow td:first-child {
	background: none;
}

#mapRow td {
	text-align: center;
	max-width: 100%;
}

#map-section {
	height: 0;
	margin: 0 0.25vw;
	overflow: hidden;
	transition: height .5s ease, margin .5s ease;
	z-index: -2;
}

#notReadyButton {
	background-color: var(--danger-color);
	border-color: var(--danger-accent-color);
}

#onlineUserList {
	background-color: #1d1d1d;
	border-right: .304vh solid var(--darker-primary-color);
	border-top: .304vh solid var(--darker-primary-color);
	bottom: -20vh;
	display: flex;
	flex-direction: column;
	height: 20vh;
	overflow-y: auto;
	position: absolute;
	transition: bottom .4s ease;
	width: 10vw;
	overflow-x: hidden;
}

#onlineUserList::-webkit-scrollbar {
	background: transparent;
	width: 0px;
}

#pickBar {
	background: var(--success-color);
	height: 2vh;
	/*0.5s ease*/
	transition: width 1s linear;
	width: 0vw;
}

#pickBarHolder {
	background: white;
	height: 2vh;
	overflow: hidden;
	width: 45vw;
}

.pickContainer>td {
	/*transition: background-color, easeInElastic, 1s;*/
}

#pick-section {
	height: 0;
	margin: 0 0.25vw;
	overflow: hidden;
	transition: height .5s ease;
	z-index: -2;
}

#pickTimer {
	align-items: center;
	display: flex;
	flex-direction: row;
	justify-content: center;
	margin-right: 1vw;
}

#playerAvatar {
	flex-shrink: 0;
	height: 7vh;
	margin-right: 1vw;
	width: 7vh;
}

#playerClasses td {
	background: none;
	border: none;
	color: #333;
	font-size: 1.7vh;
	height: 3.5vh;
}

#playerClasses thead {
	background: none;
}

#playerClasses tr {
	height: 3.5vh;
}

#playersTable thead tr {
	height: 7vh;
}

#playerTable {
	margin-left: 1vw;
}

#playerTable tr {
	height: auto;
}

#playerTable tr td {
	background: none !important;
	border: none;
	border-bottom: .2vh solid var(--primary-color);
	border-collapse: collapse !important;
	border-right: .2vh solid var(--primary-color);
	font-size: 1.5vh;
	height: 2.5vh;
	min-height: 0;
	width: 4vw;
}

#playerTable tr td:first-child {
	border-left: .2vh solid var(--primary-color);
}

#playerTable tr:first-child td {
	border: .2vh solid var(--primary-color);
	border-left: none;
}

#readyButton {
	background-color: var(--success-color);
	border-color: var(--success-accent-color);
}

#readyButtons {
	align-items: flex-end;
	display: flex;
}

#ready-section {
	align-items: center;
	display: flex;
	height: 10vh;
	justify-content: space-around;
	margin: 0 0.25vw 0 0.1vw;
	position: relative;
	width: 70vw;
	z-index: -2;
}

#readyUp {
	align-items: center;
	background: rgba(0, 0, 0, .75);
	bottom: 0;
	display: flex;
	height: 100vh;
	position: absolute;
	text-align: center;
	top: 0;
	width: 100vw;
}

#readyUpBar {
	background: var(--success-color);
	height: 3vh;
	/*0.5s ease*/
	transition: width 1s linear;
	width: 0vw;
}

#readyUpBarHolder {
	background: white;
	height: 3vh;
	overflow: hidden;
	width: 60vw;
}

#readyUpHolder {
	margin: 0 auto;
	text-align: center;
}

#readyUpTimer {
	flex-direction: column;
	align-items: center;
	background: var(--primary-color);
	display: none;
	height: 100%;
	justify-content: center;
	position: absolute;
	width: 100%;
}

#submit {
	display: flex;
	flex-shrink: 0;
}

#subSection {
	display: flex;
	transition: margin .5s ease;
}

#toolbar {
	/*overflow: hidden;*/
	white-space: normal;
	align-items: center;
	/*TODO (minicircle): fix properly*/
	background-color: var(--primary-color);
	display: flex;
	flex-shrink: 1;
	height: 100%;
	justify-content: flex-end;
	margin-left: 100vw;
	right: 1vw;
	position: absolute;
	width: 50%;
}

#topbar {
	opacity: 0;
	align-items: center;
	background: var(--primary-color);
	border-bottom: solid .508vh var(--dark-primary-color);
	box-sizing: border-box;
	display: flex;
	height: 6vh;
	padding: 0;
	position: relative;
	z-index: 3;
}

#top-section {
	display: flex;
	justify-content: center;
	overflow: hidden;
	transition: height .5s ease;
}

#userInfo {
	align-items: center;
	display: flex;
}
/*#userOptions .dropdown-content {*/
/*	visibility: hidden;*/
/*}*/
#userOptions:hover .dropdown-content {
	box-shadow: .25vh .25vh .5vh rgba(0, 0, 0, 0.25);
	top: 6vh;
	transition: top .25s ease;
}

#usersConnected {
	align-items: center;
	bottom: 0;
	cursor: pointer;
	display: flex;
	left: 0;
	margin: .5vh;
	position: absolute;
	transition: color .2s ease;
}

#usersConnected:hover {
	color: #eee;
}

#voicepackimg {
	height: 13vh;
	margin-bottom: 1vh;
	width: 13vh;
}

* {
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}

#canvas {
	width: 100%;
	height: 100%;
	position: fixed;
	top: 0;
	left: 0;
	z-index: 999999;
	pointer-events: none;
	-moz-pointer-events: none;
	-webkit-pointer-events: none;
	-o-pointer-events: none;
}

.acceptButton {
	background-color: var(--success-color);
	border-color: var(--success-accent-color) !important;
}

.addUpBtn {
	background-color: #ccc;
	border: 0;
	border-radius: 1vh;
	height: 2vh;
	outline: 0;
	width: 2vh;
}

.admin {
	color: #923e3b!important;
}

.adminInput {
	background-color: #282828;
	border: 0;
	border-bottom: .102vh solid white;
	border-radius: 0;
	color: var(--text-color);
	font-family: Lato, sans-serif;
	font-size: 1.626vh;
	height: 3.048vh;
	margin: .508vh;
	outline: none;
	padding: 0;
	padding-left: .508vh;
	text-align: left;
	text-shadow: 0px 0px;
	width: 100%;
	width: calc(100% - 1.016vh);
}

#autocomplete {
	background-color: #1d1d1d;
	border: 0.3vh solid var(--darker-primary-color);
	bottom: 4.25vh;
	display: none;
	flex-direction: column;
	height: 25vh;
	overflow: hidden;
	position: absolute;
	right: 15vw;
	width: 18vw;
	z-index: 5;
}

#favorite-classes {
	background: var(--dark-primary-color);
	border-radius: 4px;
	display: flex;
	flex-direction: column;
	margin: 1vh auto;
	padding: 1vh 0.5vw;
	width: calc(100% - 2vw);
}

.favorite-classes-item {
	width: 10vw;
}

.favorite-classes-row {
	display: flex;
	flex-direction: row;
	justify-content: space-between;
	margin: 2vh 0;
}

.adminPages {
	left: 1vw;
	position: absolute;
	top: 1vw;
	width: 85vw;
}

.adminPages>h3 {
	margin-bottom: 2vh;
	margin-top: 0;
}

.adminSelect {
	background-color: #282828;
	color: var(--text-color);
	border: none;
	cursor: pointer;
	font-size: 1.5vh;
	height: 3vh;
	width: 12vw;
}

.adminShield {
	color: var(--primary-color);
}

.adminTab {
	align-items: center;
	border-bottom: solid .1vh rgba(0, 0, 0, 0.1);
	color: #B3E5FC;
	cursor: pointer;
	display: flex;
	font-size: 1.7vh;
