	opacity: 0;
	position: absolute;
	z-index: -2;
}

.styled-checkbox:checked+label:after {
	box-shadow: .3vh 0 0 white,
	.6vh 0 0 white,
	.9vh 0 0 white,
	.9vh -.2vh 0 white,
	.9vh -.4vh 0 white,
	.9vh -.6vh 0 white,
	.9vh -.8vh 0 white,
	.9vh -1vh 0 white,
	.9vh -1.2vh 0 white;
	content: '';
	height: .3vh;
	left: 0vh;
	position: absolute;
	top: .6vh;
	transform: rotate(45deg);
	transition: box-shadow .5s ease, transform .5s ease;
	width: .3vh;
}

.styled-checkbox:checked+label:before {
	background: var(--primary-color);
	box-shadow: 0 0 0 .3vh var(--dark-primary-color);
	transition: box-shadow .5s ease;
}

.styled-checkbox:disabled+label {
	color: #b8b8b8;
	cursor: auto;
}

.styled-checkbox:disabled+label:before {
	background: #ccc;
	box-shadow: none;
}

.styled-checkbox:hover+label:before {
	background: #B3E5FC;
	transition: background .5s ease;
}

.styled-checkbox+label {
	cursor: pointer;
	padding: 0;
	position: relative;
}

.styled-checkbox+label:after {
	box-shadow: 0vh 0 0 rgba(0, 0, 0, 0),
	0vh 0 0 rgba(0, 0, 0, 0),
	0vh 0 0 rgba(0, 0, 0, 0),
	0vh 0 0 rgba(0, 0, 0, 0),
	0vh 0 0 rgba(0, 0, 0, 0),
	0vh 0 0 rgba(0, 0, 0, 0);
	content: '';
	height: .3vh;
	left: 0vh;
	position: absolute;
	top: .6vh;
	transform: rotate(0deg);
	transition: box-shadow .5s ease, transform .5s ease;
	width: .3vh;
}

.styled-checkbox+label:before {
	background: white;
	box-shadow: 0 0 0 .3vh rgba(0, 0, 0, 0);
