css:
@-moz-document url-prefix("https://br.ign.com/forum/forums/mundo-aberto.24/"), url-prefix("https://br.ign.com/forum/threads/"), url-prefix("https://br.ign.com/forum/") {
a {
    color: #b44b16;
    text-decoration: none;
}

header {
    position: fixed;
    top: 0px;
    width: 100%;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    top: 0px;
    background-color: rgba(123, 123, 123, 0.27);
    z-index: 10;
}



#gheader,
section.stripe {
    background-color: rgba(0, 0, 0, 0);
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.18);
    padding: 18px 0px;
    transition: padding 0.1s linear 0s;
    overflow: hidden;
    border-top: 1px solid #d1d5db;
}

.p-nav {
    color: #cdcdcd;
    background: #ae4714;
}

.p-sectionLinks {
    font-size: 13px;
    color: #cdcdcd;


    color: #ffffff;
    background: #b44b16;

    background-color: #b44b16;
}
.button.button--cta,
a.button.button--cta {
    color: #ffffff;
    background: #b44b16;
    border-color: #b44b16 #b44b16 #b44b16 #b44b16;
}


.block-filterBar {
    padding: 6px 10px;
    font-size: 13px;
    color: #90b1d1;
    background: #4d4d4d;
    border-bottom: 1px solid #4d4d4d;
    background: linear-gradient(0deg, #7b320d, #a44414);
}

.pageNav-page {
    display: table-cell;
    /*background: linear-gradient(0deg, rgb(87, 36, 36), mix(rgb(36, 62, 87), lighten(rgb(0, 0, 0), 17%), 25%));*/
    background-color: #954015;
    color: #c0d1e0;
    border-width: 1px;
    border-style: solid;
    border-top-color: #4d4d4d;
    border-right-color: #4d4d4d;
    border-bottom-color: #4d4d4d;
    border-left-color: #4d4d4d;
    font-size: 13px;
    white-space: nowrap;
}


.pageNav-jump {
    display: inline-block;
    /*background: linear-gradient(0deg, rgb(36, 62, 87), mix(rgb(36, 62, 87), lighten(rgb(0, 0, 0), 17%), 25%));*/
    background-color: #954015;
    color: #c0d1e0;
    border-width: 1px;
    border-style: solid;
    border-top-color: #4d4d4d;
    border-right-color: #4d4d4d;
    border-bottom-color: #4d4d4d;
    border-left-color: #4d4d4d;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}


.button.button--link,
a.button.button--link {
    background: #2b2b2b;
    color: #df6122;
    border-color: #404040 #595959 #595959 #404040;
}


a:hover {
    color: #c7571f;
    text-decoration: underline;
}


.input {
    font-size: 15px;
    color: #cdcdcd;
    background: #c44e1f;
    border-width: 1px;
    border-style: solid;
    border-top-color: #c44e1f;
    border-right-color: #c44e1f;
    border-bottom-color: #c44e1f;
    border-left-color: #c44e1f;
    border-radius: 4px;
    padding: 6px;
    background: #95320a !important;
    display: block;
    width: 99%;
    line-height: 1.4;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all 0.25s ease;
    transition: all 0.25s ease;
}
.input::-webkit-input-placeholder {
    color: rgba(205, 205, 205, 0.4);
}
.input::-moz-placeholder {
    color: rgba(205, 205, 205, 0.4);
}
.input:-moz-placeholder {
    color: rgba(205, 205, 205, 0.4);
}
.input:-ms-input-placeholder {
    color: rgba(205, 205, 205, 0.4);
}
.input:focus,
.input.is-focused {
    outline: 0;
    background: #84361b;
    background: #7c341b !important;
}
.pageNav-jump {
    display: inline-block;
    /*background: linear-gradient(0deg, rgb(123, 97, 208), mix(rgb(36, 62, 87), lighten(rgb(0, 0, 0), 17%), 25%));*/
    background-color: #8a3e23;
    color: #c0d1e0;
    border-width: 1px;
    border-style: solid;
    border-top-color: #4d4d4d;
    border-right-color: #4d4d4d;
    border-bottom-color: #4d4d4d;
    border-left-color: #4d4d4d;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}
.pageNav-jump:hover,
.pageNav-jump:active {
    background: #6a3d12;
    text-decoration: none;
}

.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #cdcdcd;
    background: #2b2b2b;
    border-radius: 3px;
    border-top: 3px solid #cc5c14;
}
.pageNav-page.pageNav-page--current {
    background: #b44b16;
    color: #ffffff;
    border: 1px solid #a7a7a7;
    cursor: pointer;
}
.pageNav-page.pageNav-page--current:hover,
.pageNav-page.pageNav-page--current:active {
    background: #b44b16;
}



a {
    color: #b44b16;
    text-decoration: none;
}
a:hover {
    color: #b44b16;
    text-decoration: underline;
}

.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #b44b16;
    background: #2e2e2e;
}


a {
    color: #b44b16;
    text-decoration: none;
}
.userBanner.userBanner--staff,
.userBanner.userBanner--primary {
    color: #2b2b2b;
    background: #af4f1e;
    border-color: #e1701c;
    border-color: #999999;
}

.likeIcon:before {
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    content: "\f087";
    display: inline-block;
    width: 0.86em;
    color: #c84448;
    margin-right: .2em;
}


/***** ui/toolbar.less *****/
.fr-toolbar {
    color: #cdcdcd;
    background: #9b3a09;
    position: relative;
    z-index: 4;
    font-family: 'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #4d4d4d;
    border-top: 3px solid #333333;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}

.button:not(.button--splitTrigger):hover,
a.button:not(.button--splitTrigger):hover,
.button.button--splitTrigger > .button-text:hover,
a.button.button--splitTrigger > .button-text:hover,
.button.button--splitTrigger > .button-menu:hover,
a.button.button--splitTrigger > .button-menu:hover,
.button:not(.button--splitTrigger):focus,
a.button:not(.button--splitTrigger):focus,
.button.button--splitTrigger > .button-text:focus,
a.button.button--splitTrigger > .button-text:focus,
.button.button--splitTrigger > .button-menu:focus,
a.button.button--splitTrigger > .button-menu:focus,
.button:not(.button--splitTrigger):active,
a.button:not(.button--splitTrigger):active,
.button.button--splitTrigger > .button-text:active,
a.button.button--splitTrigger > .button-text:active,
.button.button--splitTrigger > .button-menu:active,
a.button.button--splitTrigger > .button-menu:active {
    background-color: #b44b16;
}
.button.button--primary,
a.button.button--primary {
    color: #ffffff;
    font-weight: 700;
    background: #b44b16;
    min-width: 80px;
    border-color: #b44b16 #b44b16 #b44b16 #b44b16;
}
.button.button--primary.button--splitTrigger > .button-text,
a.button.button--primary.button--splitTrigger > .button-text {
    border-right-color: #b44b16;
}
.button.button--primary.button--splitTrigger > .button-menu,
a.button.button--primary.button--splitTrigger > .button-menu {
    border-left-color: #b44b16;
}
.button.button--primary:not(.button--splitTrigger):hover,
a.button.button--primary:not(.button--splitTrigger):hover,
.button.button--primary.button--splitTrigger > .button-text:hover,
a.button.button--primary.button--splitTrigger > .button-text:hover,
.button.button--primary.button--splitTrigger > .button-menu:hover,
a.button.button--primary.button--splitTrigger > .button-menu:hover,
.button.button--primary:not(.button--splitTrigger):focus,
a.button.button--primary:not(.button--splitTrigger):focus,
.button.button--primary.button--splitTrigger > .button-text:focus,
a.button.button--primary.button--splitTrigger > .button-text:focus,
.button.button--primary.button--splitTrigger > .button-menu:focus,
a.button.button--primary.button--splitTrigger > .button-menu:focus,
.button.button--primary:not(.button--splitTrigger):active,
a.button.button--primary:not(.button--splitTrigger):active,
.button.button--primary.button--splitTrigger > .button-text:active,
a.button.button--primary.button--splitTrigger > .button-text:active,
.button.button--primary.button--splitTrigger > .button-menu:active,
a.button.button--primary.button--splitTrigger > .button-menu:active {
    background-color: #b44b16;
}
.button.button--cta,
a.button.button--cta {
    color: #ffffff;
    background: #b44b16;
    border-color: #b44b16 #b44b16 #6a92b6 #4e78a0;
}

.button,
a.button {
    display: inline-block;
    text-decoration: none;
    cursor: pointer;
    border: 1px solid transparent;
    white-space: nowrap;
    -webkit-transition: background-color 0.25s ease;
    transition: background-color 0.25s ease;
    font-size: 13px;
    border-radius: 4px;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 10px;
    text-align: center;
    color: #ffffff;
    background: #b44b16;
    border-color: #252627 #252627 #252627 #252627;
}

.block-header {
  padding: 6px 10px;
  margin: 0;
  font-weight: 400;
  text-decoration: none;
  font-size: 20px;
  color: #ffffff;
  background: #243e57;
  border-bottom: 1px solid #333333;
  background: linear-gradient(0deg, #35271b, #d35d16);
}

.block-minorHeader {
  padding: 6px 10px;
  margin: 0;
  font-weight: 400;
  text-decoration: none;
  font-size: 17px;
  color: #b94000;
}


.fr-wrapper ::selection {
  background: #ed7750;
  color: #010101;
}

.message-cell.message-cell--alert {
  font-size: 13px;
  flex: 1 1 auto;
  width: 100%;
  min-width: 0;
  color: #ffffff;
  background: #d05110;
}
.message-cell.message-cell--alert a {
  color: #d8c9c9;
}

.badge.badge--highlighted,
.badgeContainer.badgeContainer--highlighted:after {
  display: inline-block;
  color: white;
  background: #ff7c2a;
}

.menu-arrow {
  position: absolute;
  width: 0;
  height: 0;
  border: 0 solid transparent;
  top: -8px;
  left: 50%;
  margin-left: -8px;
  border: 8px solid transparent;
  border-top-width: 0;
  border-bottom-color: #b8bbb0;
}

.block-textHeader {
  margin: 3px 0;
  padding: 0;
  font-weight: 400;
  font-size: 17px;
  color: #b94700;
  background: #333333;
}

.bbCodeBlock-expandLink a {
  position: absolute;
  bottom: 6px;
  left: 0;
  right: 0;
  text-align: center;
  font-size: 13px;
  color: #de6313;
  text-decoration: none;
}


.block-formSectionHeader {
  padding: 6px 10px;
  margin: 0;
  font-weight: 400;
  text-decoration: none;
  font-size: 17px;
  color: #d47215;
  border-top: 1px solid #4d4d4d;
  border-bottom: 1px solid #4d4d4d;
}


}
