css:
@-moz-document domain("scene-rls.net") {
#contentWrapper {
    background: hsla(0, 0%, 0%, .5)!important;
}

* {
    color: hsl(0, 0%, 80%)!important;
    border-color: hsl(180, 20%, 20%)!important;
}

.contentHeader h3 {
    padding-top: 4px!important;
    border-bottom: 1px solid hsl(180, 20%, 20%)!important;
}

.HeaderSubArea span {
    color: hsl(0, 0%, 100%)!important;
}

.postFooter {
    border-top: 0!important;
    border-bottom: 1px solid hsl(180, 20%, 20%)!important;
    padding-bottom: 8px!important;
}

.post:not([id]) {
    margin-bottom: 32px!important;
}

a,
a * {
    color: hsl(180, 65%, 81%)!important;
    text-decoration: none!important;
}

a {
    transition: color .2s, text-shadow .2s;
}

a * {
    transition: color .2s;
}

a:hover,
a:hover *,
.post:not([id]) .postTitle a:visited:hover,
.postContent h2 a:visited:hover,
body.bs .p-c-title h2 a:visited:hover {
    color: hsl(180, 65%, 91%)!important;
}

a:hover {
    text-shadow: 0 0 3px hsla(180, 65%, 91%, .7)!important;
}

.post:not([id]) .postTitle a:visited,
.postContent h2 a:visited,
body.bs .p-c-title h2 a:visited {
    color: hsl(180, 20%, 45%)!important;
}

.postTitle a {
    padding-left: 0!important;
    display: inline-block!important;
}

.postTitle {
    padding-left: 10px!important;
    padding-right: 10px!important;
    box-sizing: border-box!important;
}

body {
    background-attachment: fixed!important;
}

.HeaderSubArea {
    height: 98px!important;
}

.HeaderSubArea .SearchBox {
    top: 17px!important;
}

.postTitle span,
.Footer .copyright {
    display: none!important;
}

.HeaderSubMenu {
    bottom: 0!important;
}

.postDay,
.postMonth,
.postCategories,
.postTags,
.postComments,
.commentHeader .addComment,
.rss {
    background: none!important;
}

.commentHeader .addComment {
    padding-left: 10px!important;
}

.rss {
    padding: 0!important;
}

.postComments {
    padding: 2px 0!important;
}

.postTags {
    position: relative!important;
}

.postTags > span:first-child:before {
    content: 'Tags: '!important;
}

.Footer {
    height: 77px!important;
    background-position: unset!important;
    background: linear-gradient(hsla(0, 0%, 0%, .5), hsla(0, 0%, 0%, .35), hsla(0, 0%, 0%, .2), hsla(0, 0%, 0%, .05))!important;
}

#contentWrapper input[type="text"],
#contentWrapper textarea,
.bs .s-input {
    background: hsla(200, 30%, 16%, .5)!important;
    border-color: hsl(180, 20%, 30%)!important;
    transition: background .2s, border-color .2s;
    outline: none!important;
}

#contentWrapper input[type="text"]:focus,
#contentWrapper textarea:focus,
.bs .s-input:focus {
    background: hsla(200, 30%, 22%, .5)!important;
    border-color: hsl(180, 20%, 40%)!important;
}

.HeaderSubArea h1 a:not(:hover) {
    text-shadow: -4px 0 4px hsla(0, 0%, 0%, .2), 0 4px 4px hsla(0, 0%, 0%, .2), 4px 0 4px hsla(0, 0%, 0%, .2), 0 -4px 4px hsla(0, 0%, 0%, .2)!important;
}

#contentWrapper #pageNavigation {
    border: 0!important;
    background: none!important;
    pointer-events: none!important;
    display: flex!important;
    padding: 0 16px!important;
}

#contentWrapper #pageNavigation > * {
    pointer-events: all!important;
}

.zmg_pn {
    padding-bottom: 16px!important;
    cursor: default!important;
}

.zmg_pn span,
.zmg_pn span.zmg_pn_current {
    padding: 0!important;
    background: none!important;
}

.zmg_pn span.zmg_pn_current {
    color: hsl(0, 0%, 50%)!important;
    cursor: default!important;
}

.zmg_pn a,
.zmg_pn span.zmg_pn_current {
    padding: 4px 0!important;
    display: inline-block!important;
}

.zmg_pn span:not(:last-child) {
    margin-right: 10px!important;
}

.postReadMore > b,
#contentWrapper input[type="submit"],
#newerEntries a,
#olderEntries a,
.bs a.page-button,
.bs .s-submit-outer .s-submit {
    background: hsl(200, 20%, 20%)!important;
    border: 1px solid hsl(180, 20%, 40%)!important;
    color: hsl(180, 65%, 81%)!important;
    cursor: pointer!important;
    border-radius: 2px!important;
    box-shadow: none!important;
    text-shadow: none!important;
}

#contentWrapper .postReadMore > b:hover,
#contentWrapper input[type="submit"]:hover,
#newerEntries a:hover,
#olderEntries a:hover,
.bs a.page-button:hover,
.bs .s-submit-outer .s-submit:hover {
    background: hsl(200, 20%, 26%)!important;
    border: 1px solid hsl(180, 20%, 60%)!important;
    color: hsl(180, 65%, 90%)!important;
    text-shadow: 0 0 3px hsla(180, 65%, 91%, .7)!important;
}

#contentWrapper input[type="submit"],
#newerEntries a,
#olderEntries a,
.bs a.page-button,
.bs .s-submit-outer .s-submit {
    transition: background .2s, border-color .2s, color .2s, text-shadow .2s;
}

#contentWrapper .postReadMore > b {
    transition: background .2s, border-color .2s, color .2s;
}

blockquote,
pre {
    background: hsla(200, 20%, 20%, .5)!important;
    padding-left: 12px!important;
}

#olderEntries a {
    padding: 4px 26px 4px 10px!important;
}

#olderEntries a:after {
    content: '>'!important;
    position: absolute!important;
    right: 10px;
    top: 4px;
    font-weight: bold!important;
    font-size: 13px!important;
}

#newerEntries a {
    padding: 4px 10px 4px 26px!important;
}

#newerEntries a:before {
    content: '<'!important;
    position: absolute!important;
    left: 10px;
    top: 4px;
    font-weight: bold!important;
    font-size: 13px!important;
}

#newerEntries,
#olderEntries {
    display: inline-flex!important;
    font-weight: bold!important;
    position: relative!important;
    font-size: 11px!important;
}

#newerEntries {
    margin-right: auto!important;
}

#olderEntries {
    margin-left: auto!important;
}

#contentWrapper input[type="submit"] {
    padding: 2px 6px!important;
}

#contentWrapper .postReadMore > b {
    position: relative!important;
}

#contentWrapper .postReadMore > b > b {
    margin: 0!important;
}

#contentWrapper .postReadMore > b > b:after {
    content: '>'!important;
    position: absolute!important;
    right: 10px;
    top: 2px;
}
}

@-moz-document regexp("https?://scene-rls\\.net/releases.*") {
html {
    background: #0f2233 url(https://scene-rls.net/wp-content/themes/eos/images/BodyBg.png) repeat-x 0 0!important;
    background-attachment: fixed!important;
    min-height: 100vh!important;
}

body {
    background: hsla(0, 0%, 0%, .5)!important;
    min-height: 100vh!important;
}

.header,
.core {
    width: 100%!important;
}

.bs .sidebar.block {
    background: hsla(0, 0%, 0%, .2)!important;
    border-radius: 4px!important;
}

body.bs .tl-term:hover,
body.bs .tl-adult > .tl-term:hover + .tl-children .tl-term {
    background-color: hsla(200, 20%, 20%, .5)!important;
    color: hsl(180, 65%, 81%)!important;
}

body.bs a:hover,
body.bs .button:hover,
body.bs input[type=submit]:hover,
body.bs .logo a:hover {
    color: hsl(180, 65%, 91%)!important;
}

.term-list > li {
    border-top: 0!important;
    transition: backgrounbd-color .2s;
}

.bs .block-head {
    background: none!important;
    text-shadow: none!important;
}

.bs .page-current {
    cursor: default!important;
}

.core,
.header,
.footer {
    margin: 0!important;
    padding-left: 60px!important;
    padding-right: 60px!important;
}

.pl-foot {
    padding-top: 10px!important;
    padding-bottom: 10px!important;
}

.header {
    padding-bottom: 20px!important;
}
}
