css:
@-moz-document domain("www.twitch.tv") {
.tw-root--theme-dark .directory-header-new__banner-cover {
    background: linear-gradient(0deg, #18181c, rgba(0, 0, 0, .25)), linear-gradient(90deg, #18181c, rgba(0, 0, 0, .25));
}
/*Main body color and fonts*/
 .tw-root--theme-dark body {
    background-color: #18181c!important;
    font-family: Roobert, Helvetica Neue, Helvetica, Arial, sans-serif!important
}
 .channel-info-content, .home-header-sticky, .stream-schedule__header{
	background-color: #18181c!important;
}
/*Header font size*/
 .tw-font-size-4 {
    font-size: 1.8rem!important;
}
/*Live Chat color*/
 .tw-root--theme-dark .chat-room, .chat-room {
    background-color: #18181c!important;
    font-family: Helvetica, Arial, sans-serif!important
}
/*Vod chat color & Live chat top part "STREAM CHAT" box*/
 .tw-root--theme-dark .channel-root__right-column, .tw-c-background-base {
    background-color: #1e1e23!important;
    
}
/*Vod chat padding*/
 .tw-align-items-end.tw-flex.tw-flex-wrap.tw-full-width {
	padding-left: .9rem;
}
/*Twitch name color*/
/*.tw-link:visited {
    color: #ffffff!important;
}*/

/*
2c2c31
