*/
body {
    color: #f3f3f3;
}
::selection {
    background-color: #3390ff;
    color: #fff;
}
::-moz-selection {
    background-color: #3390ff;
    color: #fff;
}
::-o-selection {
    background-color: #3390ff;
    color: #fff;
}
::-ms-selection {
    background-color: #3390ff;
    color: #fff;
}
::-webkit-selection {
    background-color: #3390ff;
    color: #fff;
}

/*Left directory panel color*/
 .tw-root--theme-dark .side-nav__overlay-wrapper, .side-nav__overlay-wrapper {
    background-color: #212126!important;
}
/*Search to add friends*/
 .tw-c-background-alt {
    background-color: #212126!important;
}
/*Header color*/
 .top-nav__menu {
	background-color: #2f2d3e!important;
}
/*Channel Header color*/
 .tw-root--theme-dark .channel-header, .channel-header {
    background-color: #18181c!important;
}
/*Subscribe & Follow & Chat button*/
 .tw-core-button--primary {
    background-color: #7232d3!important;
    color: #fff!important;
}
}
