css:
@-moz-document domain(simyard.com)
{
body, th.title  {
    background: #626262 !important;
}
img[src$="images/logo.gif"] {
    background: transparent url(http://gocosmonaut.com/classic/sy20.png) center top no-repeat !important;
    height: 85px !important;
    margin-top: 0 !important;
    padding-left: 115px !important;
    width: 0 !important;
}
.r1, .r1c, .r1r, .middle_r1r, .middle_r1, .middle_r1c, .notice {background: #666666 !important;}
.r2, .r2c, .r2r, middle_r2r, .middle_r2, .middle_r2c {background: #767676 !important;}

.notice {border:1px solid #999 !important; color: #fff !important;}

body table:first-child tr[valign="middle"] td:first-child a:before {color: white;}
body table:first-child tr[valign="middle"] td:first-child a:hover::before {color: gold; display: block; background: #222;}

/* Top navigation */
 a[title="membership"] img, a[title="teams"] img, a[title="standings"] img, a[title="leagues"] img, a[title="schedules"] img, a[title="players"] img, a[title="stadiums"] img, a[title="training"] img, a[title="forums"] img, a[title="messages"] img, a[title="account"] img, a[title="help"] img, a[title="giftcertificates"] img, a[title="merchandise"] img, a[title="publicfields"] img, a[title="signup"] img {
    display: none;
}
a[title="membership"], a[title="teams"], a[title="standings"], a[title="leagues"], a[title="schedules"], a[title="players"], a[title="stadiums"], a[title="training"], a[title="forums"], a[title="messages"], a[title="account"], a[title="help"], a[title="giftcertificates"], a[title="merchandise"], a[title="publicfields"], a[title="signup"] {
    text-decoration: none;
}
a[title="membership"]::before, a[title="teams"]::before, a[title="standings"]::before, a[title="leagues"]::before, a[title="schedules"]::before, a[title="players"]::before, a[title="stadiums"]::before, a[title="training"]::before, a[title="forums"]::before, a[title="messages"]::before, a[title="account"]::before, a[title="help"]::before, a[title="giftcertificates"]::before, a[title="merchandise"]::before, a[title="publicfields"]::before, a[title="signup"]::before {
    font-size: 14px !important;
    letter-spacing: .5px;
    display: flex;
    width: auto;
    border: 1px solid gray;
    padding: 6px;
    margin: 3px 10px;
    color: #ccc;
}
a[title="membership"]::before {
    content:"Membership";
}
a[title="teams"]::before {
    content:"Teams";
}
a[title="standings"]::before {
    content:"Park Standings";
}
a[title="leagues"]::before {
    content:"Leagues";
}
a[title="schedules"]::before {
    content:"Schedules";
}
a[title="players"]::before {
    content:"Players";
}
a[title="stadiums"]::before {
    content:"Stadiums";
}
a[title="training"]::before {
    content:"Training";
}
a[title="forums"]::before {
    content:"Forums";
}
a[title="messages"]::before {
    content:"Mail";
}
a[title="account"]::before {
    content:"Account";
}
a[title="help"]::before {
    content:"Halp!";
}
a[title="giftcertificates"]::before {
    content:"Gift Certificates";
}
a[title="merchandise"]::before {
    content:"Stuff to Buy";
}
a[title="publicfields"]::before {
    content:"Play Park Games";
}

a[title="signup"]::before {
    content:"Sign Up";
}

  td[style*="images/parkfields.gif"]
  {
    background: url(http://www.gocosmonaut.com/sy/trondiamond.png) center top no-repeat !important;
    height: 167px !important;
    width: 0 !important;
  }
}
