css:
@-moz-document url-prefix("https://my.overhosting.ru") {
 
 ::-webkit-scrollbar{width:3px;height:3px}
::-webkit-scrollbar-track{ background-color:#202528}
::-webkit-scrollbar-track-piece{background-color:#0B0D0E}
::-webkit-scrollbar-thumb{height:50px;background-color:#87c403;border-radius:100px}
::-webkit-scrollbar-corner{background-color:#202528}
::-webkit-resizer{background-color:#202528} 
  
@font-face {
  font-family: 'PT Sans';
  font-style: normal;
  font-weight: 400;
  src: local('PT Sans'), local('PTSans-Regular'), url(https://fonts.gstatic.com/s/ptsans/v11/jizaRExUiTo99u79D0aExdGM.woff2) format('woff2');
  unicode-range: U+0400-045F, U+0490-0491, U+04B0-04B1, U+2116;
}
body {
 background: #fff url(https://gamemania.myarena.site/images/themeoverhosting/107.png) !important; 
    font-family: 'Segoe UI', sans-serif !important;
    font-size: 14px !important;
background-attachment: fixed !important;
}
#header {
 background: #fff  !important; 
    border-bottom: 1px #87C403 solid;
    box-shadow: 0px 1px 15px 1px rgba(69, 65, 78, 0.08) !important;
}
div.row {
 margin-top: 20px !important;  
}
#header a {font-weight: bold; font-size: 13px !important; color: #000 !important}
#header a:hover {color:#87c403 !important}
h1 {font-family: 'PT Sans', sans-serif !important; font-size: 17px !important; font-weight: bold; margin-top: 15px;}
h3 {font-family: 'PT Sans', sans-serif !important; font-size: 17px !important; font-weight: bold; margin-bottom: 15px; margin-top: 15px;}
h2 {font-family: 'PT Sans', sans-serif !important; font-size: 17px !important; font-weight: bold; margin-bottom: 15px; margin-top: 15px;}
.btn {
height: 25px !important;   
font-size: 12px !important;
padding-top: -25px !important;
padding-bottom: 25px !important; 
background: #343A40 !important; 
color: #fff !important; 
}
.btn:focus, .btn-outline-dark.focus {
  box-shadow: 0 0 0 0.0rem rgba(52, 58, 64, 0.5) !important;
    height: 25px !important; 
}
.card {
  position: relative !important;
  box-shadow: 0px 1px 15px 1px rgba(69, 65, 78, 0.08) !important;
  min-width: 0 !important;
  background-color: #fff !important;
  background-clip: border-box !important;
  border: 0px solid rgba(0, 0, 0, 0.125) !important;
  border-radius: 2px !important; }
.card-header {
  background-color: rgba(0, 0, 0, 0.03) !important;
  border-bottom: 1px solid #F1F7F8 !important;
  font-weight: bold;  
}
.list-group-item {
  border: 1px solid #e0e4e5 !important;
  box-shadow: 0px 1px 15px 1px rgba(69, 65, 78, 0.08) !important;
}
.list-group-item.active {
  background-color: #87c403 !important;
  border-color: #3498DB;
}
th {
   font-weight:normal   !important;
}
tr {
    cursor:pointer !important;
}
.btn-toolbar button {
 margin: 1px !important;
}
.btn-toolbar {
 margin-bottom: 15px !important;
}
.overlogo {
	display: block !important;
	margin-top: -3px !important; 
	width: 180px !important;
	height: 60px !important;
	background-image: url("https://gamemania.myarena.site/images/themeoverhosting/logo_overhosting.png") !important;
	background-size: cover !important;
}
.overlogo:hover {
    opacity: 0.5 !important;
        -webkit-transition: all 0.4s ease;
     -moz-transition: all 0.4s ease;
       -o-transition: all 0.4s ease;
      -ms-transition: all 0.4s ease;
          transition: all 0.4s ease;
}
.table-striped tbody tr:nth-of-type(odd) {
  background-color: rgba(0, 0, 0, 0.0) !important;
}
.table-striped tbody tr:hover {
  background-color: rgba(0, 0, 0, 0.0) !important;
}
}


