css:
@-moz-document domain("twitch.tv") {
/**                                                     **/
/** Twitch Dark Style v0.9 by Strong_WTF **/
/**                                                     **/

:root {
    --purple: #b391e6 !important;
    --darkpurple: #4b367b !important;
    --purple1: #6440a6 !important;
    --purple2: #8d65c7 !important;
    --header: #19171c !important;
    --greybg: #19171c !important;
    --midgrey: #0f0f11 !important;
}

.tw-root--theme-dark {
    --color-background-base: #1d1b21;
    --color-background-alt: #212126;
    --color-fill-brand: #62458c;
    --color-background-body: #0f0f11;
}

body {
    font-family: helvetica, sans-serif !important;
}

.tw-root--theme-dark body {
    background-color: var(--midgrey) !important;
}

.tw-root--theme-dark .chat-room {
    background: var(--midgrey) !important;
}

.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: var(--header) !important;
}

.tw-root--theme-dark .directory-header-new__banner-cover {
    background: linear-gradient(0deg,#0f0f11,rgba(0,0,0,.25)),linear-gradient(90deg,#444,rgba(0,0,0,.25)) !important;
}

.tw-root--theme-dark .side-nav {
    border-right: 1px solid #242426 !important;
    background: var(--greybg) !important;
}

.tw-root--theme-dark .channel-header {
    background-color: var(--header) !important;
}

.tw-root--theme-dark .chat-viewers__pane {
    background: var(--midgrey) !important;
}

.tw-root--theme-dark .persistent-notification__unread {
    background-color: var(--midgrey);
}

.rooms-header {
    background: var(--midgrey) !important;
}

.channel-header-verified-badge, .tw-root--theme-dark .channel-header-verified-badge, .tw-root--theme-light .channel-header-verified-badge {
    color: var(--purple2);
}

.tw-root--theme-dark .carousel-player-nav-arrow__container {
    background-color: #222;
}

.channel-root__player-container {
    background-color: var(--greybg) !important;
}

.channel-panels-container {
    margin-top: 4rem !important;
}

.tw-pd-b-2 {
    padding-bottom: 0.3rem !important;
}

.channel-panels {
    margin-top: 2rem;
}

.follow-btn--following, .tw-root--theme-dark .follow-btn--following, .tw-root--theme-light .follow-btn--following {
    background: #424246;
    color: #fff;
}

.tw-font-size-2 {
    font-size: 3.2rem !important;
}

.tw-font-size-4 {
    font-size: 1.6rem !important;
    font-weight: 100;
}

.tw-semibold, .tw-strong {
    font-weight: 100 !important;
}

.tw-bold {
    font-weight: 100 !important;
}

h1 {
    font-size: 3.2rem;
}

h2 {
    font-size: 2.6rem;
}

a {
    color: var(--purple);
}

.top-nav__menu {
    background-color: var(--darkpurple) !important;
}

.tw-ellipsis {
    color: #fff;
}

.tw-core-button--primary {
    background-color: var(--purple1);
}

.tw-core-button--primary:focus, .tw-root--hover .tw-core-button--primary:hover:not(.tw-core-button--disabled) {
    background-color: var(--purple2);
}

.tw-core-button--primary:active:not(.tw-core-button--disabled), .tw-root--hover .tw-core-button--primary:hover:active:not(.tw-core-button--disabled) {
    background-color: var(--purple2);
}

.tw-button {
    background-color: var(--purple1);
}

.tw-button:focus, .tw-root--hover .tw-button:hover:not(.tw-button--disabled) {
    background-color: var(--purple2);
    color: #1c1c1c;
}

.tw-button:active:not(.tw-button--disabled), .tw-button:hover:active:not(.tw-button--disabled) {
    background-color: var(--purple2);
}

.channel-info-bar {
    background-color: var(--greybg) !important;
}

.tw-typeset blockquote {
    border-left: 3px solid var(--purple1);
}

.tw-input:focus {
    background-color: #222;
    border-color: var(--purple1);
}

.side-nav-header {
    border-bottom: 1px solid #242426;
    padding-bottom: 10px;
}

.tw-border-l {
    border-left: 1px solid #242426 !important;
}

.tw-border-b {
    border-bottom: 1px solid #242426 !important;
}

.tw-border-r {
    border-right: 1px solid #242426 !important;
}

.tw-border-t {
    border-top: 1px solid #242426 !important;
}

.current-subscription__heading-tier, .tw-root--theme-dark .current-subscription__heading-tier, .tw-root--theme-light .current-subscription__heading-tier {
    color: var(--purple2);
}

.tw-hover-accent-effect {
    background: var(--purple2);
}

.tw-select-button:focus {
    background-color: #222;
    border-color: var(--purple1);
}

.tw-interactable--inverted.tw-interactable--hover-forced, .tw-interactable--inverted:focus, .tw-root--hover .tw-interactable--inverted.tw-interactable--hover-enabled:hover {
    background-color: var(--purple1);
}

.tw-pill--brand {
    background-color: var(--purple1);
}

.tw-root--theme-dark .tw-tab-wrapper {
    box-shadow: inset 0 -1px 0 var(--purple1);
}

.tw-tab:not(.tw-tab--disabled) .tw-tab__link.tw-tab__link--active {
    box-shadow: inset 0 -1px 0 var(--purple2);
}

.tw-root--hover .tw-tab:not(.tw-tab--disabled) .tw-tab__link:hover {
    box-shadow: inset 0 -1px 0 var(--purple2);
}

.tw-number-badge {
    background-color: initial !important;
}

.tw-link {
    color: var(--purple2);
}

.tw-link--inherit {
    color: inherit;
}

.tw-root--hover .tw-link:hover {
    color: var(--purple1);
}

.video-chat__message-author--me {
    background-color: var(--purple1);
}

.tw-root--theme-dark .thread-header__title-bar-container {
    background: var(--greybg);
    border: 1px solid #242426;
    box-shadow: inset 0 0px #242426;
}

.tw-svg__asset--brand {
    fill: var(--purple2);
}

.tw-elevation-1 {
    box-shadow: 0px 0px 0px rgba(0,0,0,.65),0px 0px 8px rgba(0,0,0,.4)!important;
}

.tw-image-selector__input:checked+.tw-image-selector__label {
    border: 1px solid var(--purple1);
    box-shadow: inset 0 0 0 0.1rem var(--purple1);
}

.tw-button--success {
    color: #1c1c1c;
}

/* bits */
.t-bits-card__footer, .tw-root--theme-dark .t-bits-card__footer, .tw-root--theme-light .t-bits-card__footer {
    background-color: var(--darkpurple);
}

.bits-leaderboard-expanded__section-break {
    border-bottom: 1px solid var(--purple1);
}

/* avatar */
.tw-avatar {
    background-color: initial !important;
}

.tw-avatar__presence {
    background-color: initial;
}

/* host */
.tw-c-background-accent {
    background-color: #4e357b !important;
}

.tw-c-background-accent-alt-2 {
    background-color: #212126 !important;
}

/* multistream */
.tw-core-button--primary.tw-core-button--overlay {
    background-color: #202026;
    color: #ccc;
}

.tw-core-button--primary.tw-core-button--overlay:focus, .tw-root--hover .tw-core-button--primary.tw-core-button--overlay:hover:not(.tw-core-button--disabled) {
    background-color: #313131;
    color: #ccc;
}

.multi-stream-player-layout__player--outlined:not(.multi-stream-player-layout__player--ad-playing)::after, .multi-stream-player-layout__player:not(.multi-stream-player-layout__player--ad-playing):hover::after {
    border-color: var(--purple1);
}

/* chat */
.tw-textarea {
    background-color: var(--midgrey);
}

.tw-textarea:focus {
    background-color: #222;
    border-color: var(--purple1);
}

.chat-input__textarea textarea {
    min-height: 50px;
}

/* dashboard */
.tw-root--theme-dark .drag-and-drop-theming .mosaic-root {
    background: #0a0a0a;
}

.tw-root--theme-dark .drag-and-drop-theming .mosaic-split:active {
    background-color: var(--purple1);
}

.tw-root--theme-dark .drag-and-drop-theming .mosaic-split:hover {
    background-color: var(--purple1);
}

.quick-action__not-edit-mode:not([disabled]):focus, .quick-action__not-edit-mode:not([disabled]):hover, .tw-root--theme-dark .quick-action__not-edit-mode:not([disabled]):focus, .tw-root--theme-dark .quick-action__not-edit-mode:not([disabled]):hover, .tw-root--theme-light .quick-action__not-edit-mode:not([disabled]):focus, .tw-root--theme-light .quick-action__not-edit-mode:not([disabled]):hover {
    background: var(--purple2);
}


.video-card-thumbnail__video-state-overlay {
    background-color: rgba(103, 71, 164, 0.65);
}

.dashboard-side-nav__link:active, .tw-root--theme-dark .dashboard-side-nav__link:active, .tw-root--theme-light .dashboard-side-nav__link:active {
    background-color: var(--purple2);
}

.dashboard-side-nav__link:hover, .tw-root--theme-dark .dashboard-side-nav__link:hover, .tw-root--theme-light .dashboard-side-nav__link:hover {
    background-color: var(--purple1);
}

.ach-q-card {
    box-shadow: #6747a4 0px 0px 0px 1px;
}

.tw-pill--success {
    background-color: var(--purple1);
    color: #fff;
}

.tw-segmented-button-option__input--checkbox:checked + .tw-segmented-button-option__label, .tw-segmented-button-option__input--radio:checked + .tw-segmented-button-option__label {
    background-color: var(--purple1);
}

.tw-segmented-button-option {
    border-color: var(--purple1);
}

.tw-radio__input:checked+.tw-radio__label:before {
    border-color: var(--purple2);
}

.tw-radio__input:checked+.tw-radio__label:after {
    background-color: var(--purple2);
}

.tw-toggle__input:checked+.tw-toggle__button {
    background-color: var(--purple2);
}

.tw-badge--dashboard {
    background-color: var(--purple1);
}

.timeseries-panel__btn {
    background-color: #572fa4;
}

.timeseries-panel__btn:hover {
    background-color: var(--purple2);
}

.tw-root--theme-dark .ach-card--expanded .ach-card__inner, .tw-root--theme-light .ach-card--expanded .ach-card__inner {
    border-color: var(--purple1);
}

.tw-interactable--inverted.tw-interactable--selected.tw-interactable--hover-forced, .tw-interactable--inverted.tw-interactable--selected:focus, .tw-root--hover .tw-interactable--inverted.tw-interactable--selected.tw-interactable--hover-enabled:hover {
    background-color: var(--purple1);
}

.autohost-list-item--hovered, .tw-root--theme-dark .autohost-list-item--hovered, .tw-root--theme-light .autohost-list-item--hovered {
    background-color: var(--purple1);
}

/* pika-stata-dashboard */
.pika-single {
    font-family: helvetica, sans-serif;
}

.is-endrange .pika-button, .is-startrange .pika-button, .is-startrange .pika-button:active, .is-startrange .pika-button:focus, .is-startrange .pika-button:hover {
    background: var(--purple1);
}

.is-selected .pika-button:hover {
    background: var(--purple2);
}

.tw-root--theme-dark .top-stats-tab--active {
    box-shadow: inset 0 -3px 0 var(--purple1);
}

/* subscriptions */
.tier-display {
    color: var(--purple2);
}

.sub-badge-progress__progress-bar .tw-progress-bar div, .tw-root--theme-dark .sub-badge-progress__progress-bar .tw-progress-bar div, .tw-root--theme-light .sub-badge-progress__progress-bar .tw-progress-bar div {
    background: var(--purple2);
}

/* twitch player */
.tw-c-background-overlay {
    background-color: rgba(25, 25, 25, 0.6) !important;
}

.offline-recommendations-video-card .offline-recommendations-video-card-border-3, .tw-root--theme-dark .offline-recommendations-video-card .offline-recommendations-video-card-border-3, .tw-root--theme-light .offline-recommendations-video-card .offline-recommendations-video-card-border-3 {
    border: 1px solid var(--purple1);
}

/* VOD */
.qa-vod-chat {
    background-color: var(--midgrey);
}

/* clips main */
.tw-root--theme-dark .clips-top-nav-user {
    background: #18181b;
}

/* team (group) */
.tw-card-img--size-4 {
    margin-right: 8px;
}

/* drops */
.drops-campaign-details__drops-success, .tw-root--theme-dark .drops-campaign-details__drops-success, .tw-root--theme-light .drops-campaign-details__drops-success {
    color: var(--purple2);
}

/* new update channels */
.channel-root--home .channel-info-content {
    width: calc(100%);
    margin-top: 20px;
}

.channel-root--home .channel-root__info {
    margin-bottom: 0rem;
}

.home-header-sticky, .home-header-sticky__inner, .home-header-sticky__wrapper {
    background: var(--header);
}

.home__lower-content {
    padding-top: 2rem;
}

.tw-mg-t-1 {
    background: rgba(255, 255, 255, 0) !important;
}

.tw-mg-t-05 {
    margin-top: 0px !important;
}

.tw-align-items-baseline {
    align-items: normal !important;
    margin: -3px 0px 3px 0px;
}

.user-avatar-animated {
    margin-top: -1rem;
}

.channel-header__right {
    margin-top: 3px;
}

.top-bar {
    background: linear-gradient(180deg,rgba(0,0,0,.8) 0,rgba(0,0,0,.35) 60%,transparent)!important;
}

.tw-border-t {
    background: var(--header);
}

.tw-c-text-alt-2 {
    background: rgba(255, 255, 255, 0) !important;
}

.tw-mg-b-0 {
    background: rgba(255, 255, 255, 0)!important;
}

.tw-mg-x-1 {
    background: rgba(255, 255, 255, 0)!important;
}

.tw-pd-t-2 {
    background: rgba(255, 255, 255, 0)!important;
}

.tw-tag {
    margin-top: 4px;
}

.viewer-card-drag-cancel {
    margin-bottom: 2px;
}

.tw-root--theme-dark .first-paid-sub-offer__cta--description {
    background-color: var(--color-background-base);
}

.tw-root--theme-dark .first-paid-sub-offer__upsell {
    background-color: var(--color-background-base);
}

/* ffz */
.ffz__tooltip {
    background: var(--greybg);
    color: #ccc;
}

.ffz__tooltip .ffz__tooltip--arrow {
    background: var(--greybg);
}

/* selection */
::selection {
    background-color: #be93ff !important;
}

.tw-tooltip {
    color: #ccc !important;
    background-color: #242426;
}

.tw-tooltip:after {
    background-color: #242426;
}

/* Copyright */
li.Footer-item.Footer-copyright.copyright:after {
  content: " | Dark Style by @Strong_WTF"
}
}
