css:
@-moz-document domain("twitch.tv") {
/**                                                           **/
/**           Twitch Dark Style v1.62 by Strong_WTF           **/
/**                                                           **/

:root {
    --purple: #b391e6 !important;
    --darkpurple: #4b367b !important;
    --purple1: #6d49a2 !important;
    --purple2: #8d65c7 !important;
    --header: #19171c !important;
    --greybg: #19171c !important;
    --midgrey: #0f0f11 !important;
}

.tw-root--theme-dark {
    --color-background-base: #1d1b21;
    --color-background-alt: #212126;
    --color-fill-brand: #62458c;
    --color-text-link: #8d65c7;
    --color-text-link-hover: #9c71dc;
    --color-background-body: #0f0f11;
    --color-background-button-overlay-primary-default: #292929;
    --color-text-button-overlay-primary: #ccc;
    --color-background-button-overlay-primary-active: #292929;
    --color-background-button-overlay-primary-hover: #333;
    --color-background-accent-alt: #6d49a2;
    --color-background-accent-alt-2: #6d49a2;
    --color-background-accent: #6d49a2;
    --color-background-input-focus: #171717;
    --color-border-brand: #6d49a2;
    --color-accent: #6d49a2;
    --shadow-elevation-1: 0px 0px 0px rgba(0,0,0,.65),0px 0px 8px rgba(0,0,0,.4);
    
    --font-size-1: 3.2rem;
    --font-size-2: 2.6rem;
    --font-size-4: 1.6rem;
    --font-weight-semibold: 100;
}

body {
    font-family: helvetica, sans-serif!important;
    --color-text-tooltip-alt: hsla(0, 0%, 55%, 0.8);
    --color-text-tooltip-alt-2: hsla(0, 0%, 55%, 0.8);
}

/* fix avatar */
.gaFNUc { /*tw-c-background-base*/
    background-color: unset!important;
}

/* button */
.wgheP { /*button*/
    background-color: var(--purple1);
}

.tw-root--hover .wgheP:hover {
    background-color: var(--purple2);
}

.tw-button { /* chat emote sub */
    background-color: var(--purple1);
}

.tw-button[data-focus-visible-added], .tw-root--hover .tw-button:hover:not(.tw-button--disabled) {
    background-color: var(--purple2);
}
    
.tw-root--theme-dark .navigation-link__active-indicator, .dark-theme .navigation-link__active-indicator {
    background-color: var(--ffz-color-accent-10)!important;
}

/* profile+settings+... Toggle */
.ScToggleInput-sc-iguyno-1:checked + .qZlTz.qZlTz {
    border-color: var(--purple2);
    background-color: #171717;
}

.ScToggleInput-sc-iguyno-1:checked + .qZlTz.qZlTz::after {
    background-color: var(--purple2);
}

.presence-text {
    width: 173px;
}

.gCiPLx {
    padding-right: 0.9rem !important;
}

/* text */
a {
    color: var(--purple);
}

.tw-root--theme-dark .search-item__history {
    color: var(--purple2)!important;
}

/* main */
.tw-root--theme-dark body {
    background-color: var(--midgrey)!important;
}

.top-nav__menu { /*top-nav*/
    background-color: var(--darkpurple)!important;
}

.tw-root--theme-dark .chat-room {
    background: var(--midgrey)!important;
}

.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: var(--header)!important;
}

.tw-root--theme-dark .side-nav {
    border-right: 1px solid #242426!important;
}

.tw-elevation-1 {
    box-shadow: 0px 0px 0px rgba(0,0,0,.65),0px 0px 8px rgba(0,0,0,.4)!important;
}

/* title */
.iofXOu { /*tw-border-t (title)*/
    background: var(--header);
}

.tw-mg-r-1 {
    margin-right: .5rem!important;
}

.tw-word-break-word {
    margin-bottom: 1px;
}

.metadata-layout__support {
    margin: -2px 0px 9px 0px;
}

.cqcPrW { /*right button*/
    margin-bottom: -12px;
}

.elZcx { /*avatar*/
    margin-bottom: 1.5rem !important;
}

.igzOaC { /*name channel*/
    font-size: 1.9rem !important;
}

.kFQuPx { /*ffz*/
    margin-top: 14px;
}

/* channel review */
.eEFkwG {
    width: auto !important;
}

/* border */
.side-nav-header {
    border-bottom: 1px solid #242426;
    padding-bottom: 10px;
}

.tw-border-l {
    border-left: 1px solid #242426!important;
}

.tw-border-b {
    border-bottom: 1px solid #242426!important;
}

.tw-border-r {
    border-right: 1px solid #242426!important;
}

.tw-border-t {
    border-top: 1px solid #242426!important;
}

.tw-border {
    border: 1px solid #242426!important;
}

/* channel */
.channel-root--home .channel-info-content {
    width: calc(100%);
    margin-top: 20px;
}

.home-header-sticky {
    background: var(--header);
    width: calc(100%);
    padding: 0 40px 0 40px;
}

.tw-align-items-center.tw-c-background-base.tw-c-text-alt.tw-elevation-1.tw-flex.tw-full-height.tw-full-width.tw-overflow-hidden.tw-relative.tw-z-default {
    background-color: var(--header)!important;
}

.home-offline-hero__upsell {
    background-color: var(--color-background-base)!important;
}

/* twitch tv moderator */
.mod-view-landing-page__followed-channels-list {
    height: auto;
    width: 50rem;
}

.cXnKEP {
    margin-bottom: 7rem;
}

.bvonyM {
    padding-top: 0rem !important;
    margin-bottom: 0rem;
}

/* directory */
.tw-root--theme-dark .directory-header-new__banner-cover {
    background: linear-gradient(0deg,#0f0f11,rgba(0,0,0,.25)),linear-gradient(90deg,#444,rgba(0,0,0,.25))!important;
    width: 104%;
    margin-left: -30px;
}

.directory-header-new__info {
    margin-left: 30px;
}

.tw-tag__content {
    padding: 0 .6rem;
}

.beXCOC { /* tags */
    display: contents !important;
}

/* sub stream */
.tw-root--theme-dark .subscriber-stream-tag {
    background: var(--purple1);
}

.tw-root--theme-dark .subscriber-stream-tag:focus, .tw-root--theme-dark .subscriber-stream-tag:hover {
    background: var(--purple2);
}

/* bits */
.t-bits-card__footer, .tw-root--theme-dark .t-bits-card__footer, .tw-root--theme-light .t-bits-card__footer {
    background-color: var(--darkpurple);
}

/* host */
.metadata-layout__secondary-button-spacing { /* host link button */
    margin: 8px 0 -4px 0;
}

/* multistream */
.squad-stream-top-bar__container {
    background-color: var(--color-background-base)!important;
}

.multi-stream-player-layout__player--outlined:not(.multi-stream-player-layout__player--ad-playing)::after, .multi-stream-player-layout__player:not(.multi-stream-player-layout__player--ad-playing):hover::after {
    border: 3px solid var(--purple1);
}

.squad-stream-channel-switcher__btn--active, .squad-stream-channel-switcher__btn--active:hover {
    box-shadow: 0 0 0 2px var(--purple1);
}

/* chat */
.chat-input__textarea textarea {
    padding-top: 1.3rem;
}

.chat-wysiwyg-input__editor, .chat-wysiwyg-input__editor:hover {
    background-color: var(--midgrey);
}

.chat-wysiwyg-input__editor:focus-within {
    background-color: #222;
    border-color: var(--purple1);
}

/* credit card form */
.tw-root--theme-dark .recurly-credit-card-form .recurly-hosted-field {
    background-color: hsl(264deg 10% 10%);
}

.tw-root--theme-dark .recurly-credit-card-form .recurly-hosted-field:hover {
    background-color: var(--color-background-input);
}

.tw-root--theme-dark .recurly-credit-card-form .recurly-hosted-field-focus {
    background-color: #222;
    border-color: var(--purple1);
}

.checkout-form__animation-container {
    top: -40rem;
    border-top-left-radius: 0.6rem !important;
    border-top-right-radius: 0.6rem !important;
}

.hfTZLu { /* user card */
    margin-bottom: -2rem;
}

.ffz-input:focus, .ffz-input:focus:hover, .ffz-select:focus, .ffz-select:focus:hover, .ffz-textarea:focus, .ffz-textarea:focus:hover {
    background-color: #222;
    border-color: var(--purple1);
}

.tw-pd-x-05 {
    padding: 3px 4px 4px 4px;
}

/* social links */
.fGOHeL { /* block right */
    margin-right: -30px;
}

.nzqFb {
    white-space: nowrap;
}

/* whispers */
.tw-root--theme-dark .thread-header__title-bar-container--highlighted, .tw-root--theme-dark .thread-header__title-bar-container--highlighted, html .thread-header__title-bar-container--highlighted, html .thread-header__title-bar-container--highlighted {
    background-color: var(--purple1)!important;
}

.tw-root--theme-dark .thread-header__title-bar-container--highlighted:hover, .tw-root--theme-dark .thread-header__title-bar-container--highlighted:hover, html .thread-header__title-bar-container--highlighted:hover, html .thread-header__title-bar-container--highlighted:hover {
    background-color: var(--purple2)!important;
}

.jpMKvL:focus, .jpMKvL:focus:hover { /*chat*/
    border-color: var(--purple1);
    background-color: #171717;
}

/* search */
.cZzxIj {
    background-color: hsl(259deg 23% 48%);
}

.gXVFsI:focus, .gXVFsI:focus:hover {
    background-color: #222;
    border-color: var(--purple1);
}

/* wallet */
.kOcrrb {
    display: grid !important;
}

.DoQsR {
    flex-direction: inherit !important;
    margin-left: 2rem;
}



/* dashboard main */
.esRSpc { /* left panel button */
    background-color: var(--purple1);
}

.tw-root--hover .esRSpc:hover {
    background-color: var(--purple2);
}
.zuEaA { /* poll */
    padding-right: 0em !important;
}
/*quick action*/
.quick-action-layout--purple {
    background: var(--purple1);
}

.tw-root--theme-dark .quick-action__not-edit-mode:not([disabled]):focus, .tw-root--theme-dark .quick-action__not-edit-mode:not([disabled]):hover, html .quick-action__not-edit-mode:not([disabled]):focus, html .quick-action__not-edit-mode:not([disabled]):hover {
    background-color: var(--purple2)!important;
}

/* dashboard analytics categories */
.hHXnJQ {
    height: auto !important;
}

/* dashboard achievements */
.tw-root--theme-dark .ach-q-card, html .ach-q-card {
    box-shadow: #6747a4 0px 0px 0px 1px !important;
}

/* dashboard clips */
.clmgr-table-wrap .clmgr-table-inner {
    height: calc(100% - 20rem);
}

.tw-root--theme-dark .clmgr-table__row-expanded {
    border: 1px solid #8d65c7;
}

.clmgr-filters-container {
    height: 100%;
}

/* dashboard moderation */
.chat-term {
    max-width: 40rem;
}

/* dashboard charity */
.fetUvv {
    position: inherit !important;
}



/* twitch player */
.tw-c-background-overlay {
    background-color: rgba(25, 25, 25, 0.6)!important;
}

.offline-recommendations-video-card .offline-recommendations-video-card-border-3, .tw-root--theme-dark .offline-recommendations-video-card .offline-recommendations-video-card-border-3, .tw-root--theme-light .offline-recommendations-video-card .offline-recommendations-video-card-border-3 {
    border: 1px solid var(--purple1);
}

.offline-recommendations-video-card:focus .offline-recommendations-video-card-border-2, .offline-recommendations-video-card:hover .offline-recommendations-video-card-border-2, .tw-root--theme-dark .offline-recommendations-video-card:focus .offline-recommendations-video-card-border-2, .tw-root--theme-dark .offline-recommendations-video-card:hover .offline-recommendations-video-card-border-2, .tw-root--theme-light .offline-recommendations-video-card:focus .offline-recommendations-video-card-border-2, .tw-root--theme-light .offline-recommendations-video-card:hover .offline-recommendations-video-card-border-2 {
    border: 2px solid var(--purple2);
}

/* VOD */
.qa-vod-chat {
    background-color: var(--midgrey);
}

/* clips main */
.social-button__icon--embed, .tw-root--theme-dark .social-button__icon--embed, .tw-root--theme-light .social-button__icon--embed {
    background-color: var(--purple1);
}
    
.social-button__icon--copy, .social-button__icon--download, .tw-root--theme-dark .social-button__icon--copy, .tw-root--theme-dark .social-button__icon--download, .tw-root--theme-light .social-button__icon--copy, .tw-root--theme-light .social-button__icon--download {
    background-color: var(--purple1);
}

.fqTAbB {
    background-color: var(--purple1);
}

.fqTAbB:hover {
    background-color: var(--purple2);
}

/* team (group) */
.member-list__scrollable-container {
    /*height: 62vh;*/
    height: 52vh;
    overflow: auto;
}

.bbvncg {
    background-color: var(--purple1);
}
.bbvncg:hover {
    background-color: var(--purple2);
}




/* ffz */
.ffz__tooltip {
    background: #242426;
    color: #ccc;
}

.ffz__tooltip .ffz__tooltip--arrow {
    background: #242426;
}

.ffz__tooltip .ffz__tooltip--arrow:before {
    background: #242426;
}

.ffz-tooltip.ffz-tooltip--no-mouse>* {
    padding: 4px 0px 3.1px 0px;
}

.cQQfqe { /*ffz--meta-tray*/
    width: 480px;
}
    
.fCMDPR { /*online*/
    margin-right: .6rem !important;
}

.ffz--reply-mention:hover, .ffz--reply-mention:focus {
    background-color: var(--purple1);
}

.ffz-stat--fix-padding {
    margin-top: -.5rem!important;
    margin-bottom: -.7rem!important;
}

/* bttv */
.bttv-moderator-card-user-stats {
    background-color: rgba(0,0,0,0.8);
    padding-bottom: 5px;
    padding-top: 5px;
}

/* selection */
::selection {
    background-color: #be93ff!important;
}

/* tooltip */
.tw-tooltip, .tw-tooltip-popper-arrow:after, .tw-tooltip:after {
    background-color: #242426;
    color: #ccc!important;
}

.tw-tooltip-wrapper {
    background-color: #242426;
    color: #ccc!important;
}

/* Copyright */
li.Footer-item.Footer-copyright.copyright:after {
  content: " | Dark Style by @Strong_WTF"
}
}
