css:
@-moz-document regexp("https:\\/\\/developer\\.android\\.(com|google\\.cn)\\/(guide|training|topic|reference|design|docs|studio|jetpack|about|platform|samples).*") {

/* scroll bar */
* {
	scrollbar-color: rgb(38, 163, 219) rgb(28, 30, 32);
	scrollbar-width: thin;
}
/* button */
.button, button, input[type="button"], input[type="file"], input[type="image"], input[type="reset"], input[type="submit"], .devsite-footer-utility-button > a {
	background: rgba(37, 120, 189, 0.2);
    border: 1px solid rgba(37, 120, 189, 0.5);
    opacity: 1;
}
.button:hover, .button:focus, button:hover, button:focus, input[type="button"]:hover, input[type="button"]:focus, input[type="file"]:hover, input[type="file"]:focus, input[type="image"]:hover, input[type="image"]:focus, input[type="reset"]:hover, input[type="reset"]:focus, input[type="submit"]:hover, input[type="submit"]:focus, .devsite-footer-utility-button > a:hover, .devsite-footer-utility-button > a:focus {
	background: rgba(37, 120, 189, 0.6);
    border: 1px solid rgba(37, 120, 189, 1);
}
body, html { 
    color: #6f7275; 
}

body[layout="docs"] .devsite-article {
	background: #1c1e20;
}
code, pre, .pre-style {
	background: #0f1013;
    color: #70838c;
    border-radius: 4px;
}
h1, .devsite-landing-row h2, .devsite-landing-row-large-headings .devsite-landing-row-item-description h3, strong {
	color: #a6a6a6;
}
:link, :visited {
	color: #30a3db;
}
.caution {
	border: 1px red dashed;
}

.caution, .caution code, .caution :link, .caution :visited {
    background: #1c1e20;
	
}

.caution a:focus, .caution a:hover{
	background: rgba(72, 56, 56, 0.7);
}
.note a:focus, .note a:hover {
    background: rgba(68, 71, 81, 0.7);
}
aside, aside code, aside :link, aside :visited, .note, .note :link, .note code, .note :visited, .special, .special code, .special :link, .special :visited {
    background: #262829;
    color: #30a3db;
    border-radius: 4px;
    
}
.screenshot {
	border: 1px solid #31353c;
}
.note {
    box-shadow: 4px 4px 4px rgba(0,0,0,.2);
}
blockquote > aside.note {
	box-shadow: none;
}

body[theme="android-theme"][layout="docs"], body[theme="android-theme"] .devsite-book-nav-bg::after {
	background: #222324;
}
[layout="docs"] h2 {
	border-bottom: 1px solid #2a2b2d;
}
/* NAV */
body[theme="android-theme"] devsite-header .devsite-product-description-row .devsite-breadcrumb-link, body[theme="android-theme"] devsite-header .devsite-product-description-row .devsite-breadcrumb-guillemet {
	color: #909497;
    fill: #909497;
}
body[theme="android-theme"] devsite-header .devsite-product-description-row .devsite-breadcrumb-link:hover, body[theme="android-theme"] devsite-header .devsite-product-description-row .devsite-breadcrumb-link:focus, body[theme="android-theme"] devsite-header .devsite-product-description-row .devsite-breadcrumb-guillemet:hover, body[theme="android-theme"] devsite-header .devsite-product-description-row .devsite-breadcrumb-guillemet:focus {
    color: #3ddc84;
    fill: #3ddc84;
}
/*start logo bar section*/
body[theme="android-theme"] devsite-header .devsite-top-logo-row {
	background: linear-gradient(90deg, rgb(34, 35, 36), rgb(0, 0, 0));
}
body[theme="android-theme"] devsite-header devsite-tabs.upper-tabs .devsite-tabs-wrapper {
	background: none;
}
/*end logo bar section*/
body[theme="android-theme"] .devsite-collapsible-section {
	background: #222324;
}
body[theme="android-theme"] devsite-tabs.upper-tabs tab > a {
	color: #a4aaae;
}
body[theme="android-theme"] devsite-tabs.lower-tabs a {
	background: #222324;
    color: #a4aaae;
}
body[theme="android-theme"] devsite-tabs.lower-tabs a:hover, body[theme="android-theme"] devsite-tabs.lower-tabs a:focus, body[theme="android-theme"] devsite-tabs.upper-tabs > div > tab > a:hover, body[theme="android-theme"] devsite-tabs.upper-tabs > div > tab > a:focus {
    color: #3ddc84;
}
body[theme="android-theme"] devsite-tabs.lower-tabs tab[active] > a, body[theme="android-theme"] devsite-tabs.upper-tabs > div > tab[active] > a {
    color: #3ddc84;
}
/* over float menu */
devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu,body devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu tab a, body[theme="android-theme"] devsite-header devsite-tabs.upper-tabs tab[overflow-tab]::after {
    background: #1c1e20;
}
devsite-tabs.upper-tabs tab[overflow-tab] .devsite-tabs-overflow-menu {
	box-shadow: 1px 1px 2px rgba(0,0,0,.3)
}
body devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu tab a:hover, body devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu tab a:focus,
devsite-search .devsite-popout-result a:hover {
	background: rgba(0,0,0,.3);
	color: #3ddc84;
	border: 1px dashed rgba(3, 229, 151, 0.6);
}
/* side drawer */
.devsite-book-nav-bg {
	background: #222324;
}
.devsite-nav-title {
    color: #717171;
}
.devsite-nav-heading > .devsite-nav-title {
    color: rgb(128, 134, 129);
    font-weight: 700;
}
devsite-book-nav .devsite-nav-list > .devsite-nav-heading:not(.devsite-nav-divider) {

    border-top: 1px solid #2a2b2d;
    
}
/* card view*/
devsite-dynamic-content .devsite-card {
	background: #1c1e20;
	box-shadow: 2px 2px 4px rgba(0,0,0,.3);
	border: 2px solid #4d4d4d;
}
devsite-dynamic-content .devsite-card h3 {
	color: #a6a6a6;
}
/* footer */
devsite-content-footer {
    color: rgb(111, 114, 117);
}
devsite-page-rating[position="footer"] {
	border-top: 1px solid rgb(42, 43, 45);
}
devsite-footer-promos {
	border-top: 1px solid rgb(42, 43, 45);
	background: #222324;
}
devsite-footer-promos .devsite-footer-promos-list {
	border-bottom: 1px solid rgb(42, 43, 45);
}
devsite-footer-linkboxes {
	background: #222324;
}
devsite-footer-linkboxes .devsite-footer-linkbox-link, devsite-footer-utility .devsite-footer-utility-link, devsite-footer-utility .devsite-footer-sites-link, devsite-footer-promos .devsite-footer-promo-title {
    color: #909497;
}
devsite-footer-utility {
    background: #222324;
}
devsite-footer-utility .devsite-footer-sites, devsite-footer-utility nav {
    border-top: 1px solid rgb(42, 43, 45);
}
devsite-footer-linkboxes .devsite-footer-linkbox-item:hover {
	background: rgba(20, 22, 23, 0.4);
    border: 1px dashed rgba(3, 155, 229, 0.6);
    border-radius: 4px;
    padding-left: 6px;
    transition: padding-left .3s ease 0s;
}
devsite-footer-linkboxes .devsite-footer-linkbox-item {
	transition: padding-left .3s ease 0s;
}
/* Language chooser */
devsite-select .devsite-select-toggle {
	background: #161717 url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='20' height='4' viewBox='0 0 20 4'><path d='M0,0l4,4l4-4H0z' fill='%23212121'/></svg>") no-repeat right;
    border: none;
}
devsite-select .devsite-select-toggle:hover, devsite-select .devsite-select-toggle:focus {
	background-color: #3ddc84;
}
devsite-select .devsite-select-list {
	border: none;
    background: #161717;
    box-shadow: 1px 1px 2px rgba(0,0,0,.3);
    width: 200px;
}
devsite-select .devsite-select-item:hover, devsite-select .devsite-select-item:focus {
	background: rgba(0,0,0,.3);
	color: #3ddc84;
	border: 1px dashed rgba(3, 229, 151, 0.6);
    transition: background 1s ease 0s;
}
devsite-select .devsite-select-item {
    transition: background 1s ease 0s;
}

/* search field */
devsite-search .devsite-searchbox, body[theme="android-theme"] devsite-header devsite-search .devsite-search-field {
	background: #161717;
}
body[theme="android-theme"] devsite-header devsite-search[search-active] .devsite-search-field,
body[theme="android-theme"] devsite-header devsite-search[search-active] .devsite-search-field:hover, 
devsite-search[search-active] .devsite-searchbox:hover,
body[theme="android-theme"] devsite-header[search-active] devsite-search-background::after{
	background: #161717;
    color: #5f6368;
}
body[theme="android-theme"] devsite-header devsite-search .devsite-search-field:hover {
	background: #000;
}
body[theme="android-theme"] devsite-header devsite-search .devsite-searchbox::before, body[theme="android-theme"] devsite-header devsite-search-background, devsite-search .devsite-searchbox::before, devsite-header devsite-search-background::after, body[theme="android-theme"] devsite-header devsite-search-background::after{
	background: #161717;
}
/* search suggestion */
devsite-search .devsite-popout-result {
	background: #161717;
    box-shadow: 0 1px 2px 0 rgb(13, 13, 13);
}
devsite-search .devsite-suggest-wrapper {
	padding-top: 0px;
}
devsite-search .devsite-result-item a {
	color: #717171;
}
devsite-search .devsite-suggest-results-container,
devsite-search hr {
    border-top:1px solid rgb(42, 43, 45);
}
devsite-search hr {
	margin-bottom: 0px;
}
devsite-search .devsite-suggest-header {
    background: #0d0d0d;
	color: #bbb;
}
/* code editor */
devsite-selector {
	background: #040209;
    border: none;
    border-radius: 4px;
}
devsite-selector devsite-tabs[connected] {
	border: none
}
.note pre {
    background: rgb(15, 16, 19);
}
/* code theme */
devsite-code pre .typ, devsite-code pre .atn {
	color: #d9bc0f;
}
devsite-code pre .kwd, devsite-code pre .tag {
	color: #038ce5;
}
/* android API Reference page */
/* side drawer */
devsite-book-nav .devsite-nav-list > .devsite-nav-item:not(.devsite-nav-accordion) .devsite-nav-title {
	border-bottom-color: #2a2b2d;
}
devsite-book-nav .devsite-nav-list > .devsite-nav-item.devsite-nav-heading:not(.devsite-nav-accordion) .devsite-nav-title {
    background: #0d0d0d;
    color: #bbb;
}
devsite-book-nav .devsite-nav-list > .devsite-nav-item:not(.devsite-nav-active) .devsite-nav-title:hover, devsite-book-nav .devsite-nav-list > .devsite-nav-item:not(.devsite-nav-active) .devsite-nav-title:focus {
	color: #039be5;
}
.devsite-nav-accordion > devsite-expandable-nav > .devsite-nav-title {
	color: rgb(164, 170, 161);
}
.devsite-nav-accordion {
	border-bottom: 1px dashed rgba(3, 155, 229, 0.6);
	border-top: 1px dashed rgba(3, 155, 229, 0.6);
}
/* Overview section */
select {
    background: #0d0d0d url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='20' height='4' viewBox='0 0 20 4'><path d='M0,0l4,4l4-4H0z' fill='%23212121'/></svg>") no-repeat right;
    border: 1px solid #039be5;
    color: #9d9d9d;
}
select:hover, select:focus, select:active {
	color: #039be5;
    border: 1px solid #039be5;
    background-color: rgba(29, 54, 91, 0.8);
}

/* center contents */
tr {
	border-top: 1px solid #424751;
    background: rgba(66, 68, 70, 0.56);
}
td, td code {
	color: #9b9b9b;
}
td, table.responsive tr:not(.alt) td td:first-child, table.responsive td tr:not(.alt) td:first-child {
	background: rgba(33, 35, 36, 0.95);
}
.jd-sumtable-expando td {
	border: none;
}
table.responsive > * > tr:not(.alt) td:first-child, table.responsive > tr:not(.alt) td:first-child {
	background: #262829;
}
.expand-control::before {
	background-color: rgba(17, 144, 232, 0.8);
}
/* Design & Quality section */
.devsite-card-list-link, devsite-dynamic-content .devsite-card-list-link {
	background: #343739;
    border-radius: 4px;
}
.devsite-card-list-link:hover, .devsite-card-list-link:focus, devsite-dynamic-content .devsite-card-list-link:hover, devsite-dynamic-content .devsite-card-list-link:focus {
	background: #0d0d0d;
    border: 1px dashed rgba(3, 155, 229, 0.6);
    color: rgba(3, 155, 229, 0.6);
}
.devsite-card-list-item {
	box-shadow: 2px 2px 4px rgba(0,0,0,.2);
}
/* quality guidelines */
.devsite-landing-row:not([background]),
.devsite-landing-row:not([background]).dac-landing-row-bg-blob-8, [background="grey"] {
	background: none;
}
[background="white"] h2, [background="white"]:not(.devsite-landing-row-cards) h3, [background="white"]:not([foreground]):not(.devsite-landing-row-cards),
.devsite-landing-row-item-description-content, .devsite-landing-row:not([background]):not([foreground]) .devsite-landing-row-description{
	color: #cbcfdb;
}
[background="white"] {
    background-color: #3a3e42;
    box-shadow: 2px 2px 4px rgba(0,0,0,.2);
}
.devsite-landing-row h1, .devsite-landing-row h2, .devsite-landing-row-item h2, .devsite-landing-row-item h3, .dac-landing-row-hero h2 {
	color: #d7d7d7;
}
article.devsite-article {
	background: #222324;
}
.dac-landing-row-hero-description {
	color: #d7d7d7;
}
.devsite-landing-row-column > .devsite-landing-row-item-no-media:not(.dac-landing-row-bg-blue) > .devsite-landing-row-item-description {
	background: #3a3e42;
}
/* Jetpack */
.dac-landing-row-bg-yellow {
    background-color: #d76f1f;
}
blockquote {
    background: #262829;
    border: 1px dashed rgba(3, 155, 229, 0.6);
    border-radius: 4px;
    box-shadow: 2px 2px 4px rgba(0,0,0,.2);
}
.dac-outline-button {
	border: 2px solid rgba(3, 155, 229, 1);
}
.dac-outline-button:hover, .dac-outline-button:focus {
	background: rgba(3, 155, 229, 0.3);
}
devsite-dynamic-content .devsite-pagination-more-button, devsite-dynamic-content .devsite-pagination-less-button {
	background: rgba(3, 155, 229, 1);
}
devsite-dynamic-content .devsite-pagination-more-button:hover, devsite-dynamic-content .devsite-pagination-more-button:focus, devsite-dynamic-content .devsite-pagination-less-button:hover, devsite-dynamic-content .devsite-pagination-less-button:focus {
	background: rgba(3, 155, 229, .5);
}
.warning, .warning code, .warning :link, .warning :visited {
	border: 1px dashed red;
    background: none;
}
}

@-moz-document regexp("https:\\/\\/developer\\.android\\.(com|google\\.cn)\\/samples.*") {
/* sample section */
body[ready] #gc-wrapper {
	background: #222324;
}
android-samples .navrow, android-samples .sample-card {
	background: #3a3e42;
}
android-samples .sample-description-area .text,
android-samples .card-info .title{
    color: #cbcfdb;
}
android-samples .card-info .section {
    color: #3ddc84;
}
android-samples .per-page-item:hover, 
android-samples .page-number-selector-button:hover,
android-samples .per-page-selected-item, android-samples .page-number-selector-inactive{
	color: rgba(3, 155, 240, 0.5);
}
/* search field */
android-samples .navrow input#search {
	background: #222324;
    border: 1px solid rgba(3, 155, 229, 0.6);
}
}
