css:
@-moz-document domain("mds-online.ru") {
    body {
        background-color: #313131;
        color: white;
    }

    .news__gradient {
        background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #313131);
    }

    .track__param {
        border-color: hsl(60, 0%, 30%);
        background: hsl(60, 0%, 30%);
        color: hsl(60, 0%, 89%);
    }

    .player__control {
        filter: invert(0.9);
    }

    .player_visible {
        background: hsl(196, 56%, 37%);
    }

    .soundstream img {
        filter: invert(0.9);
    }

    .checkbox__control:checked + .checkbox__decore:after {
        background: #dbdbdb
    }

    .searchStatus {
        background: hsl(116, 51%, 34%);
        font-weight: bold
    }

    .searchStatus_type_warning {
        cursor: default;
        background: hsl(36, 90%, 34%)
    }
    
    .input__control {
        color: white;
    }
}
