css:
@-moz-document regexp("https?://pi[.-]?hole/admin.*") {
body {
    font-size: 12px;
}

body, html, code, kbd, pre, samp, a,
h1, h2, h3, h4, h5, h6,
.main-footer,
.skin-blue .main-header .logo,
.skin-blue .sidebar a
{
/*     font-family: Menlo,Monaco,Consolas,"Courier New",monospace; */
    color: #090 !important;
    font-family: consolas, courier, "courier new";
}

code { background-color: transparent; color: #0c0 !important; }
pre {
    background-color: #030;
    border: 1px solid #090;
    color: #0c0 !important;
}

/* LOGIN */
.panel-default {
    border-color: #060;
}
.panel, .panel-default > .panel-heading {
    background: #020;
    border-color: #090;
    color: #0c0;
}

.box.box-solid.box-info > .box-header {
    color: #fff;
/*     background: #00c0ef;
    background-color: rgb(0, 192, 239);
    background-color: #00c0ef; */
    background: #060;
}
.box.box-solid.box-info > .box-header.with-border {
    border: 1px solid #060;
}
.box.box-solid.box-info > .box-header a,
.box.box-solid.box-info > .box-header .btn,
.box-header h3 {
    color: #0f0 !important;
}
.user-panel > .info, .user-panel > .info > a {
    color: #0c0;
}

.box.box-solid.box-info {
    border: 1px solid #060;
}

.icheck-primary > input:first-child:checked + input[type="hidden"] + label::before, .icheck-primary > input:first-child:checked + label::before {
    background-color: #090;
    border-color: #060;
}

.icheck-primary > input:first-child:not(:checked):not(:disabled):hover + input[type="hidden"] + label::before, .icheck-primary > input:first-child:not(:checked):not(:disabled):hover + label::before {
    border-color: #060;
}

.user-panel .info a,
.small-box p
{
    font-family: 'Source Sans Pro','Helvetica Neue',Helvetica,Arial,sans-serif;
}

.skin-blue .sidebar-menu > li:hover > a,
.skin-blue .sidebar-menu > li.active > a
{
    color: #0f0 !important;
    background: none;
    border-left-color: #0f0;
}

a.dropdown-toggle,
.skin-blue .sidebar-menu > li > a,
.table-bordered a,
.main-footer a
{
    text-decoration: underline !important;
}

.skin-blue .user-panel > .info,
.sidebar-menu > li.header {
    color: #0f0 !important;
    background: none !important;
}

.layout-boxed,
.nav-tabs-custom,
.nav-tabs-custom > .tab-content
{
    background: #001400;
}

.skin-blue .sidebar-menu > li > .treeview-menu {
    background: #000800;
}

/* INTERNAL CONTAINERS (DARK) */
.main-header .logo,
.main-header .navbar,
.wrapper,
.main-sidebar,
.left-side,
.content-wrapper,
.right-side,
.main-footer
{
    background: none !important;
}

/* CONTAINER OUTLINES */
.table-bordered,
.wrapper
{ border: 1px solid #0c0 !important; }

.main-header,
.page-header,
.box-header.with-border
{ border-bottom: 1px solid #0c0; }

.main-sidebar { border-right: 1px solid #0c0; }

.main-footer { border-color: #0c0; }

.box-footer { background-color: #030; border-top: 1px solid #0c0; }

.table-bordered > thead > tr > th,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > tbody > tr > td,
.table-bordered > tfoot > tr > td
{
    border-color: #0c0 !important;
}

.table-bordered td,
.chart-legend ul
{
/*     word-wrap: anywhere; */
    word-break: break-all;
}

/* STRAIGHTEN OUT BOXES */
.box {
    border-radius: 0;
    background: #010 !important;
    border: 1px solid #0c0;
}

.bg-green, .bg-aqua, .bg-yellow, .bg-red {
    background: #000 !important;
    border: 1px solid #0c0;
    color: #080 !important;
    max-height: 105px;
}

.small-box .icon {
    color: rgba(0, 255, 0, 0.2);
}

.bg-aqua { border-color: #00c0ef; }
.bg-yellow { border-color: #f39c12; }
.bg-red { border-color: #dd4b39; }

/* .bg-green .icon { color: rgba(0, 255, 0, 0.2); } */
/* .bg-aqua .icon { color: rgba(0, 192, 255, 0.2); } */

.progress { background-color: #030; }
.progress-bar { background-color: #0f0; }

/* TABLE ROWS */
.table-striped > tbody > tr { background-color: #020; }
.table-striped > tbody > tr:nth-of-type(2n+1) { background-color: #030; }
.table-striped > tbody > tr > td:nth-child(6) { color: #0c0 !important; }

/* #network-entries.table-bordered tbody tr { background: #020 !important; } */
/* .box-body > table > tbody > tr > td { background: #000 !important; } */

/* OTHER CONTROLS */

button, .btn-default, .btn-primary
{
    border: 1px solid #090;
    border-radius: 0;
    background: #000;
    color: #0f0;
    margin-left: 2px !important;
}

.btn-primary {
    background-color: #090;
    color: #030;
}

.btn-primary:hover {
    background-color: #0c0;
    color: #030;
}

/* .box-warning { background-color: #200 !important; } */
.box-warning .box-header { background-color: #300; }
.box-warning .box-header h1,
.box-warning .box-header h2,
.box-warning .box-header h3
{ color: #fff !important; }
.box-warning .btn { font-size: 12px; }

.form-control {
    background: #000;
    color: #0f0;
    border: 1px solid #090;
}

.form-control::placeholder {
    color: #063;
}

.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control
{ background-color: #000900; }

.input-group .input-group-addon {
    background-color: #030;
    border-color: #090;
    color: #fff;
}

.pagination > li > a,
.pagination > li > span {
    background: #000;
    border: 1px solid #090;
}

.pagination > .disabled > a,
.pagination > .disabled > a:focus,
.pagination > .disabled > a:hover,
.pagination > .disabled > span,
.pagination > .disabled > span:focus,
.pagination > .disabled > span:hover {
    background: #000;
    border: 1px solid #090;
    color: #063 !important;
}

.pagination > .active > a, 
.pagination > .active > a:focus,
.pagination > .active > a:hover,
.pagination > .active > span,
.pagination > .active > span:focus,
.pagination > .active > span:hover {
    background: #020;
    border: 1px solid #0c0;
}

.pagination > li > a:focus,
.pagination > li > a:hover,
.pagination > li > span:focus,
.pagination > li > span:hover {
    background-color: #003300;
    border-color: #0c0;
}

.list-group-item { background: #000800; border-color: #090; }
.skin-blue .list-group-item:hover { background-color: #002400; }

.nav-tabs-custom > .tab-content
{ border: 1px solid #0c0; }

.nav-tabs-custom > .nav-tabs { border-bottom-color: #0c0; }

.nav-tabs-custom > .nav-tabs > li.active > a,
.nav-tabs-custom > .nav-tabs > li.active:hover > a
{
    background: none;
    border-left-color: #0c0 !important;
    border-right-color: #0c0;
}
.nav-tabs-custom > .nav-tabs > li.active {
    border-top-color: #0c0;
}

/* #network-entries tbody tr { background: #020 !important; } */
/* #network-entries tbody tr.odd { background: #030 !important; } */
#network-entries tbody tr { color: #030; }
#network-entries.table-striped > tbody > tr > td:nth-child(6) {
    color: #030 !important;
}
#network-entries i.fas {
    height: 100%;
    width: 100%;
    display: block;
    overflow: auto;
    text-align: center;
    line-height: unset;
}
#network-entries i.fas.fa-check {
    background: #090;
    color: #fff;
}
#network-entries i.fas.fa-question {
    background: #c60;
    color: #fff;
}
}
