css:
@-moz-document domain("twitch.tv"), domain("go.twitch.tv") {
.top-nav__menu { /* шапка\header*/
    background:#4B367C !important ;
}
.channel-header__user {
    color: #000000 !important;
}
.tw-button-icon { /*кнопка\button*/
color: #fff !important;
    }
div.search-box{ /* поиск\search*/
    background:#fff !important ;
}
button.tw-align-items-center.tw-align-middle.tw-border-bottom-left-radius-medium.tw-border-bottom-right-radius-medium.tw-border-top-left-radius-medium.tw-border-top-right-radius-medium.tw-core-button.tw-core-button--border.tw-core-button--icon.tw-core-button--padded.tw-core-button--secondary.tw-inline-flex.tw-interactive.tw-justify-content-center.tw-overflow-hidden.tw-relative{ /* купить bits*/
color: #fff !important; /* шрифт шапка3 купить\buy bits*/
}
.navigation-link { /* шрифт шапка1\font color header1*/
    color: #CEC3E4;
}
.top-nav__external-link {/* шрифт шапка2\font color header2*/
    color: #CEC3E4;
}
figure.tw-avatar.tw-avatar--size-30{ /* fix avatar */
    background-color:#4B367C !important ;
}
    
svg.tw-icon__svg{ /* шрифт шапка2\font color header2*/
   color: #000; 
} 
textarea.tw-block.tw-border-radius-medium.tw-font-size-6.tw-full-width.tw-textarea.tw-textarea--no-resize{ /* поиск бар\*/
    background:#fff !important ; /* белый чат\white color chat*/
}
p.tw-strong.tw-upcase { /* прямой эфир в одну строку\live*/
    width: max-content;
}
.tw-core-button--primary { /* кнопка чат\button chat*/
    background-color: #6441A4 !important ;
    color: #fff;
}
div.side-nav__overlay-wrapper.tw-flex.tw-full-height.tw-overflow-hidden.tw-relative{ /* боковые меню\side menu  2C2541  181520*/
    background:#2C2541 !important ;  
    color: #fff;
}
.tw-c-text-alt,.tw-c-text-alt-2 { /* боковые меню  шрифт\side color menu*/
    color: rgb(136, 149, 174)!important;
}

h3.tw-ellipsis.tw-font-size-5{ /* шрифт\color font*/

    color: #000;
}
a.tw-interactive.tw-link.tw-link--hover-underline-none.tw-link--inherit,ul.tw-align-items-end.tw-flex.tw-flex-wrap.tw-full-width { /* шрифт\color font*/

    color: #000;
}

div.extension-frame-wrapper{ /* adblock*/
    display:none;
}
