css:
@-moz-document domain("service.post.ch"), domain("post.ch") {
#search-form-container[_ngcontent-c10] {
    background-color: #2d2d2d;
    padding: 40px;
    margin-top: 56px;
}

.os-header .ppm-main-navigation__search-label {
    border-left: 1px solid white !important;
    outline-offset: -3px;
}
.nav-tabs .nav-link, .nav-tabs-a .nav-link, .nav-tabs-i .nav-link {
    font-family: Frutiger,Helvetica,Arial,sans-serif;
    font-size: 17px;
    font-weight: 400;
    font-style: normal;
    font-stretch: normal;
    line-height: 1.5;
    letter-spacing: normal;
    color: white;
    padding: 17px 24px;
    display: block; 
} 
.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active, .nav-tabs-a .nav-item.show .nav-link, .nav-tabs-a .nav-link.active, .nav-tabs-i .nav-item.show .nav-link, .nav-tabs-i .nav-link.active {
    color: white;
    background-color: black;
    border-bottom: 4px solid grey;
}
.form-control {
    display: block;
    width: 100%;
    padding: .375rem .75rem;
    font-size: 1rem;
    line-height: 1.5;
    color: white;
    background-color: black ;
    background-clip: padding-box;
    border: 2px solid black ;
    border-radius: 0;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
body {
    margin: 0;
    font-family: Frutiger,Arial,Helvetica,sans-serif;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: white;
    text-align: left;
    background-color: black;
}
.fm_formcontainer .fm_label {
    color: white;
    max-width: 100%;
}
#search-country-selection-info-link[_ngcontent-c10], #search-large-format-details-link-left[_ngcontent-c10], #search-large-format-details-link-right[_ngcontent-c10], #search-small-format-details-link[_ngcontent-c10] {
    font-family: Frutiger,Helvetica,Arial,sans-serif;
    color: white;
    text-decoration: none;
}
.os-header .ppm-main-navigation__mobile-bar-wrapper {
    position: relative;
    z-index: 3;
    display: flex;
    flex-wrap: nowrap;
    justify-content: space-between;
    height: 3.5rem;
    background-color: black;
    border-bottom: 1px solid black;
}
.os-header .ppm-main-navigation__search-label {
    font-size: 0.9375rem;
    line-height: 1.6;
    font-weight: 300;
    color: #666666;
    text-decoration: none;
    transition: color 150ms;
    display: flex;
    align-items: center;
    justify-content: center;
    min-width: 52px;
    height: 100%;
    margin-bottom: 0;
    margin-left: auto;
    background-color: black;
    border: none;
    outline: none !important;
}
#klpLoginWidget .klp-widget-anonymous a {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
    padding: 0 16px;
    color: #666666;
    font-weight: normal;
    font-size: 15px;
    font-family: Frutiger, Arial, Helvetica, sans-serif;
    text-decoration: none;
    background-color: black;
    outline: none;
}
.navigation[_ngcontent-c4] {
    background-color:#2d2d2d;
    padding-bottom: 5px;
    padding-top: 20px;
}
h1.navigation-title[_ngcontent-c4] {
    margin-top: 16px;
    margin-bottom: 16px;
    font-size: 17px;
    font-weight: 700;
    font-style: normal;
    font-stretch: normal;
    line-height: 1.4;
    letter-spacing: normal;
    text-align: left;
    color: white;
    font-family: Frutiger,Helvetica,Arial,sans-serif;
}
li.navigation-item[_ngcontent-c4], li.navigation-item[_ngcontent-c4] a[_ngcontent-c4] {
    font-size: 15px;
    font-weight: 700;
    font-style: normal;
    font-stretch: normal;
    line-height: 1.6;
    letter-spacing: normal;
    text-align: left;
    color: white;
    cursor: pointer;
}
.ppm-main-navigation__metanav-linkitem--active .ppm-main-navigation__metanav-link {
    color: #000;
    background-color: #B789DF;
}
.os-header .ppm-main-navigation .ppm-main-navigation__langnav-item--button {
    position: relative;
    z-index: 10;
    display: flex;
    box-sizing: content-box;
    width: 64px;
    color: #666666;
    background-color: black;
    border: none;
    border-left: 1px solid black;
}
.os-header .ppm-main-navigation__meta-nav-container {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    width: 100%;
    height: 100%;
    background-color: black;
}
.os-header .ppm-main-navigation__mainlist-list {
    display: flex;
    flex-direction: column;
    width: 100%;
    margin-bottom: 0;
    padding: 0;
     background-color: black;
}
.matching-service>.body {
   
    background: #B789DF;
}
.os-header .ppm-main-navigation__meta-nav-wrapper {
   
    background-color: black;
}
.os-header .ppm-main-navigation__main-nav-wrapper {
   
    background-color: black;
}

.os-header .ppm-main-navigation__mainlist {
   
    background-color: black;
}
.os-header .ppm-main-navigation__mainlist-item--active > .ppm-main-navigation__mainlist-link::after {
    position: absolute;
    right: 0;
    bottom: 1px;
    left: 0;
    display: block;
    height: 0.25rem;
    background-color: #B789df;
    content: "";
    pointer-events: none;
}


}
