css:
@-moz-document domain("photoriviera.ch") {
#main-nav-menu {background: black
}

.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
    color: white;
    background-color: transparent;}

.navbar-default .navbar-nav>li>a {
    color: white;}

.navbar-brand .header-title img {
    max-height: 70px; display:none}

#services {background: black !important}

h4 {
    font-size: 1.4rem;
    padding: 20px;
    font-family: 'Montserrat', sans-serif;
    color: white;}

#home-container {background: black !important}

#occasions {background: black !important}

#contact {background: black !important}

#submitform{background: black}

.footer{background: black}

.map-address{background: black}

div[data-page-id="5840"] a, div[data-page-id="5834"] a {
    padding: 10px;
    background-color: #000;
    border: 1px solid #fff;
    color: #fff;
    border-radius: 30px;}

h2 {
    font-size: 40px;
    font-family: 'Montserrat', sans-serif;
    color: #fff;
    background: #000;
    border: 1px solid #fff;
    padding: 15px;
    line-height: 1;
    z-index: 2;
    text-transform: uppercase;
    display: inline-block;
    -webkit-transform: rotate(-3deg);
    -moz-transform: rotate(-3deg);
    -ms-transform: rotate(-3deg);
    -o-transform: rotate(-3deg);
    font-weight: 700;}

#slider-swiper .content-slide {
    background: rgba(0, 0, 0, .45);
    padding: 100px;
    margin: auto;
    text-align: center;
    color: #fff;
    font-family: "Montserrat", Open Sans, Helvetica, Arial, sans-serif;
    font-size: 50px;
    font-size: 3.5714rem;
    position: relative;}


}
