    background-color: #005783;
    width: 100%;
    z-index: 10;
}
html, body {
    margin: 0;
    padding: 0;
    background-color: #2a2a2a;
    -webkit-background-size: 100% auto;
    background-size: 100% auto;
    color: #333;
    font-family: Open Sans,sans-serif;
    font-size: 95%;
    font-weight: 400;
    line-height: normal;
}
.navbar .popover-region-container {
    top: 1.995rem;
    background: #212121;
    border: 2px;
}
.popover-region-header-text {
    float: left;
    margin: 0;
    font-size: 14px;
    line-height: 25px;
    color: white;
}
.popover-region-header-container {
    height: 25px;
    line-height: 25px;
    padding-left: 5px;
    padding-right: 5px;
    border-bottom: 1px solid #5992af;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}
.popover-region-footer-container {
    height: 30px;
    text-align: center;
    border-top: 1px solid #ddd;
    background-color: #00537e;
    padding-top: 3px;
    border: 2px;
    color:white;
}
.breadcrumb {
    background: none!important;
    background-color: #2a2a2a!important;
    padding: 5px;
}
#ticker-wrap {
    position: relative;
    margin-bottom: 15px;
    margin-top: 15px;
    background: #212121;
    line-height: 40px;
}
.breadcrumb {
    background: none;
    background-color: #929292;
    padding: 5px;
}
.breadcrumb ul i, .breadcrumb li.lastli span {
    color: #fff;
    text-decoration: none;
}
#region-main {
    background-color: #2a2a2a;
    border: none;
}.block>.content, #dockeditempanel .dockeditempanel_bd {
    border-width: 0 0 0 0;
    border-color: #59585d;
    border-style: none;
    padding: 10px 5px;
    font-weight: 400;
    background: #2a2a2a;
    border-radius: 0 0 0 0;
}
.block .content a, .block .content li a, .block .content td a {
    color: #0089cf;
}
.jsenabled .block_settings .block_tree .tree_item.branch, .block_navigation .block_tree .tree_item.branch {
    color: #0089cf!important;
}
.activity_footer .text, .section_footer .text {
    display: table-cell;
    width: 10007px;
    vertical-align: top;
    padding-top: 9px;
    color: #c6c6c6;
}
.fp-iconview .fp-filename-field .fp-filename {
    background: #51666c;
    padding-top: 5px;
    padding-bottom: 12px;
    min-width: 112px;
}
input, button, select, textarea {
    color: #333;
    font-family: Calibri,sans-serif;
    font-size: 95%;
    font-weight: 400!important;
    -webkit-text-fill-color: white;
}
#adaptable-page-header-wrapper #main-navbar {
    clear: both;
    border-bottom: 3px solid #0089cf;
    background-color: #fff;
    z-index: 100;
    background: #2a2a2a;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: .95rem;
    color: #373a3c;
    text-align: left;
    list-style: none;
    background-color: #212121;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,.15);
}
nav>li>a {
    color: #fff;
    line-height: 40px;
    padding: 2px 20px 2px 20px;
    text-decoration: none;
    text-shadow: none;
    font-weight: 400;
    font-size: 14px;
    text-transform: none;
    border-right: 0;
    background-color: none;
    -webkit-transition: all .2s;
    -moz-transition: all .2s;
    -ms-transition: all .2s;
    -o-transition: all .2s;
    transition: all .2s;
}

.navbar-expand-md .navbar-nav .dropdown-menu {
    position: absolute;
    background: black;
}
#page .course-content ul li.section.main {
    background-color: #929292;
    border: 1px solid #929292;
    margin-bottom: 10px;
    border-radius: 0;
}
.sectionname {
    background-color: #a5a5a5!important;
    color: #3a454b!important;
    font-size: 1.6em;
    margin-bottom: 20px;
    padding: 3px 10px;
    border-bottom: none #f3f3f3 0;
    border-radius: 0;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
}
.section li.modtype_forum div.activity-wrapper {
    -webkit-box-shadow: -3px 0 0 0 #909;
    box-shadow: -3px 0 0 0 #909;
    border-bottom: 2px dashed #eee;
    background-color: #b8b7b7;
}
.section li.modtype_forum div.activity-wrapper {
    -webkit-box-shadow: -3px 0 0 0 #909;
    box-shadow: -3px 0 0 0 #909;
    border-bottom: 2px dashed #909;
    background-color: #b8b7b7;
}
