    -webkit-box-shadow: -3px 0 0 0 #06c;
    box-shadow: -3px 0 0 0 #06c;
    border-bottom: 2px dashed #eee;
    background-color: #00172c;
}
.section .activity .contentwithoutlink>a, .section .activity .activityinstance>a, .section .activity .contentwithoutlink .inplaceeditable>a, .section .activity .activityinstance .inplaceeditable>a {
    color: #006ca4;
}
.block .header .title h2 {
    margin: 0;
    padding: 0 0 10px;
    text-transform: none;
    font-size: 22px;
    color: #ffffff;
    font-weight: 400;
    text-shadow: none;
    line-height: 30px;
}
